import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { emailAutomation } from "./email-automation";
import { bulkPricingService } from "./bulk-pricing";
import { seedDatabase } from "./seed-data";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Serve static audio files
app.use('/audio', express.static('public/audio'));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "…";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, async () => {
    log(`serving on port ${port}`);

    // Seed database with initial course data
    await seedDatabase();

    // Initialize email campaigns on startup
    await emailAutomation.initializeCampaigns();

    // Initialize bulk pricing tiers
    await bulkPricingService.initializeBulkTiers();

    // Initialize beta feedback system
    const { initializeBetaFeedbackSystem } = await import('./betaFeedbackSystem');
    await initializeBetaFeedbackSystem();

    // Start email queue processor (runs every 5 minutes)
    setInterval(async () => {
      try {
        await emailAutomation.processEmailQueue();
      } catch (error) {
        console.error("Error processing email queue:", error);
      }
    }, 5 * 60 * 1000); // 5 minutes

    // Check for monthly beta feedback sending (runs daily at 9 AM)
    const checkBetaFeedback = async () => {
      const now = new Date();
      const isFirstOfMonth = now.getDate() === 1;
      const isNineAM = now.getHours() === 9;

      if (isFirstOfMonth && isNineAM) {
        try {
          const { BetaFeedbackService } = await import('./betaFeedbackSystem');
          const result = await BetaFeedbackService.sendMonthlyFeedbackEmails();
          console.log(`Monthly beta feedback sent to ${result.emailsSent} testers`);
        } catch (error) {
          console.error("Error sending monthly beta feedback:", error);
        }
      }
    };

    // Run beta feedback check every hour
    setInterval(checkBetaFeedback, 60 * 60 * 1000); // 1 hour

    // Also run on startup if it's the first day of the month
    setTimeout(checkBetaFeedback, 5000); // After 5 seconds

    // Run feedback monitoring every 4 hours to check for concerning patterns
    const runFeedbackMonitoring = async () => {
      try {
        const { BetaFeedbackService } = await import('./betaFeedbackSystem');
        await BetaFeedbackService.runAutomatedMonitoring();
      } catch (error) {
        console.error("Error running feedback monitoring:", error);
      }
    };

    // Monitor feedback patterns every 4 hours
    setInterval(runFeedbackMonitoring, 4 * 60 * 60 * 1000); // 4 hours

    // Run initial monitoring after 30 seconds
    setTimeout(runFeedbackMonitoring, 30000);

    log("Email automation system initialized");
    log("Bulk pricing system initialized");
    log("Beta feedback system initialized");
  });
})();

server/openai.ts.content:
import OpenAI from "openai";
import fs from "fs";
import path from "path";
import PDFDocument from "pdfkit";
import crypto from "crypto";
import { storage } from "./storage";

if (!process.env.OPENAI_API_KEY) {
  throw new Error("OPENAI_API_KEY environment variable is required");
}

// the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });

// Ensure audio directory exists
const audioDir = path.join(process.cwd(), 'public', 'audio');
if (!fs.existsSync(audioDir)) {
  fs.mkdirSync(audioDir, { recursive: true });
}

export async function analyzePhoto(base64Image: string): Promise<{
  isCompliant: boolean;
  violations: string[];
  recommendations: string[];
  confidence: number;
}> {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: `You are a Louisiana plumbing code expert. Analyze plumbing installations for code compliance. Respond with JSON in this format: {
            "isCompliant": boolean,
            "violations": string[],
            "recommendations": string[],
            "confidence": number (0-1)
          }`
        },
        {
          role: "user",
          content: [
            {
              type: "text",
              text: "Analyze this plumbing installation for Louisiana plumbing code compliance. Check for proper pipe sizing, joint connections, support spacing, clearances, and any visible code violations."
            },
            {
              type: "image_url",
              image_url: {
                url: `data:image/jpeg;base64,${base64Image}`
              }
            }
          ],
        },
      ],
      response_format: { type: "json_object" },
      max_tokens: 1000,
    });

    return JSON.parse(response.choices[0].message.content || "{}");
  } catch (error) {
    throw new Error("Failed to analyze photo: " + (error as Error).message);
  }
}

export async function analyzePlans(base64Image: string): Promise<{
  materialList: Array<{
    item: string;
    quantity: number;
    unit: string;
    estimatedCost: number;
  }>;
  numberedFittings: Array<{
    number: number;
    description: string;
    size: string;
    type: string;
    location: string;
    connections: string[];
    pipeLength?: number;
    pipeSize?: string;
    position: {
      x: number; // normalized coordinate 0-1 from left edge
      y: number; // normalized coordinate 0-1 from top edge
      confidence: number; // 0-1 confidence in position accuracy
    };
  }>;
  codeCompliance: {
    issues: string[];
    recommendations: string[];
  };
  totalEstimatedCost: number;
}> {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: `You are a Louisiana plumbing expert who analyzes construction plans to generate detailed material lists, numbered fitting placement diagrams, and code compliance checks. 

          For numbered fittings, assign sequential numbers (1, 2, 3...) to each fitting, valve, fixture connection, and pipe run visible in the plans. Include precise measurements when visible on professional plans.

          CRITICAL: For each numbered fitting, provide spatial coordinates as normalized values (0.0 to 1.0) where:
          - x: 0.0 = left edge, 1.0 = right edge of the plan image
          - y: 0.0 = top edge, 1.0 = bottom edge of the plan image
          - confidence: 0.0-1.0 indicating how certain you are of the position

          Respond with JSON in this format: {
            "materialList": [{"item": string, "quantity": number, "unit": string, "estimatedCost": number}],
            "numberedFittings": [{"number": number, "description": string, "size": string, "type": string, "location": string, "connections": string[], "pipeLength": number, "pipeSize": string, "position": {"x": number, "y": number, "confidence": number}}],
            "codeCompliance": {"issues": string[], "recommendations": string[]},
            "totalEstimatedCost": number
          }`
        },
        {
          role: "user",
          content: [
            {
              type: "text",
              text: "Analyze these plumbing plans and generate: 1) A comprehensive material list with quantities and costs, 2) A numbered fitting placement system where each fitting, valve, connection point, and pipe run gets a sequential number with detailed specifications including pipe lengths, sizes, AND precise spatial coordinates (x,y as 0-1 normalized values) for accurate overlay positioning, 3) Louisiana plumbing code compliance analysis. The numbered fittings should create a 'puzzle system' for plumbers doing rough-in work with precise positioning data for professional installation diagrams."
            },
            {
              type: "image_url",
              image_url: {
                url: `data:image/jpeg;base64,${base64Image}`
              }
            }
          ],
        },
      ],
      response_format: { type: "json_object" },
      max_tokens: 1500,
    });

    const rawResult = JSON.parse(response.choices[0].message.content || "{}");

    // Validate and sanitize coordinates
    if (rawResult.numberedFittings) {
      rawResult.numberedFittings = rawResult.numberedFittings.map((fitting: any) => ({
        ...fitting,
        position: fitting.position ? {
          x: Math.max(0, Math.min(1, fitting.position.x || 0.5)),
          y: Math.max(0, Math.min(1, fitting.position.y || 0.5)),
          confidence: Math.max(0, Math.min(1, fitting.position.confidence || 0))
        } : {
          x: 0.5,
          y: 0.5,
          confidence: 0
        }
      }));
    }

    return rawResult;
  } catch (error) {
    throw new Error("Failed to analyze plans: " + (error as Error).message);
  }
}

// Helper functions for section-specific validation and content
function getSectionContent(section: string): string {
  const sectionContent: { [key: string]: string } = {
    '101': `**Louisiana Plumbing Code Section 101 - Administration:**
    - Enforcement Authority: State health officer has primary responsibility, can delegate to local inspectors
    - Legal Basis: R.S. 36:258(B), Title 40 Chapters 1 & 4, supporting statutes R.S. 40:4(A)(7) and R.S. 40:5
    - Historical: Originally promulgated June 2002, major amendments November 2012 (LR 38:2795)
    - Delegation Process: Authority flows from state to local parishes and municipalities
    - Local Jurisdiction: Can be more restrictive than state code, but not less restrictive`,
    '103': `**Louisiana Plumbing Code Section 103 - Permits:**
    - Permit Requirements: When permits are required and exemptions
    - Application Process: Required documentation and procedures
    - Permit Validity: Duration and conditions of permits
    - Inspection Requirements: Mandatory inspection points`,
    '105': `**Louisiana Plumbing Code Section 105 - Inspections:**
    - Inspector Qualifications: Training and certification requirements
    - Inspection Procedures: Standard practices and protocols
    - Inspection Records: Documentation and reporting requirements
    - Compliance Verification: Testing and validation methods`,
    '107': `**Louisiana Plumbing Code Section 107 - Violations:**
    - Violation Procedures: Notice requirements and processes
    - Penalties: Types of sanctions and enforcement actions
    - Emergency Procedures: Immediate response protocols
    - Appeal Process: Rights and procedures for challenging violations`,
    '109': `**Louisiana Plumbing Code Section 109 - Plan Review:**
    - Plan Requirements: Technical drawing specifications
    - Review Process: Submission and approval procedures
    - Plan Standards: Engineering requirements and standards
    - Review Timeline: Processing timeframes and requirements`
  };
  return sectionContent[section] || sectionContent['101'];
}

async function validateSectionRelevance(message: string, currentSection: string): Promise<{isRelevant: boolean, redirectMessage: string}> {
  // Simple keyword-based validation for common section topics
  const sectionKeywords: { [key: string]: string[] } = {
    '101': ['administration', 'enforcement', 'authority', 'legal', 'delegate', 'health officer', 'jurisdiction'],
    '103': ['permit', 'application', 'documentation', 'validity', 'exemption'],
    '105': ['inspection', 'inspector', 'qualification', 'procedure', 'record', 'compliance'],
    '107': ['violation', 'penalty', 'notice', 'emergency', 'appeal', 'enforcement'],
    '109': ['plan', 'review', 'drawing', 'engineering', 'standard', 'approval']
  };

  const currentKeywords = sectionKeywords[currentSection] || [];
  const messageWords = message.toLowerCase().split(/\s+/);

  // Check if message contains section-relevant keywords
  const hasRelevantKeywords = currentKeywords.some(keyword => 
    messageWords.some(word => word.includes(keyword) || keyword.includes(word))
  );

  if (hasRelevantKeywords) {
    return { isRelevant: true, redirectMessage: '' };
  }

  // Check if asking about other sections
  const otherSections = Object.keys(sectionKeywords).filter(s => s !== currentSection);
  for (const section of otherSections) {
    const keywords = sectionKeywords[section];
    const hasOtherSectionKeywords = keywords.some(keyword => 
      messageWords.some(word => word.includes(keyword) || keyword.includes(word))
    );
    if (hasOtherSectionKeywords) {
      return {
        isRelevant: false,
        redirectMessage: `Your question seems to be about Section ${section}. I'm currently focused on Section ${currentSection} topics like administration, enforcement, authority, and legal basis.`
      };
    }
  }

  // Generic questions might still be relevant
  const genericQuestions = ['what', 'how', 'when', 'why', 'where', 'explain', 'help', 'tell me'];
  const hasGenericQuestion = genericQuestions.some(q => message.toLowerCase().includes(q));

  if (hasGenericQuestion) {
    return { isRelevant: true, redirectMessage: '' };
  }

  return {
    isRelevant: false,
    redirectMessage: `I can help you with Section ${currentSection} topics including administration, enforcement authority, legal basis, and delegation processes.`
  };
}

// AI Response Caching Functions
function normalizeQuestion(question: string): string {
  return question
    .toLowerCase()
    .replace(/[^\w\s]/g, '') // Remove punctuation
    .replace(/\s+/g, ' ') // Normalize whitespace
    .trim();
}

function generateQuestionHash(
  normalizedQuestion: string, 
  subscriptionTier: string, 
  currentSection?: string
): string {
  const contextString = `${normalizedQuestion}|${subscriptionTier}|${currentSection || 'all'}`;
  return crypto.createHash('sha256').update(contextString).digest('hex');
}

async function getCachedMentorResponse(
  message: string,
  subscriptionTier: string,
  currentSection?: string
): Promise<string | null> {
  try {
    const normalizedQuestion = normalizeQuestion(message);
    const questionHash = generateQuestionHash(normalizedQuestion, subscriptionTier, currentSection);

    const cachedResponse = await storage.getCachedAiResponse(questionHash);
    if (cachedResponse) {
      console.log(`Using cached AI response for question hash: ${questionHash}`);
      // Update access tracking
      await storage.updateAiResponseAccess(questionHash);
      return cachedResponse.response;
    }

    return null;
  } catch (error) {
    console.error('Error checking cached response:', error);
    return null;
  }
}

async function cacheMentorResponse(
  originalMessage: string,
  response: string,
  subscriptionTier: string,
  currentSection?: string,
  tokensUsed?: number
): Promise<void> {
  try {
    const normalizedQuestion = normalizeQuestion(originalMessage);
    const questionHash = generateQuestionHash(normalizedQuestion, subscriptionTier, currentSection);

    await storage.createCachedAiResponse({
      questionHash,
      originalQuestion: originalMessage,
      response,
      subscriptionTier: subscriptionTier as any,
      currentSection,
      modelUsed: 'gpt-4o',
      tokensUsed
    });

    console.log(`Cached AI response for question hash: ${questionHash}`);
  } catch (error) {
    console.error('Error caching AI response:', error);
  }
}

export async function getMentorResponse(
  message: string, 
  context?: string, 
  currentSection: string = '101', 
  subscriptionTier: string = 'basic'
): Promise<string> {
  try {
    // Check for cached response first
    const cachedResponse = await getCachedMentorResponse(message, subscriptionTier, currentSection);
    if (cachedResponse) {
      return cachedResponse;
    }

    // Basic plan users are restricted to current section only
    if (subscriptionTier === 'basic') {
      const sectionValidation = await validateSectionRelevance(message, currentSection);
      if (!sectionValidation.isRelevant) {
        return `I'm focused specifically on **Section ${currentSection}** of the Louisiana State Plumbing Code. 📖\n\n${sectionValidation.redirectMessage}\n\n💡 **Want access to the complete codebook?** Upgrade to Professional or Master plan for unlimited AI mentor access across all Louisiana plumbing code sections!\n\nFor now, please ask me questions about Section ${currentSection} topics! 🔧`;
      }

      const sectionContent = getSectionContent(currentSection);
      const systemPrompt = `You are an expert Louisiana plumbing mentor specializing in the Louisiana State Plumbing Code (LSPC). You are currently helping a BASIC PLAN student with **SECTION ${currentSection}** ONLY.

      ${sectionContent}

      **Your teaching approach:**
      - ONLY answer questions related to Section ${currentSection} 
      - Provide detailed, educational responses with specific code references
      - Use formatting like **bold**, • bullet points, and emojis for clarity
      - Connect concepts to real-world applications in Louisiana
      - Encourage further learning with follow-up questions about Section ${currentSection}
      - Always be supportive and encouraging to students
      - Reference specific Louisiana statutes and code sections when relevant

      **Context provided:** ${context || `Louisiana Plumbing Code Section ${currentSection} content`}

      Answer questions ONLY about Section ${currentSection} topics. If asked about other sections, redirect them back to Section ${currentSection} content.`;

      const response = await openai.chat.completions.create({
        model: "gpt-4o",
        messages: [
          { role: "system", content: systemPrompt },
          { role: "user", content: message }
        ],
        max_tokens: 1200,
        temperature: 0.7,
      });

      const responseContent = response.choices[0].message.content || "I'm sorry, I couldn't provide a response at this time.";

      // Cache the response for future use
      await cacheMentorResponse(message, responseContent, subscriptionTier, currentSection, response.usage?.total_tokens);

      return responseContent;
    }

    // Professional and Master users get complete codebook access
    const systemPrompt = `You are an expert Louisiana plumbing mentor specializing in the Louisiana State Plumbing Code (LSPC). You are helping a ${subscriptionTier.toUpperCase()} PLAN student with COMPLETE CODEBOOK ACCESS.

    **Complete Louisiana Plumbing Code Knowledge:**
    - **Section 101 - Administration**: Enforcement, authority, legal basis, delegation
    - **Section 103 - Permits**: Requirements, applications, validity, exemptions
    - **Section 105 - Inspections**: Procedures, qualifications, records, compliance
    - **Section 107 - Violations**: Penalties, notices, appeals, emergency procedures
    - **Section 109 - Plan Review**: Requirements, standards, approval processes
    - **Installation Codes**: Pipe sizing, fixtures, connections, materials
    - **Safety Standards**: Testing, backflow prevention, pressure requirements
    - **Specialized Systems**: Medical gas, natural gas, backflow devices

    **Your teaching approach:**
    - Answer questions about ANY section of the Louisiana plumbing code
    - Provide detailed, educational responses with specific code references
    - Use formatting like **bold**, • bullet points, and emojis for clarity
    - Connect concepts to real-world applications in Louisiana
    - Cross-reference between different code sections when relevant
    - Always be supportive and encouraging to students
    - Reference specific Louisiana statutes and code sections

    **Context provided:** ${context || 'Complete Louisiana Plumbing Code access'}

    Answer questions about ANY Louisiana plumbing code section, installation technique, safety standard, or compliance requirement.`;

    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        { role: "system", content: systemPrompt },
        { role: "user", content: message }
      ],
      max_tokens: 1500,
      temperature: 0.7,
    });

    const responseContent = response.choices[0].message.content || "I'm sorry, I couldn't provide a response at this time.";

    // Cache the response for future use (no currentSection for pro/master users)
    await cacheMentorResponse(message, responseContent, subscriptionTier, undefined, response.usage?.total_tokens);

    return responseContent;
  } catch (error) {
    console.error("OpenAI API error:", error);
    // Provide a fallback response based on common Louisiana plumbing questions
    if (message.toLowerCase().includes("enforce")) {
      return "The Louisiana State Plumbing Code (LSPC) is enforced by the state health officer, who can delegate this authority to local plumbing inspectors and other qualified entities at the parish and municipal level.";
    } else if (message.toLowerCase().includes("legal basis")) {
      return "The legal foundation for the Louisiana State Plumbing Code stems from Louisiana Revised Statutes (R.S.), primarily R.S. 36:258(B), with additional provisions in Chapters 1 and 4 of Title 40.";
    } else if (message.toLowerCase().includes("historical") || message.toLowerCase().includes("promulgate")) {
      return "The Louisiana State Plumbing Code was first promulgated in June 2002 (Louisiana Register, Vol. 28, No. 6) and was amended in November 2012 (Louisiana Register, Vol. 38, No. 11).";
    }
    return "I'm having trouble connecting to the AI service right now, but I can help you learn about Louisiana Plumbing Code administration. Please try asking about enforcement authority, legal basis, or historical notes.";
  }
}

export async function calculatePipeSize(fixtureUnits: number, pipeLength: number, material: string): Promise<{
  recommendedSize: string;
  velocityCheck: boolean;
  pressureLoss: number;
  explanation: string;
}> {
  try {
