          duplicates.push(sp);
        } else {
          newStudyPlans.push({
            ...sp,
            id: crypto.randomUUID(),
            courseId,
            createdAt: new Date(),
          });
          // Add to set to prevent duplicates within this batch too
          existingStudyPlanTitles.add(normalizedTitle);
        }
      });

