        success: true, 
        imported: createdStudyPlans.length,
        duplicatesSkipped: duplicates.length,
        totalSubmitted: studyPlans.length,
        studyPlans: createdStudyPlans,
        duplicates: duplicates.map(d => d.title.substring(0, 100) + "...")
