      .select({
        section: courseContent.section,
        count: sql<number>`COUNT(*)`
      })
      .from(courseContent)
      .where(and(eq(courseContent.courseId, courseId), eq(courseContent.type, 'study-plan')))
      .groupBy(courseContent.section);

    // Get actual difficulty breakdown for study plans
    const difficultyResults = await db
      .select({
        difficulty: courseContent.difficulty,
        count: sql<number>`COUNT(*)`
      })
      .from(courseContent)
      .where(and(eq(courseContent.courseId, courseId), eq(courseContent.type, 'study-plan')))
      .groupBy(courseContent.difficulty);

    // Ensure all difficulty levels are represented
    const difficultyMap = new Map(difficultyResults.map(d => [d.difficulty, d.count]));
    const difficulty = [
      { difficulty: 'easy', count: difficultyMap.get('easy') || 0 },
      { difficulty: 'hard', count: difficultyMap.get('hard') || 0 },
      { difficulty: 'very_hard', count: difficultyMap.get('very_hard') || 0 }
    ];

    return {
      byChapter: chapters.map(item => ({ category: `Chapter ${item.chapter}`, count: item.count })),
      bySection: sections.map(item => ({ codeReference: `Section ${item.section}`, count: item.count })),
      byDifficulty: difficulty
    };
  }

  // Podcasts breakdown - only chapter/section, no difficulty
  async getUserProgressBreakdowns(courseId: string): Promise<any> {
    const chapters = await db
      .select({
        chapter: courseContent.chapter,
        count: sql<number>`COUNT(*)`
      })
      .from(courseContent)
      .where(and(eq(courseContent.courseId, courseId), eq(courseContent.type, 'podcast')))
      .groupBy(courseContent.chapter);

    const sections = await db
      .select({
        section: courseContent.section,
        count: sql<number>`COUNT(*)`
      })
      .from(courseContent)
      .where(and(eq(courseContent.courseId, courseId), eq(courseContent.type, 'podcast')))
      .groupBy(courseContent.section);

    return {
      byChapter: chapters.map(item => ({ category: `Chapter ${item.chapter}`, count: item.count })),
      bySection: sections.map(item => ({ codeReference: `Section ${item.section}`, count: item.count }))
      // No byDifficulty for podcasts - they're just educational content, not difficulty-based
    };
  }

  // Audio cache methods
  async getCachedAudio(contentHash: string): Promise<AudioCache | undefined> {
    const [audio] = await db.select().from(audioCache).where(eq(audioCache.contentHash, contentHash));
    return audio || undefined;
  }

  async createCachedAudio(audioData: InsertAudioCache): Promise<AudioCache> {
    const [audio] = await db.insert(audioCache).values(audioData).returning();
    return audio;
  }

  async updateAudioAccess(contentHash: string): Promise<void> {
    await db
      .update(audioCache)
      .set({ 
        lastAccessedAt: new Date(),
        accessCount: sql`${audioCache.accessCount} + 1`
      })
      .where(eq(audioCache.contentHash, contentHash));
  }

  // AI response cache methods
  async getCachedAiResponse(questionHash: string): Promise<AiResponseCache | undefined> {
    const [response] = await db
      .select()
      .from(aiResponseCache)
      .where(and(
        eq(aiResponseCache.questionHash, questionHash),
        eq(aiResponseCache.isValid, true)
      ));
    return response || undefined;
  }

  async createCachedAiResponse(responseData: InsertAiResponseCache): Promise<AiResponseCache> {
    const [response] = await db.insert(aiResponseCache).values(responseData).returning();
    return response;
  }

  async updateAiResponseAccess(questionHash: string): Promise<void> {
    await db
      .update(aiResponseCache)
      .set({ 
        lastAccessedAt: new Date(),
        accessCount: sql`${aiResponseCache.accessCount} + 1`
      })
      .where(eq(aiResponseCache.questionHash, questionHash));
  }

  async invalidateAiResponseCache(questionHash?: string): Promise<void> {
    if (questionHash) {
      // Invalidate specific cached response
      await db
        .update(aiResponseCache)
        .set({ isValid: false })
        .where(eq(aiResponseCache.questionHash, questionHash));
    } else {
      // Invalidate all cached responses (for global cache refresh)
      await db
        .update(aiResponseCache)
        .set({ isValid: false });
    }
  }
}

export const storage = new DatabaseStorage();

