import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql, relations } from "drizzle-orm";
import { 
  pgTable, 
  text, 
  varchar, 
  integer, 
  serial,
  decimal, 
  boolean, 
  timestamp, 
  jsonb,
  pgEnum,
  unique
} from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Enums
export const subscriptionTierEnum = pgEnum("subscription_tier", ["basic", "professional", "master"]);
export const courseTypeEnum = pgEnum("course_type", ["journeyman", "backflow", "natural_gas", "medical_gas", "master"]);
export const jobTypeEnum = pgEnum("job_type", ["full_time", "part_time", "contract", "temporary"]);
export const paymentStatusEnum = pgEnum("payment_status", ["pending", "completed", "failed", "cancelled"]);
export const productCategoryEnum = pgEnum("product_category", ["gas_detection", "pipe_tools", "measuring", "safety", "valves", "fittings", "books", "training_materials"]);
export const jobStatusEnum = pgEnum("job_status", ["pending", "approved", "rejected", "expired"]);
export const emailCampaignTypeEnum = pgEnum("email_campaign_type", ["employer_onboarding", "student_enrollment", "bulk_enrollment"]);
export const emailStatusEnum = pgEnum("email_status", ["pending", "sent", "failed", "cancelled"]);
export const bulkEnrollmentStatusEnum = pgEnum("bulk_enrollment_status", ["pending", "approved", "active", "cancelled"]);
export const leadSourceEnum = pgEnum("lead_source", ["linkedin", "website", "referral", "google", "facebook", "other"]);
export const paymentMethodEnum = pgEnum("payment_method", ["paypal", "stripe_transfer", "account_credit"]);
export const payoutStatusEnum = pgEnum("payout_status", ["pending", "processing", "completed", "failed", "cancelled"]);
export const achievementTypeEnum = pgEnum("achievement_type", ["course_completion", "quiz_mastery", "study_streak", "first_login", "perfect_score", "speed_demon", "social_sharer", "mentor_helper"]);
export const questionDifficultyEnum = pgEnum("question_difficulty", ["easy", "hard", "very_hard"]);
export const competitionStatusEnum = pgEnum("competition_status", ["upcoming", "active", "completed", "cancelled"]);
export const productSearchCategoryEnum = pgEnum("product_search_category", ["tools", "pipe_fittings", "valves", "safety_equipment", "measuring_tools", "books", "supplies", "all"]);

// Users table
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: text("email").notNull().unique(),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
  firstName: text("first_name"),
  lastName: text("last_name"),
  phone: text("phone"),
  subscriptionTier: subscriptionTierEnum("subscription_tier").default("basic"),
  stripeCustomerId: text("stripe_customer_id"),
  stripeSubscriptionId: text("stripe_subscription_id"),
  referralCode: text("referral_code").unique(),
  referredBy: text("referred_by"),
  // Payment preferences
  preferredPaymentMethod: paymentMethodEnum("preferred_payment_method").default("paypal"),
  paypalEmail: text("paypal_email"),
  accountBalance: decimal("account_balance", { precision: 10, scale: 2 }).default("0.00"),
  minimumPayout: decimal("minimum_payout", { precision: 10, scale: 2 }).default("25.00"),
  // Gamification fields
  totalPoints: integer("total_points").default(0),
  currentStreak: integer("current_streak").default(0),
  longestStreak: integer("longest_streak").default(0),
  lastLoginDate: timestamp("last_login_date"),
  // Beta testing fields
  isBetaTester: boolean("is_beta_tester").default(false),
  betaStartedAt: timestamp("beta_started_at"),
  // Adaptive difficulty settings
  preferredDifficulty: questionDifficultyEnum("preferred_difficulty").default("easy"),
  currentSkillLevel: questionDifficultyEnum("current_skill_level").default("easy"),
  adaptiveDifficultyEnabled: boolean("adaptive_difficulty_enabled").default(true),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Courses table
export const courses = pgTable("courses", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description").notNull(),
  type: courseTypeEnum("type").notNull(),
  price: decimal("price", { precision: 10, scale: 2 }).notNull(),
  duration: text("duration"), // e.g., "2 months", "1.5 months"
  lessons: integer("lessons"),
  practiceQuestions: integer("practice_questions"),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Course enrollments
export const courseEnrollments = pgTable("course_enrollments", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: text("user_id").notNull(),
  courseId: text("course_id").notNull(),
  progress: decimal("progress", { precision: 5, scale: 2 }).default("0"),
  completedLessons: integer("completed_lessons").default(0),
  testScores: jsonb("test_scores"), // Array of test scores
  isCompleted: boolean("is_completed").default(false),
  enrolledAt: timestamp("enrolled_at").defaultNow(),
  completedAt: timestamp("completed_at"),
});

// Employers table
export const employers = pgTable("employers", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  companyName: text("company_name").notNull(),
  contactName: text("contact_name").notNull(),
  contactEmail: text("contact_email").notNull().unique(),
  contactPhone: text("contact_phone"),
  companyWebsite: text("company_website"),
  companyDescription: text("company_description"),
  address: text("address"),
  city: text("city"),
  state: text("state").default("Louisiana"),
  zipCode: text("zip_code"),
  isVerified: boolean("is_verified").default(false),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Jobs table
export const jobs = pgTable("jobs", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  employerId: text("employer_id").notNull(),
  title: text("title").notNull(),
  company: text("company").notNull(),
  description: text("description").notNull(),
  location: text("location").notNull(),
  type: jobTypeEnum("type").notNull(),
  salaryMin: decimal("salary_min", { precision: 10, scale: 2 }),
  salaryMax: decimal("salary_max", { precision: 10, scale: 2 }),
  requirements: text("requirements").array(),
  benefits: text("benefits").array(),
  contactEmail: text("contact_email").notNull(),
  status: jobStatusEnum("status").default("pending"),
  isFeatured: boolean("is_featured").default(false),
  isActive: boolean("is_active").default(true),
  rejectionReason: text("rejection_reason"),
  approvedBy: text("approved_by"),
  approvedAt: timestamp("approved_at"),
  expiresAt: timestamp("expires_at"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Job applications
export const jobApplications = pgTable("job_applications", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  jobId: text("job_id").notNull(),
  userId: text("user_id").notNull(),
  resumeUrl: text("resume_url"),
  coverLetter: text("cover_letter"),
  status: text("status").default("pending"),
  appliedAt: timestamp("applied_at").defaultNow(),
});

// AI mentor conversations
export const mentorConversations = pgTable("mentor_conversations", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: text("user_id").notNull(),
  messages: jsonb("messages").notNull(), // Array of messages
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Study companion chat messages
export const studyCompanionMessages = pgTable("study_companion_messages", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: text("user_id").notNull(),
  role: text("role").notNull(), // "user" or "assistant"
  content: text("content").notNull(),
  emotion: text("emotion"), // For assistant messages: "happy", "excited", "helpful", "thinking", "encouraging"
  timestamp: timestamp("timestamp").defaultNow(),
});

// Chat answers for course content
export const chatAnswers = pgTable("chat_answers", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  question: text("question").notNull(),
  answer: text("answer").notNull(),
  contentId: text("content_id"),
  keywords: text("keywords").array(),
  createdAt: timestamp("created_at").defaultNow(),
});

// Flashcards for course study
export const flashcards = pgTable("flashcards", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  courseId: text("course_id").notNull(),
  front: text("front").notNull(),
  back: text("back").notNull(),
  difficulty: text("difficulty"), // Optional difficulty level
  chapter: integer("chapter"), // Chapter number
  section: integer("section"), // Section number
  codeReference: text("code_reference"), // Chapter and section reference string
  category: text("category"), // Category/chapter description
  tags: text("tags").array(), // Optional tags for categorization
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Photo uploads for code checking
export const photoUploads = pgTable("photo_uploads", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: text("user_id").notNull(),
  filename: text("filename").notNull(),
  url: text("url").notNull(),
  analysis: jsonb("analysis"), // AI analysis results
  createdAt: timestamp("created_at").defaultNow(),
});

// Plan uploads
export const planUploads = pgTable("plan_uploads", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: text("user_id").notNull(),
  filename: text("filename").notNull(),
  url: text("url").notNull(),
  materialList: jsonb("material_list"), // Generated material list
  analysis: jsonb("analysis"), // Plan analysis results
  createdAt: timestamp("created_at").defaultNow(),
});

// Audio cache for generated TTS files
export const audioCache = pgTable("audio_cache", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  contentHash: text("content_hash").notNull().unique(), // Hash of the text content
  contentId: text("content_id").notNull(), // Content identifier for organization
  audioData: text("audio_data").notNull(), // Base64 encoded audio data (MP3)
  mimeType: text("mime_type").notNull().default("audio/mpeg"), // MIME type
  fileSize: integer("file_size").notNull(), // File size in bytes
  duration: integer("duration"), // Audio duration in seconds (optional)
  generatedAt: timestamp("generated_at").defaultNow(),
  lastAccessedAt: timestamp("last_accessed_at").defaultNow(),
  accessCount: integer("access_count").default(0), // Track usage for cleanup
});

// AI response cache for mentor chat responses
export const aiResponseCache = pgTable("ai_response_cache", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  questionHash: text("question_hash").notNull().unique(), // Hash of normalized question
  originalQuestion: text("original_question").notNull(), // Original question text
  response: text("response").notNull(), // AI generated response
  contextHash: text("context_hash"), // Hash of context (section, tier, etc.) for more specific caching
  subscriptionTier: subscriptionTierEnum("subscription_tier"), // Tier-specific responses
  currentSection: text("current_section"), // Course section context
  modelUsed: text("model_used").default("gpt-5"), // Track which AI model generated the response
  tokensUsed: integer("tokens_used"), // Track API usage for analytics
  generatedAt: timestamp("generated_at").defaultNow(),
  lastAccessedAt: timestamp("last_accessed_at").defaultNow(),
  accessCount: integer("access_count").default(0), // Track usage for cache management
  isValid: boolean("is_valid").default(true), // Flag for cache invalidation
});

// Referrals
export const referrals = pgTable("referrals", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  referrerId: text("referrer_id").notNull(),
  referredId: text("referred_id").notNull(),
  referrerPlanTier: subscriptionTierEnum("referrer_plan_tier").notNull(),
  referredPlanTier: subscriptionTierEnum("referred_plan_tier").notNull(),
  referredPlanPrice: decimal("referred_plan_price", { precision: 10, scale: 2 }).notNull(),
  commissionRate: decimal("commission_rate", { precision: 5, scale: 4 }).default("0.10"), // 10% default
  commissionAmount: decimal("commission_amount", { precision: 10, scale: 2 }),
  eligibleTier: subscriptionTierEnum("eligible_tier").notNull(), // The tier used for commission calculation
  isPaid: boolean("is_paid").default(false),
  createdAt: timestamp("created_at").defaultNow(),
  paidAt: timestamp("paid_at"),
});

// Monthly recurring commissions from subscription upgrades/renewals
export const monthlyCommissions = pgTable("monthly_commissions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  referralId: text("referral_id").notNull().references(() => referrals.id),
  referrerId: text("referrer_id").notNull(),
  referredUserId: text("referred_user_id").notNull(),
  commissionMonth: varchar("commission_month").notNull(), // Format: "2025-01" 
  referrerTierAtTime: subscriptionTierEnum("referrer_tier_at_time").notNull(),
  referredTierAtTime: subscriptionTierEnum("referred_tier_at_time").notNull(),
  eligibleTier: subscriptionTierEnum("eligible_tier").notNull(),
  commissionAmount: decimal("commission_amount", { precision: 10, scale: 2 }).notNull(),
  commissionRate: decimal("commission_rate", { precision: 5, scale: 4 }).default("0.10"),
  isPaid: boolean("is_paid").default(false),
  createdAt: timestamp("created_at").defaultNow(),
  paidAt: timestamp("paid_at"),
}, (table) => ({
  uniqueMonthlyCommission: unique("unique_monthly_commission").on(table.referralId, table.commissionMonth),
}));

// Payout requests
export const payoutRequests = pgTable("payout_requests", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: text("user_id").notNull(),
  amount: decimal("amount", { precision: 10, scale: 2 }).notNull(),
  paymentMethod: paymentMethodEnum("payment_method").notNull(),
  paymentDetails: jsonb("payment_details"), // PayPal email, Stripe details, etc.
  status: payoutStatusEnum("status").default("pending"),
  referralIds: text("referral_ids").array(), // Array of referral IDs included in this payout
  monthlyCommissionIds: text("monthly_commission_ids").array(), // Array of monthly commission IDs
  transactionId: text("transaction_id"), // External payment service transaction ID
  failureReason: text("failure_reason"),
  requestedAt: timestamp("requested_at").defaultNow(),
  processedAt: timestamp("processed_at"),
  completedAt: timestamp("completed_at"),
  createdAt: timestamp("created_at").defaultNow(),
});

// Account credit transactions
export const accountCreditTransactions = pgTable("account_credit_transactions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: text("user_id").notNull(),
  amount: decimal("amount", { precision: 10, scale: 2 }).notNull(),
  type: varchar("type").notNull(), // "credit", "debit", "refund"
  description: text("description").notNull(),
  referenceId: text("reference_id"), // Reference to commission, refund, etc.
  balanceBefore: decimal("balance_before", { precision: 10, scale: 2 }).notNull(),
  balanceAfter: decimal("balance_after", { precision: 10, scale: 2 }).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

// Course content (lessons, quizzes, etc.)
export const courseContent = pgTable("course_content", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  courseId: text("course_id").notNull(),
  title: text("title").notNull(),
  type: text("type").notNull(), // 'lesson', 'quiz', 'review', 'video'
  chapter: integer("chapter"),
  section: integer("section"),
  content: jsonb("content"), // Main content (HTML, quiz questions, etc.)
  duration: integer("duration"), // in minutes
  difficulty: questionDifficultyEnum("difficulty"), // easy, hard, very_hard
  quizgeckoUrl: text("quizgecko_url"), // URL for QuizGecko content
  isActive: boolean("is_active").default(true),
  sortOrder: integer("sort_order").default(0),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Private code books (admin reference only)
export const privateCodeBooks = pgTable("private_code_books", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  filename: text("filename").notNull(),
  originalName: text("original_name").notNull(),
  filePath: text("file_path").notNull(),
  fileSize: integer("file_size").notNull(),
  contentType: text("content_type").notNull(),
  uploadedBy: text("uploaded_by").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

// Study sessions (time tracking)
export const studySessions = pgTable("study_sessions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: text("user_id").notNull(),
  contentId: text("content_id").notNull(),
  contentType: text("content_type").notNull(), // 'lesson', 'flashcards', 'quiz', 'chat', 'podcast', 'study_notes', 'study_plans'
  sessionStart: timestamp("session_start").defaultNow(),
  sessionEnd: timestamp("session_end"),
  durationSeconds: integer("duration_seconds"), // calculated duration in seconds
  completed: boolean("completed").default(false),
  createdAt: timestamp("created_at").defaultNow(),
});

// Quiz attempts
export const quizAttempts = pgTable("quiz_attempts", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: text("user_id").notNull(),
  contentId: text("content_id").notNull(), // quiz content ID
  courseId: text("course_id").notNull(),
  section: integer("section").notNull(),
  chapter: integer("chapter").notNull(),
  questions: jsonb("questions").notNull(), // Array of questions with user answers
  totalQuestions: integer("total_questions").notNull(),
  correctAnswers: integer("correct_answers").notNull(),
  score: decimal("score", { precision: 5, scale: 2 }).notNull(), // percentage score
  passed: boolean("passed").notNull(), // true if score >= 70%
  completedAt: timestamp("completed_at").defaultNow(),
  createdAt: timestamp("created_at").defaultNow(),
});

// Section progress tracking
export const sectionProgress = pgTable("section_progress", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: text("user_id").notNull(),
  courseId: text("course_id").notNull(),
  chapter: integer("chapter").notNull(),
  section: integer("section").notNull(),
  isUnlocked: boolean("is_unlocked").default(false),
  quizPassed: boolean("quiz_passed").default(false),
  highestScore: decimal("highest_score", { precision: 5, scale: 2 }).default("0"),
  attemptCount: integer("attempt_count").default(0),
  lastAttemptAt: timestamp("last_attempt_at"),
  unlockedAt: timestamp("unlocked_at"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Lesson step progress tracking (detailed progress within each lesson)
