import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/lib/auth.ts.content:
import { apiRequest } from "./queryClient";

export interface LoginCredentials {
  email: string;
  password: string;
}

export interface RegisterData {
  firstName: string;
  lastName: string;
  email: string;
  username: string;
  password: string;
  phone?: string;
  referredBy?: string;
}

export interface User {
  id: string;
  email: string;
  username: string;
  firstName?: string;
  lastName?: string;
  phone?: string;
  subscriptionTier: 'basic' | 'professional' | 'master';
  stripeCustomerId?: string;
  stripeSubscriptionId?: string;
  referralCode?: string;
  isActive: boolean;
  createdAt: string;
  updatedAt: string;
}

export class AuthService {
  /**
   * Authenticate user with email and password
   */
  static async login(credentials: LoginCredentials): Promise<User> {
    const response = await apiRequest("POST", "/api/auth/login", credentials);
    return response.json();
  }

  /**
   * Register a new user account
   */
  static async register(data: RegisterData): Promise<{ message: string; userId: string }> {
    const response = await apiRequest("POST", "/api/auth/register", data);
    return response.json();
  }

  /**
   * Sign out the current user
   */
  static async logout(): Promise<void> {
    await apiRequest("POST", "/api/auth/logout");
  }

  /**
   * Get current authenticated user information
   */
  static async getCurrentUser(): Promise<User | null> {
    try {
      const response = await apiRequest("GET", "/api/auth/me");
      return response.json();
    } catch (error) {
      // Return null if not authenticated (401 error)
      return null;
    }
  }

  /**
   * Check if user is authenticated
   */
  static async isAuthenticated(): Promise<boolean> {
    try {
      const user = await this.getCurrentUser();
      return user !== null;
    } catch {
      return false;
    }
  }

  /**
   * Get user's subscription status
   */
  static getSubscriptionTier(user: User | null): 'basic' | 'professional' | 'master' {
    return user?.subscriptionTier || 'basic';
  }

  /**
   * Check if user has access to specific features based on subscription tier
   */
  static hasFeatureAccess(user: User | null, feature: string): boolean {
    const tier = this.getSubscriptionTier(user);

    const featureAccess = {
      basic: [
        'job_board',
        'basic_calculators',
        'practice_tests',
        'email_support',
        'ai_mentor'
      ],
      professional: [
        'job_board',
        'basic_calculators',
        'complete_calculator_suite',
        'photo_code_checker',
        'ai_mentor',
        'resume_builder',
        'priority_support',
        'practice_tests'
      ],
      master: [
        'job_board',
        'basic_calculators',
        'complete_calculator_suite',
        'photo_code_checker',
        'ai_mentor',
        'resume_builder',
        'plan_analysis',
        'material_list_generator',
        'referral_commissions',
        'book_store',
        'white_glove_support',
        'practice_tests'
      ]
    };

    return featureAccess[tier].includes(feature);
  }

  /**
   * Format user display name
   */
  static getDisplayName(user: User | null): string {
    if (!user) return 'Guest';

    if (user.firstName && user.lastName) {
      return `${user.firstName} ${user.lastName}`;
    }

    if (user.firstName) {
      return user.firstName;
    }

    return user.username;
  }

  /**
   * Check if user has completed profile setup
   */
  static isProfileComplete(user: User | null): boolean {
    if (!user) return false;

    return !!(user.firstName && user.lastName && user.email);
  }

  /**
   * Get user's referral code for sharing
   */
  static getReferralCode(user: User | null): string | null {
    return user?.referralCode || null;
  }

  /**
   * Generate referral link
   */
  static generateReferralLink(user: User | null): string | null {
    const referralCode = this.getReferralCode(user);
    if (!referralCode) return null;

    const baseUrl = window.location.origin;
    return `${baseUrl}/register?ref=${referralCode}`;
  }

  /**
   * Validate password strength
   */
  static validatePassword(password: string): {
    isValid: boolean;
    errors: string[];
    strength: 'weak' | 'medium' | 'strong';
  } {
    const errors: string[] = [];

    if (password.length < 8) {
      errors.push('Password must be at least 8 characters long');
    }

    if (!/[A-Z]/.test(password)) {
      errors.push('Password must contain at least one uppercase letter');
    }

    if (!/[a-z]/.test(password)) {
      errors.push('Password must contain at least one lowercase letter');
    }

    if (!/\d/.test(password)) {
      errors.push('Password must contain at least one number');
    }

    if (!/[!@#$%^&*(),.?":{}|<>]/.test(password)) {
      errors.push('Password should contain at least one special character');
    }

    const isValid = errors.length === 0;

    let strength: 'weak' | 'medium' | 'strong' = 'weak';
    if (password.length >= 12 && /[A-Z]/.test(password) && /[a-z]/.test(password) && /\d/.test(password) && /[!@#$%^&*(),.?":{}|<>]/.test(password)) {
      strength = 'strong';
    } else if (password.length >= 8 && errors.length <= 1) {
      strength = 'medium';
    }

    return { isValid, errors, strength };
  }

  /**
   * Validate email format
   */
  static validateEmail(email: string): boolean {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  }

  /**
   * Get user's subscription status information
   */
  static getSubscriptionInfo(user: User | null) {
    if (!user) return null;

    const tierInfo = {
      basic: {
        name: 'Basic',
        price: '$49/month',
        maxCourses: 1,
        features: ['1 Certification Track', 'Basic Calculator Tools', 'Practice Tests', 'Job Board Access', 'Email Support']
      },
      professional: {
        name: 'Professional',
        price: '$79/month',
        maxCourses: 3,
        features: ['3 Certification Tracks', 'Complete Calculator Suite', 'Photo Code Checker', 'AI Mentor Support', 'Resume Builder', 'Priority Support']
      },
      master: {
        name: 'Master',
        price: '$99/month',
        maxCourses: 5,
        features: ['All 5 Certification Tracks', 'Plan Analysis Tools', 'Material List Generator', 'Referral Commissions', 'Book Store Access', 'White-Glove Support']
      }
    };

    return tierInfo[user.subscriptionTier] || tierInfo.basic;
  }
}

// Export utility functions for use in components
export const {
  login,
  register,
  logout,
  getCurrentUser,
  isAuthenticated,
  hasFeatureAccess,
  getDisplayName,
  isProfileComplete,
  getReferralCode,
  generateReferralLink,
  validatePassword,
  validateEmail,
  getSubscriptionInfo
} = AuthService;

