  );
}

client/src/pages/auth/register.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Building2, Eye, EyeOff, CheckCircle } from "lucide-react";
import { Link, useLocation } from "wouter";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

export default function Register() {
  const [, setLocation] = useLocation();
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    username: "",
    password: "",
    confirmPassword: "",
    phone: "",
    referredBy: "",
  });
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const { toast } = useToast();

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const validateForm = () => {
    if (formData.password !== formData.confirmPassword) {
      setError("Passwords do not match");
      return false;
    }
    if (formData.password.length < 8) {
      setError("Password must be at least 8 characters long");
      return false;
    }
    return true;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError("");

    if (!validateForm()) return;

    setIsLoading(true);

    try {
      await apiRequest("POST", "/api/auth/register", {
        firstName: formData.firstName,
        lastName: formData.lastName,
        email: formData.email,
        username: formData.username,
        password: formData.password,
        phone: formData.phone,
        referredBy: formData.referredBy || undefined,
      });

      toast({
        title: "Account created successfully!",
        description: "Choose your learning plan to get started.",
      });

      setLocation("/pricing");
    } catch (error: any) {
      setError(error.message || "Registration failed. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const passwordRequirements = [
    { met: formData.password.length >= 8, text: "At least 8 characters" },
    { met: /[A-Z]/.test(formData.password), text: "One uppercase letter" },
    { met: /[a-z]/.test(formData.password), text: "One lowercase letter" },
    { met: /\d/.test(formData.password), text: "One number" },
  ];

  return (
    <div className="min-h-screen bg-background py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-2xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8" data-testid="register-header">
          <Link href="/">
            <div className="flex items-center justify-center space-x-2 mb-6 cursor-pointer">
              <div className="w-12 h-12 bg-primary rounded-lg flex items-center justify-center">
                <Building2 className="w-7 h-7 text-primary-foreground" />
              </div>
              <div className="text-left">
                <h1 className="text-xl font-bold text-primary">LA Plumb Prep</h1>
                <p className="text-xs text-muted-foreground">Official State Prep Course</p>
              </div>
            </div>
          </Link>
          <h2 className="text-3xl font-bold text-foreground" data-testid="register-title">
            Create your account
          </h2>
          <p className="mt-2 text-muted-foreground" data-testid="register-subtitle">
            Start your plumbing education journey today
          </p>
        </div>

        {/* Registration Form */}
        <Card data-testid="register-form">
          <CardHeader>
            <CardTitle>Join LA Plumb Prep</CardTitle>
          </CardHeader>
          <CardContent>
            {error && (
              <Alert className="mb-6" data-testid="register-error">
                <AlertDescription>{error}</AlertDescription>
              </Alert>
            )}

            <form onSubmit={handleSubmit} className="space-y-6">
              {/* Name Fields */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="firstName">First Name</Label>
                  <Input
                    id="firstName"
                    name="firstName"
                    type="text"
                    value={formData.firstName}
                    onChange={handleInputChange}
                    required
                    className="mt-1"
                    placeholder="Enter your first name"
                    data-testid="input-first-name"
                  />
                </div>
                <div>
                  <Label htmlFor="lastName">Last Name</Label>
                  <Input
                    id="lastName"
                    name="lastName"
                    type="text"
                    value={formData.lastName}
                    onChange={handleInputChange}
                    required
                    className="mt-1"
                    placeholder="Enter your last name"
                    data-testid="input-last-name"
                  />
                </div>
              </div>

              {/* Email and Username */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="email">Email Address</Label>
                  <Input
                    id="email"
                    name="email"
                    type="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                    className="mt-1"
                    placeholder="Enter your email"
                    data-testid="input-email"
                  />
                </div>
                <div>
                  <Label htmlFor="username">Username</Label>
                  <Input
                    id="username"
                    name="username"
                    type="text"
                    value={formData.username}
                    onChange={handleInputChange}
                    required
                    className="mt-1"
                    placeholder="Choose a username"
                    data-testid="input-username"
                  />
                </div>
              </div>

              {/* Phone Number */}
              <div>
                <Label htmlFor="phone">Phone Number (Optional)</Label>
                <Input
                  id="phone"
                  name="phone"
                  type="tel"
                  value={formData.phone}
                  onChange={handleInputChange}
                  className="mt-1"
                  placeholder="(555) 123-4567"
                  data-testid="input-phone"
                />
              </div>

              {/* Password Fields */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="password">Password</Label>
                  <div className="relative mt-1">
                    <Input
                      id="password"
                      name="password"
                      type={showPassword ? "text" : "password"}
                      value={formData.password}
                      onChange={handleInputChange}
                      required
                      placeholder="Create a password"
                      data-testid="input-password"
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="icon"
                      className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                      onClick={() => setShowPassword(!showPassword)}
                      data-testid="toggle-password"
                    >
                      {showPassword ? (
                        <EyeOff className="h-4 w-4 text-muted-foreground" />
                      ) : (
                        <Eye className="h-4 w-4 text-muted-foreground" />
                      )}
                    </Button>
                  </div>
                </div>
                <div>
                  <Label htmlFor="confirmPassword">Confirm Password</Label>
                  <div className="relative mt-1">
                    <Input
                      id="confirmPassword"
                      name="confirmPassword"
                      type={showConfirmPassword ? "text" : "password"}
                      value={formData.confirmPassword}
                      onChange={handleInputChange}
                      required
                      placeholder="Confirm your password"
                      data-testid="input-confirm-password"
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="icon"
                      className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                      onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                      data-testid="toggle-confirm-password"
                    >
                      {showConfirmPassword ? (
                        <EyeOff className="h-4 w-4 text-muted-foreground" />
                      ) : (
                        <Eye className="h-4 w-4 text-muted-foreground" />
                      )}
                    </Button>
                  </div>
                </div>
              </div>

              {/* Password Requirements */}
              {formData.password && (
                <div className="space-y-2" data-testid="password-requirements">
                  <p className="text-sm font-medium">Password Requirements:</p>
                  <div className="grid grid-cols-2 gap-2">
                    {passwordRequirements.map((req, index) => (
                      <div key={index} className="flex items-center space-x-2 text-sm">
                        <CheckCircle className={`w-4 h-4 ${req.met ? 'text-green-500' : 'text-muted-foreground'}`} />
                        <span className={req.met ? 'text-green-600' : 'text-muted-foreground'}>
                          {req.text}
                        </span>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Referral Code */}
              <div>
                <Label htmlFor="referredBy">Referral Code (Optional)</Label>
                <Input
                  id="referredBy"
                  name="referredBy"
                  type="text"
                  value={formData.referredBy}
                  onChange={handleInputChange}
                  className="mt-1"
                  placeholder="Enter referral code"
                  data-testid="input-referral-code"
                />
                <p className="text-xs text-muted-foreground mt-1">
                  Have a referral code? Enter it to give your referrer 10% commission.
                </p>
              </div>

              {/* Terms and Conditions */}
              <div className="flex items-center">
                <input
                  id="terms"
                  name="terms"
                  type="checkbox"
                  required
                  className="h-4 w-4 text-primary focus:ring-primary border-border rounded"
                  data-testid="checkbox-terms"
                />
                <Label htmlFor="terms" className="ml-2 text-sm">
                  I agree to the{' '}
                  <a href="#" className="text-primary hover:text-primary/80">Terms of Service</a>
                  {' '}and{' '}
                  <a href="#" className="text-primary hover:text-primary/80">Privacy Policy</a>
                </Label>
              </div>

              <Button
                type="submit"
                className="w-full"
                disabled={isLoading}
                data-testid="button-register"
              >
                {isLoading ? "Creating account..." : "Create Account"}
              </Button>
            </form>

            <div className="mt-6">
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <div className="w-full border-t border-border" />
                </div>
                <div className="relative flex justify-center text-sm">
                  <span className="px-2 bg-background text-muted-foreground">Already have an account?</span>
                </div>
              </div>

              <div className="mt-6 text-center">
                <Link href="/login">
                  <a className="font-medium text-primary hover:text-primary/80" data-testid="link-login">
                    Sign in to your account
                  </a>
                </Link>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Benefits */}
        <div className="text-center mt-8 space-y-4" data-testid="register-benefits">
          <p className="text-sm text-muted-foreground">
            What you'll get with your free account:
          </p>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
            <div className="flex items-center space-x-2">
              <CheckCircle className="w-4 h-4 text-green-500" />
              <span>Access to all certification courses</span>
            </div>
            <div className="flex items-center space-x-2">
              <CheckCircle className="w-4 h-4 text-green-500" />
              <span>Access to job board</span>
            </div>
            <div className="flex items-center space-x-2">
              <CheckCircle className="w-4 h-4 text-green-500" />
              <span>Basic calculator tools</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/course-content.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { useRoute } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  BookOpen, 
  Play, 
  Clock, 
  Star, 
  CheckCircle, 
  ExternalLink,
  Headphones,
  MessageSquare,
  Bookmark,
  NotebookPen,
  Lock
} from "lucide-react";
import { Link } from "wouter";

// Map friendly course identifiers to database UUIDs
function getCourseUUID(courseSlug: string): string {
  const courseMapping: { [key: string]: string } = {
    'journeyman-prep': '5f02238b-afb2-4e7f-a488-96fb471fee56',
    'backflow-prevention': 'b1f02238b-afb2-4e7f-a488-96fb471fee57',
    'natural-gas': 'c2f02238b-afb2-4e7f-a488-96fb471fee58',
    'medical-gas': 'd3f02238b-afb2-4e7f-a488-96fb471fee59',
    'master-plumber': 'e4f02238b-afb2-4e7f-a488-96fb471fee60'
  };
  return courseMapping[courseSlug] || courseSlug;
}

interface CourseContent {
  id: string;
  title: string;
  type: string;
  chapter: number;
  section: number;
  content: any;
  quizgeckoUrl?: string;
  duration?: number;
  isActive: boolean;
  sortOrder: number;
}

interface Course {
  id: string;
  title: string;
  description: string;
  duration?: number;
  lessons?: number;
  practiceQuestions?: number;
}

export default function CourseContent() {
  const [match, params] = useRoute("/course/:courseId");
  const courseId = params?.courseId;
  const queryClient = useQueryClient();

  // Force cache invalidation on component mount to get fresh data
  useEffect(() => {
    if (courseId) {
      queryClient.invalidateQueries({ queryKey: [`/api/courses/${courseId}/content`] });
    }
  }, [courseId, queryClient]);

  if (!courseId) {
    return <div>Course not found</div>;
  }

  const { data: courses } = useQuery<Course[]>({
    queryKey: ["/api/courses"],
  });

  // Map friendly URL to database UUID for course lookup
  const courseUUID = getCourseUUID(courseId);
  const course = courses?.find(c => c.id === courseUUID);

  const { data: content, isLoading } = useQuery<CourseContent[]>({
    queryKey: [`/api/courses/${courseId}/content`],
    staleTime: 0,
    gcTime: 0,
    refetchOnMount: 'always',
  });

  const { data: sectionProgress } = useQuery<Array<{section: number, isUnlocked: boolean, isAdmin: boolean}>>({
    queryKey: [`/api/section-progress/${courseId}`],
    enabled: !!courseId,
  });

  if (isLoading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-gray-200 rounded w-1/2"></div>
          <div className="h-4 bg-gray-200 rounded w-3/4"></div>
          <div className="space-y-2">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-20 bg-gray-200 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  if (!course) {
    return <div>Course not found</div>;
  }

  if (!content || !Array.isArray(content) || content.length === 0) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center">
          <h1 className="text-2xl font-bold mb-4">Course Content</h1>
          <p>No lessons available yet. Content is being prepared.</p>
        </div>
      </div>
    );
  }

  // Group content by section
  const contentBySection = content.reduce((acc, item) => {
    // Handle null/undefined sections by defaulting to section number
    const sectionKey = item.section?.toString() || '101';
    if (!acc[sectionKey]) acc[sectionKey] = [];
    acc[sectionKey].push(item);
    return acc;
  }, {} as Record<string, CourseContent[]>);

  // Get unique sections sorted and filter out placeholder sections
  const allSections = Object.keys(contentBySection).sort((a, b) => Number(a) - Number(b));

  // Only show the actual Louisiana State Plumbing Code sections (101-109)
  const sections = allSections.filter(section => {
    const sectionNum = Number(section);

    // Only keep sections 101, 103, 105, 107, 109 (the real LSPC sections)
    return sectionNum >= 101 && sectionNum <= 109;
  });

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'lesson': return BookOpen;
      case 'quiz': return Star;
      case 'podcast': return Headphones;
      case 'chat': return MessageSquare;
      case 'flashcards': return Bookmark;
      case 'study-notes': return NotebookPen;
      default: return BookOpen;
    }
  };

  const getTypeLabel = (type: string) => {
    switch (type) {
      case 'lesson': return 'Introduction';
      case 'quiz': return 'Quiz';
      case 'podcast': return 'Podcast';
      case 'chat': return 'Teach Me/Chat';
      case 'flashcards': return 'Flashcards';
      case 'study-notes': return 'Study Notes';
      default: return type;
    }
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'lesson': return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'quiz': return 'bg-green-100 text-green-800 border-green-200';
      case 'podcast': return 'bg-purple-100 text-purple-800 border-purple-200';
      case 'chat': return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'flashcards': return 'bg-pink-100 text-pink-800 border-pink-200';
      case 'study-notes': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      {/* Course Header */}
      <div className="mb-8">
        <div className="flex items-center gap-2 mb-4">
          <Link href="/dashboard" className="text-primary hover:underline">
            Dashboard
          </Link>
          <span>/</span>
          <span className="text-muted-foreground">Course Content</span>
        </div>

        <div className="bg-white rounded-lg shadow-sm border p-6">
          <h1 className="text-3xl font-bold text-foreground mb-4" data-testid="course-title">
            {course.title}
          </h1>
          <p className="text-muted-foreground mb-6">{course.description}</p>

          <div className="flex items-center gap-6 text-sm text-muted-foreground">
            {course.duration && (
              <div className="flex items-center gap-1">
                <Clock className="w-4 h-4" />
                <span>{course.duration} hours</span>
              </div>
            )}
            {course.lessons && (
              <div className="flex items-center gap-1">
                <BookOpen className="w-4 h-4" />
                <span>{course.lessons} lessons</span>
              </div>
            )}
            {course.practiceQuestions && (
              <div className="flex items-center gap-1">
                <Star className="w-4 h-4" />
                <span>{course.practiceQuestions} quizzes</span>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Course Lessons by Section */}
      <div className="space-y-6">
        <h2 className="text-xl font-semibold mb-4" data-testid="lessons-header">
          Course Lessons
        </h2>

        {sections.length === 0 ? (
          <Card>
            <CardContent className="p-8 text-center">
              <BookOpen className="w-12 h-12 mx-auto text-muted-foreground mb-4" />
              <h3 className="text-lg font-medium mb-2">No Lessons Available</h3>
              <p className="text-muted-foreground">
                This course doesn't have any lessons yet. Check back later!
              </p>
            </CardContent>
          </Card>
        ) : (
          sections.map((section) => {
            const sectionItems = contentBySection[section];

            // Create proper section titles based on your actual Louisiana State Plumbing Code sections
            const getSectionInfo = (sectionNum: string) => {
              const num = Number(sectionNum);
              switch (num) {
                case 101:
                  return { title: "Louisiana State Plumbing Code §101", subtitle: "Administration" };
                case 103:
                  return { title: "Louisiana State Plumbing Code §103", subtitle: "Availability" };
                case 105:
                  return { title: "Louisiana State Plumbing Code §105", subtitle: "Effective Date and Edition" };
                case 107:
                  return { title: "Louisiana State Plumbing Code §107", subtitle: "Purpose and Scope" };
                case 109:
                  return { title: "Louisiana State Plumbing Code §109", subtitle: "Permitting and Inspection Limitations" };
                default:
                  return { title: `Louisiana State Plumbing Code §${sectionNum}`, subtitle: sectionItems[0]?.title.split(' - ')[0] || `Section ${sectionNum}` };
              }
            };

            const sectionInfo = getSectionInfo(section);
            const sectionTitle = sectionInfo.title;
            const sectionSubtitle = sectionInfo.subtitle;

            // Calculate lesson progress (placeholder for now)
            const progress = 0;
            const contentCount = sectionItems.length;

            // Check if this section is unlocked
            const sectionNum = Number(section);
            const sectionStatus = sectionProgress?.find(s => s.section === sectionNum);
            const isUnlocked = sectionStatus?.isUnlocked ?? false;
            const isAdmin = sectionStatus?.isAdmin ?? false;

            // Section 101 is always unlocked for all users (starting section)
            // Other sections require completion of previous section
            const isFirstSection = sectionNum === 101;
            const shouldShowAsLocked = !isFirstSection && !isUnlocked && !isAdmin;

            return (
              <Card key={section} className={`hover:shadow-md transition-shadow ${shouldShowAsLocked ? 'opacity-60' : ''}`} data-testid={`lesson-card-${section}`}>
                <CardContent className="p-6">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-2 mb-2">
                        <Badge className="bg-primary/10 text-primary" data-testid={`lesson-section-${section}`}>
                          Section {section}
                        </Badge>
                        <Badge variant="outline" data-testid={`lesson-content-count-${section}`}>
                          {contentCount} parts
                        </Badge>
                        {shouldShowAsLocked && (
                          <Badge variant="secondary" className="bg-red-100 text-red-800">
                            <Lock className="w-3 h-3 mr-1" />
                            Locked
                          </Badge>
                        )}
                      </div>
                      <h3 className="text-lg font-semibold text-foreground mb-1" data-testid={`lesson-title-${section}`}>
                        {sectionTitle}
                      </h3>
                      <p className="text-sm text-muted-foreground mb-3" data-testid={`lesson-description-${section}`}>
                        {sectionSubtitle}
                      </p>

                      {/* Progress bar for enrolled users */}
                      {progress > 0 && (
                        <div className="space-y-1 mb-3">
                          <div className="flex justify-between text-xs">
                            <span>Progress</span>
                            <span>{progress}%</span>
                          </div>
                          <Progress value={progress} className="h-1.5" data-testid={`lesson-progress-${section}`} />
                        </div>
                      )}

                      {/* Content overview */}
                      <div className="flex items-center space-x-3 text-xs text-muted-foreground flex-wrap">
                        <div className="flex items-center space-x-1">
                          <BookOpen className="w-3 h-3" />
                          <span>Introduction</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <Headphones className="w-3 h-3" />
                          <span>Podcast</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <MessageSquare className="w-3 h-3" />
                          <span>Chat</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <Bookmark className="w-3 h-3" />
                          <span>Flashcards</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <NotebookPen className="w-3 h-3" />
                          <span>Study Notes</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <Star className="w-3 h-3" />
                          <span>Quiz</span>
                        </div>
                      </div>

                      {shouldShowAsLocked && (
                        <p className="text-xs text-red-600 mt-2">
                          🔒 Complete the previous section's quiz with {sectionTitle.toLowerCase().includes('chapter review') ? '80%' : '70%'}+ to unlock
                        </p>
                      )}
                    </div>

                    <div className="ml-4">
                      {(isFirstSection || isUnlocked || isAdmin) ? (
                        <Button 
                          asChild
                          data-testid={`button-start-lesson-${section}`}
                          className={shouldShowAsLocked ? "opacity-75" : ""}
                        >
                          <Link href={`/course/${courseId}/lesson/${section}`}>
                            {shouldShowAsLocked ? <Lock className="w-4 h-4 mr-2" /> : <Play className="w-4 h-4 mr-2" />}
                            {shouldShowAsLocked ? "Locked" : (progress > 0 ? "Continue" : "Start")}
                          </Link>
                        </Button>
                      ) : (
                        <Button 
                          disabled
                          variant="secondary"
                          className="opacity-50"
                          data-testid={`button-locked-lesson-${section}`}
                        >
                          <Lock className="w-4 h-4 mr-2" />
                          Locked
                        </Button>
                      )}
                    </div>
                  </div>
                </CardContent>
              </Card>
            );
          })
        )}
      </div>

      {content.length === 0 && (
        <Card>
          <CardContent className="p-8 text-center">
            <BookOpen className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
            <h3 className="text-lg font-medium text-foreground mb-2">No Content Available</h3>
            <p className="text-muted-foreground">
              Course content is being prepared. Check back soon!
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

