import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Plus, Edit, Trash2, Star, Package, Upload, Download, ExternalLink } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

interface ProductForm {
  name: string;
  description: string;
  shortDescription: string;
  category: string;
  price: string;
  originalPrice: string;
  amazonUrl: string;
  imageUrl: string;
  brand: string;
  model: string;
  features: string[];
  isActive: boolean;
  isFeatured: boolean;
  tags: string[];
}

const defaultForm: ProductForm = {
  name: "",
  description: "",
  shortDescription: "",
  category: "",
  price: "",
  originalPrice: "",
  amazonUrl: "",
  imageUrl: "",
  brand: "",
  model: "",
  features: [],
  isActive: true,
  isFeatured: false,
  tags: [],
};

export default function ProductManager() {
  const [activeTab, setActiveTab] = useState("list");
  const [editingProduct, setEditingProduct] = useState<any>(null);
  const [form, setForm] = useState<ProductForm>(defaultForm);
  const [newFeature, setNewFeature] = useState("");
  const [newTag, setNewTag] = useState("");
  const [extractingFromAmazon, setExtractingFromAmazon] = useState(false);
  const [amazonUrl, setAmazonUrl] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const categories = [
    { value: "gas_detection", label: "Gas Detection" },
    { value: "pipe_tools", label: "Pipe Tools" },
    { value: "measuring", label: "Measuring Tools" },
    { value: "safety", label: "Safety Equipment" },
    { value: "valves", label: "Valves" },
    { value: "fittings", label: "Fittings" },
    { value: "books", label: "Books & Manuals" },
    { value: "training_materials", label: "Training Materials" },
  ];

  const { data: products, isLoading } = useQuery({
    queryKey: ["/api/products", { limit: 100 }],
    queryFn: async () => {
      const response = await fetch("/api/products?limit=100");
      if (!response.ok) throw new Error("Failed to fetch products");
      return response.json();
    },
  });

  const createProductMutation = useMutation({
    mutationFn: async (productData: any) => {
      const response = await fetch("/api/products", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(productData),
      });
      if (!response.ok) throw new Error("Failed to create product");
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Success", description: "Product created successfully!" });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      setForm(defaultForm);
      setActiveTab("list");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create product",
        variant: "destructive",
      });
    },
  });

  const updateProductMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: any }) => {
      const response = await fetch(`/api/products/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error("Failed to update product");
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Success", description: "Product updated successfully!" });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
      setEditingProduct(null);
      setForm(defaultForm);
      setActiveTab("list");
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update product",
        variant: "destructive",
      });
    },
  });

  const deleteProductMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await fetch(`/api/products/${id}`, {
        method: "DELETE",
      });
      if (!response.ok) throw new Error("Failed to delete product");
      return response.json();
    },
    onSuccess: () => {
      toast({ title: "Success", description: "Product deleted successfully!" });
      queryClient.invalidateQueries({ queryKey: ["/api/products"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete product",
        variant: "destructive",
      });
    },
  });

  const extractFromAmazonMutation = useMutation({
    mutationFn: async (url: string) => {
      const response = await fetch("/api/amazon/extract", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ url }),
      });
      if (!response.ok) throw new Error("Failed to extract product data");
      return response.json();
    },
    onSuccess: (data) => {
      if (data.success) {
        setForm({
          ...form,
          name: data.name || form.name,
          description: data.description || form.description,
          shortDescription: data.shortDescription || form.shortDescription,
          price: data.price || form.price,
          amazonUrl: data.amazonUrl || form.amazonUrl,
          imageUrl: data.imageUrl || form.imageUrl,
          brand: data.brand || form.brand,
          features: data.features || form.features,
        });
        toast({
          title: "Success",
          description: "Product information extracted from Amazon!",
        });
      }
    },
    onError: (error: any) => {
      toast({
        title: "Extraction Failed",
        description: error.message || "Failed to extract product information",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!form.name || !form.description || !form.category || !form.price || !form.amazonUrl) {
      toast({
        title: "Error",
        description: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    const productData = {
      ...form,
      price: form.price.toString(),
      originalPrice: form.originalPrice ? form.originalPrice.toString() : undefined,
      features: Array.isArray(form.features) ? form.features : [],
      tags: Array.isArray(form.tags) ? form.tags : [],
    };

    if (editingProduct) {
      updateProductMutation.mutate({ id: editingProduct.id, data: productData });
    } else {
      createProductMutation.mutate(productData);
    }
  };

  const handleEdit = (product: any) => {
    setEditingProduct(product);
    setForm({
      name: product.name,
      description: product.description,
      shortDescription: product.shortDescription || "",
      category: product.category,
      price: product.price.toString(),
      originalPrice: product.originalPrice?.toString() || "",
      amazonUrl: product.amazonUrl,
      imageUrl: product.imageUrl || "",
      brand: product.brand || "",
      model: product.model || "",
      features: product.features || [],
      isActive: product.isActive,
      isFeatured: product.isFeatured,
      tags: product.tags || [],
    });
    setActiveTab("form");
  };

  const handleAddFeature = () => {
    if (newFeature.trim()) {
      setForm({ ...form, features: [...form.features, newFeature.trim()] });
      setNewFeature("");
    }
  };

  const handleRemoveFeature = (index: number) => {
    setForm({ ...form, features: form.features.filter((_, i) => i !== index) });
  };

  const handleAddTag = () => {
    if (newTag.trim()) {
      setForm({ ...form, tags: [...form.tags, newTag.trim()] });
      setNewTag("");
    }
  };

  const handleRemoveTag = (index: number) => {
    setForm({ ...form, tags: form.tags.filter((_, i) => i !== index) });
  };

  const handleExtractFromAmazon = () => {
    if (!amazonUrl.trim()) {
      toast({
        title: "Error",
        description: "Please enter an Amazon URL",
        variant: "destructive",
      });
      return;
    }

    if (!amazonUrl.includes('amazon.com') && !amazonUrl.includes('amzn.to')) {
      toast({
        title: "Error",
        description: "Please enter a valid Amazon.com URL",
        variant: "destructive",
      });
      return;
    }

    extractFromAmazonMutation.mutate(amazonUrl);
  };

  const formatPrice = (price: string | number) => {
    return `$${parseFloat(price.toString()).toFixed(2)}`;
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-4" data-testid="product-manager-title">
          Product Manager
        </h1>
        <p className="text-gray-600 dark:text-gray-300" data-testid="product-manager-description">
          Manage your store products, add new items, and update existing inventory
        </p>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="list" data-testid="tab-list">
            <Package className="w-4 h-4 mr-2" />
            Products List
          </TabsTrigger>
          <TabsTrigger value="form" data-testid="tab-form">
            <Plus className="w-4 h-4 mr-2" />
            {editingProduct ? "Edit Product" : "Add Product"}
          </TabsTrigger>
        </TabsList>

        <TabsContent value="list" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <span>All Products ({products?.products?.length || 0})</span>
                <Button onClick={() => { setForm(defaultForm); setEditingProduct(null); setActiveTab("form"); }} data-testid="add-product-button">
                  <Plus className="w-4 h-4 mr-2" />
                  Add Product
                </Button>
              </CardTitle>
            </CardHeader>
            <CardContent>
              {isLoading ? (
                <div className="space-y-4">
                  {Array.from({ length: 3 }, (_, i) => (
                    <div key={i} className="animate-pulse border rounded-lg p-4">
                      <div className="flex gap-4">
                        <div className="w-16 h-16 bg-gray-200 dark:bg-gray-700 rounded"></div>
                        <div className="flex-1 space-y-2">
                          <div className="h-4 bg-gray-200 dark:bg-gray-700 rounded w-3/4"></div>
                          <div className="h-3 bg-gray-200 dark:bg-gray-700 rounded w-1/2"></div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="space-y-4" data-testid="products-list">
                  {products?.products?.map((product: any) => (
                    <div key={product.id} className="border rounded-lg p-4 hover:shadow-md transition-shadow" data-testid={`product-item-${product.id}`}>
                      <div className="flex gap-4">
                        <img
                          src={product.imageUrl || "/api/placeholder/64/64"}
                          alt={product.name}
                          className="w-16 h-16 object-cover rounded"
                          data-testid="product-image"
                        />
                        <div className="flex-1">
                          <div className="flex items-start justify-between">
                            <div>
                              <h3 className="font-semibold text-lg mb-1" data-testid="product-name">
                                {product.name}
                              </h3>
                              <p className="text-gray-600 dark:text-gray-300 text-sm mb-2" data-testid="product-description">
                                {product.shortDescription || product.description.substring(0, 100)}...
                              </p>
                              <div className="flex items-center gap-4 mb-2">
                                <span className="font-bold text-blue-600" data-testid="product-price">
                                  {formatPrice(product.price)}
                                </span>
                                <Badge variant="outline" data-testid="product-category">
                                  {product.category.replace(/_/g, " ").replace(/\b\w/g, (l: string) => l.toUpperCase())}
                                </Badge>
                                {product.isFeatured && (
                                  <Badge className="bg-blue-600" data-testid="featured-badge">
                                    <Star className="w-3 h-3 mr-1" />
                                    Featured
                                  </Badge>
                                )}
                                {!product.isActive && (
                                  <Badge variant="destructive" data-testid="inactive-badge">
                                    Inactive
                                  </Badge>
                                )}
                              </div>
                              <div className="flex items-center gap-2 text-sm text-gray-500">
                                <span>★ {product.rating || "0.0"}</span>
                                <span>•</span>
                                <span>{product.reviewCount || 0} reviews</span>
                                <span>•</span>
                                <span>{product.brand || "No brand"}</span>
                              </div>
                            </div>
                            <div className="flex items-center gap-2">
                              <Button size="sm" variant="outline" onClick={() => handleEdit(product)} data-testid="edit-product-button">
                                <Edit className="w-4 h-4" />
                              </Button>
                              <Button
                                size="sm"
                                variant="outline"
                                onClick={() => deleteProductMutation.mutate(product.id)}
                                disabled={deleteProductMutation.isPending}
                                data-testid="delete-product-button"
                              >
                                <Trash2 className="w-4 h-4" />
                              </Button>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="form" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle data-testid="form-title">
                {editingProduct ? `Edit Product: ${editingProduct.name}` : "Add New Product"}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-6" data-testid="product-form">
                {/* Amazon URL Extractor */}
                <div className="bg-blue-50 dark:bg-blue-900/20 p-6 rounded-lg border border-blue-200 dark:border-blue-800">
                  <div className="space-y-4">
                    <div className="flex items-center gap-2 mb-3">
                      <ExternalLink className="w-5 h-5 text-blue-600" />
                      <h3 className="font-medium text-blue-900 dark:text-blue-100">
                        Quick Product Import from Amazon
                      </h3>
                    </div>
                    <p className="text-sm text-blue-700 dark:text-blue-300 mb-4">
                      Paste an Amazon product URL and we'll automatically extract the product name, price, description, features, and images for you!
                    </p>
                    <div className="flex gap-2">
                      <Input
                        placeholder="https://www.amazon.com/product-link..."
                        value={amazonUrl}
                        onChange={(e) => setAmazonUrl(e.target.value)}
                        className="flex-1"
                        data-testid="input-amazon-url"
                      />
                      <Button
                        type="button"
                        onClick={handleExtractFromAmazon}
                        disabled={extractFromAmazonMutation.isPending}
                        className="bg-blue-600 hover:bg-blue-700"
                        data-testid="extract-amazon-button"
                      >
                        {extractFromAmazonMutation.isPending ? (
                          <>
                            <Download className="w-4 h-4 mr-2 animate-spin" />
                            Extracting...
                          </>
                        ) : (
                          <>
                            <Download className="w-4 h-4 mr-2" />
                            Extract
                          </>
                        )}
                      </Button>
                    </div>
                  </div>
                </div>

                {/* Basic Information */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="space-y-2">
                    <Label htmlFor="name">Product Name *</Label>
                    <Input
                      id="name"
                      value={form.name}
                      onChange={(e) => setForm({ ...form, name: e.target.value })}
                      placeholder="Enter product name"
                      required
                      data-testid="input-name"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="category">Category *</Label>
                    <Select value={form.category} onValueChange={(value) => setForm({ ...form, category: value })}>
                      <SelectTrigger data-testid="select-category">
                        <SelectValue placeholder="Select category" />
                      </SelectTrigger>
                      <SelectContent>
                        {categories.map((cat) => (
                          <SelectItem key={cat.value} value={cat.value}>
                            {cat.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="shortDescription">Short Description</Label>
                  <Input
                    id="shortDescription"
                    value={form.shortDescription}
                    onChange={(e) => setForm({ ...form, shortDescription: e.target.value })}
                    placeholder="Brief product description for listings"
                    data-testid="input-short-description"
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="description">Full Description *</Label>
                  <Textarea
                    id="description"
                    value={form.description}
                    onChange={(e) => setForm({ ...form, description: e.target.value })}
                    placeholder="Detailed product description"
                    rows={4}
                    required
                    data-testid="textarea-description"
                  />
                </div>

                {/* Pricing */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div className="space-y-2">
                    <Label htmlFor="price">Price *</Label>
                    <Input
                      id="price"
                      type="number"
                      step="0.01"
                      value={form.price}
                      onChange={(e) => setForm({ ...form, price: e.target.value })}
                      placeholder="0.00"
                      required
                      data-testid="input-price"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="originalPrice">Original Price (Optional)</Label>
                    <Input
                      id="originalPrice"
                      type="number"
                      step="0.01"
                      value={form.originalPrice}
                      onChange={(e) => setForm({ ...form, originalPrice: e.target.value })}
                      placeholder="0.00"
                      data-testid="input-original-price"
                    />
                  </div>
                </div>

                {/* Product Details */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                  <div className="space-y-2">
                    <Label htmlFor="brand">Brand</Label>
                    <Input
                      id="brand"
                      value={form.brand}
                      onChange={(e) => setForm({ ...form, brand: e.target.value })}
                      placeholder="Product brand"
                      data-testid="input-brand"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="model">Model</Label>
                    <Input
                      id="model"
                      value={form.model}
                      onChange={(e) => setForm({ ...form, model: e.target.value })}
                      placeholder="Product model"
                      data-testid="input-model"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="imageUrl">Image URL</Label>
                    <Input
                      id="imageUrl"
                      value={form.imageUrl}
                      onChange={(e) => setForm({ ...form, imageUrl: e.target.value })}
                      placeholder="https://..."
                      data-testid="input-image-url"
                    />
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="amazonUrl">Amazon URL *</Label>
                  <Input
                    id="amazonUrl"
                    value={form.amazonUrl}
                    onChange={(e) => setForm({ ...form, amazonUrl: e.target.value })}
                    placeholder="https://amzn.to/..."
                    required
                    data-testid="input-amazon-url"
                  />
                </div>

                {/* Features */}
                <div className="space-y-4">
                  <Label>Product Features</Label>
                  <div className="flex gap-2">
                    <Input
                      value={newFeature}
                      onChange={(e) => setNewFeature(e.target.value)}
                      placeholder="Add a feature"
                      onKeyPress={(e) => e.key === "Enter" && (e.preventDefault(), handleAddFeature())}
                      data-testid="input-new-feature"
                    />
                    <Button type="button" onClick={handleAddFeature} data-testid="add-feature-button">
                      Add
                    </Button>
                  </div>
                  <div className="flex flex-wrap gap-2" data-testid="features-list">
                    {form.features.map((feature, index) => (
                      <Badge key={index} variant="secondary" className="flex items-center gap-1">
                        {feature}
                        <button
                          type="button"
                          onClick={() => handleRemoveFeature(index)}
                          className="ml-1 hover:text-red-500"
                          data-testid={`remove-feature-${index}`}
                        >
                          ×
                        </button>
                      </Badge>
                    ))}
                  </div>
                </div>

                {/* Tags */}
                <div className="space-y-4">
                  <Label>Tags</Label>
                  <div className="flex gap-2">
                    <Input
                      value={newTag}
                      onChange={(e) => setNewTag(e.target.value)}
                      placeholder="Add a tag"
                      onKeyPress={(e) => e.key === "Enter" && (e.preventDefault(), handleAddTag())}
                      data-testid="input-new-tag"
                    />
                    <Button type="button" onClick={handleAddTag} data-testid="add-tag-button">
                      Add
                    </Button>
                  </div>
                  <div className="flex flex-wrap gap-2" data-testid="tags-list">
                    {form.tags.map((tag, index) => (
                      <Badge key={index} variant="outline" className="flex items-center gap-1">
                        {tag}
                        <button
                          type="button"
                          onClick={() => handleRemoveTag(index)}
                          className="ml-1 hover:text-red-500"
                          data-testid={`remove-tag-${index}`}
                        >
                          ×
                        </button>
                      </Badge>
                    ))}
                  </div>
                </div>

                {/* Settings */}
                <Separator />
                <div className="flex items-center gap-6">
                  <div className="flex items-center gap-2">
                    <Switch
                      checked={form.isActive}
                      onCheckedChange={(checked) => setForm({ ...form, isActive: checked })}
                      data-testid="switch-active"
                    />
                    <Label>Active</Label>
                  </div>
                  <div className="flex items-center gap-2">
                    <Switch
                      checked={form.isFeatured}
                      onCheckedChange={(checked) => setForm({ ...form, isFeatured: checked })}
                      data-testid="switch-featured"
                    />
                    <Label>Featured</Label>
                  </div>
                </div>

                {/* Submit */}
                <div className="flex items-center gap-4 pt-4">
                  <Button
                    type="submit"
                    disabled={createProductMutation.isPending || updateProductMutation.isPending}
                    data-testid="submit-button"
                  >
                    <Upload className="w-4 h-4 mr-2" />
                    {editingProduct
                      ? updateProductMutation.isPending
                        ? "Updating..."
                        : "Update Product"
                      : createProductMutation.isPending
                      ? "Creating..."
                      : "Create Product"}
                  </Button>
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => {
                      setForm(defaultForm);
                      setEditingProduct(null);
                      setActiveTab("list");
                    }}
                    data-testid="cancel-button"
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/admin/job-approval.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  CheckCircle, 
  XCircle, 
  Clock, 
  Building2, 
  MapPin, 
  DollarSign,
  Mail,
  Calendar,
  Eye,
  AlertTriangle
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { Job } from "@/../../shared/schema";

export default function JobApproval() {
  const [selectedJobId, setSelectedJobId] = useState<string>("");
  const [rejectionReason, setRejectionReason] = useState("");
  const [showRejectModal, setShowRejectModal] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: pendingJobs = [], isLoading } = useQuery<Job[]>({
    queryKey: ["/api/admin/jobs/pending"],
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  const approveJobMutation = useMutation({
    mutationFn: (jobId: string) => 
      apiRequest("PUT", `/api/admin/jobs/${jobId}/approve`, {}),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/jobs/pending"] });
      toast({
        title: "Job Approved",
        description: "The job posting has been approved and is now live.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Approval Failed",
        description: error.message || "Failed to approve job",
        variant: "destructive",
      });
    },
  });

  const rejectJobMutation = useMutation({
    mutationFn: ({ jobId, reason }: { jobId: string; reason: string }) => 
      apiRequest("PUT", `/api/admin/jobs/${jobId}/reject`, { rejectionReason: reason }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/jobs/pending"] });
      setShowRejectModal(false);
      setRejectionReason("");
      setSelectedJobId("");
      toast({
        title: "Job Rejected",
        description: "The job posting has been rejected and the employer has been notified.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Rejection Failed",
        description: error.message || "Failed to reject job",
        variant: "destructive",
      });
    },
  });

  const handleApprove = (jobId: string) => {
    approveJobMutation.mutate(jobId);
  };

  const handleReject = (jobId: string) => {
    setSelectedJobId(jobId);
    setShowRejectModal(true);
  };

  const submitRejection = () => {
    if (!rejectionReason.trim()) {
      toast({
        title: "Rejection Reason Required",
        description: "Please provide a reason for rejecting this job posting.",
        variant: "destructive",
      });
      return;
    }
    rejectJobMutation.mutate({ jobId: selectedJobId, reason: rejectionReason });
  };

  const formatSalary = (min?: string, max?: string) => {
    if (!min && !max) return "Salary not specified";
    if (min && max) return `$${min} - $${max}/hour`;
    if (min) return `From $${min}/hour`;
    if (max) return `Up to $${max}/hour`;
  };

  const formatJobType = (type: string) => {
    return type.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background p-8">
        <div className="max-w-6xl mx-auto">
          <div className="flex items-center justify-center py-16">
            <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="bg-white border-b">
        <div className="max-w-6xl mx-auto px-4 py-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold" data-testid="admin-title">
                Job Approval Dashboard
              </h1>
              <p className="text-muted-foreground mt-1">
                Review and approve job postings from employers
              </p>
            </div>
            <div className="flex items-center space-x-4">
              <Badge variant="secondary" className="text-sm">
                <Clock className="w-4 h-4 mr-2" />
                {pendingJobs.length} Pending
              </Badge>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-6xl mx-auto px-4 py-8">
        {pendingJobs.length === 0 ? (
          <Card>
            <CardContent className="py-16 text-center">
              <CheckCircle className="w-16 h-16 text-green-500 mx-auto mb-4" />
              <h3 className="text-xl font-semibold mb-2">All Caught Up!</h3>
              <p className="text-muted-foreground">
                There are no pending job postings to review at this time.
              </p>
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-6">
            {pendingJobs.map((job) => (
              <Card key={job.id} className="relative" data-testid={`job-card-${job.id}`}>
                <CardHeader>
                  <div className="flex items-start justify-between">
                    <div className="space-y-2">
                      <div className="flex items-center space-x-2">
                        <CardTitle className="text-xl">{job.title}</CardTitle>
                        <Badge variant="outline" className="text-xs">
                          {formatJobType(job.type)}
                        </Badge>
                      </div>
                      <div className="flex items-center space-x-4 text-sm text-muted-foreground">
                        <div className="flex items-center space-x-1">
                          <Building2 className="w-4 h-4" />
                          <span>{job.company}</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <MapPin className="w-4 h-4" />
                          <span>{job.location}</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <DollarSign className="w-4 h-4" />
                          <span>{formatSalary(job.salaryMin, job.salaryMax)}</span>
                        </div>
                      </div>
                    </div>
                    <Badge variant="secondary">
                      <Clock className="w-3 h-3 mr-1" />
                      Pending Review
                    </Badge>
                  </div>
                </CardHeader>

                <CardContent className="space-y-6">
                  <Tabs defaultValue="details" className="w-full">
                    <TabsList className="grid w-full grid-cols-3">
                      <TabsTrigger value="details">Job Details</TabsTrigger>
                      <TabsTrigger value="requirements">Requirements</TabsTrigger>
                      <TabsTrigger value="employer">Employer Info</TabsTrigger>
                    </TabsList>

                    <TabsContent value="details" className="space-y-4 mt-4">
                      <div>
                        <h4 className="font-medium mb-2">Job Description</h4>
                        <p className="text-sm text-muted-foreground whitespace-pre-line">
                          {job.description}
                        </p>
                      </div>
                      {job.benefits && (
                        <div>
                          <h4 className="font-medium mb-2">Benefits</h4>
                          <ul className="text-sm text-muted-foreground space-y-1">
                            {(job.benefits || []).map((benefit, index) => (
                              <li key={index}>• {benefit}</li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </TabsContent>

                    <TabsContent value="requirements" className="space-y-4 mt-4">
                      <div>
                        <h4 className="font-medium mb-2">Requirements</h4>
                        <ul className="text-sm text-muted-foreground space-y-1">
                          {(job.requirements || []).map((req, index) => (
                            <li key={index}>• {req}</li>
                          ))}
                        </ul>
                      </div>
                    </TabsContent>

                    <TabsContent value="employer" className="space-y-4 mt-4">
                      <div className="grid md:grid-cols-2 gap-4">
                        <div>
                          <h4 className="font-medium mb-2">Contact Information</h4>
                          <div className="space-y-1 text-sm text-muted-foreground">
                            <div className="flex items-center space-x-2">
                              <Mail className="w-4 h-4" />
                              <span>{job.contactEmail}</span>
                            </div>
                          </div>
                        </div>
                        <div>
                          <h4 className="font-medium mb-2">Posting Date</h4>
                          <div className="flex items-center space-x-2 text-sm text-muted-foreground">
                            <Calendar className="w-4 h-4" />
                            <span>{job.createdAt ? new Date(job.createdAt).toLocaleDateString() : 'Unknown'}</span>
                          </div>
                        </div>
                      </div>
                    </TabsContent>
                  </Tabs>

                  {/* Action Buttons */}
                  <div className="flex items-center justify-end space-x-3 pt-4 border-t">
                    <Button
                      variant="outline"
                      onClick={() => handleReject(job.id)}
                      disabled={rejectJobMutation.isPending}
                      data-testid={`button-reject-${job.id}`}
                    >
                      <XCircle className="w-4 h-4 mr-2" />
                      Reject
                    </Button>
                    <Button
                      onClick={() => handleApprove(job.id)}
                      disabled={approveJobMutation.isPending}
                      data-testid={`button-approve-${job.id}`}
                    >
                      <CheckCircle className="w-4 h-4 mr-2" />
                      {approveJobMutation.isPending ? "Approving..." : "Approve & Publish"}
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Reject Modal */}
        {showRejectModal && (
          <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50" data-testid="reject-modal">
            <Card className="w-full max-w-md mx-4">
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <AlertTriangle className="w-5 h-5 text-orange-500" />
                  <span>Reject Job Posting</span>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <p className="text-sm text-muted-foreground">
                  Please provide a reason for rejecting this job posting. This will be sent to the employer.
                </p>
                <Textarea
                  value={rejectionReason}
                  onChange={(e) => setRejectionReason(e.target.value)}
                  placeholder="Reason for rejection..."
                  rows={4}
                  data-testid="input-rejection-reason"
                />
                <div className="flex space-x-3">
                  <Button
                    variant="outline"
                    onClick={() => {
                      setShowRejectModal(false);
                      setRejectionReason("");
                      setSelectedJobId("");
                    }}
                    className="flex-1"
                    data-testid="button-cancel-reject"
                  >
                    Cancel
                  </Button>
                  <Button
                    onClick={submitRejection}
                    disabled={rejectJobMutation.isPending || !rejectionReason.trim()}
                    className="flex-1"
                    data-testid="button-confirm-reject"
                  >
                    {rejectJobMutation.isPending ? "Rejecting..." : "Reject Job"}
                  </Button>
                </div>
              </CardContent>
            </Card>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/employer-portal.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Building2, MapPin, DollarSign, Clock, CheckCircle, Mail, Phone, BarChart3, Edit, Star, RefreshCw, Settings } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

const employerSchema = z.object({
  companyName: z.string().min(2, "Company name is required"),
  contactName: z.string().min(2, "Contact name is required"),
  contactEmail: z.string().email("Valid email is required"),
  contactPhone: z.string().optional(),
  companyWebsite: z.string().optional().refine((val) => {
    if (!val || val === "") return true;
    const urlPattern = /^(https?:\/\/)?(www\.)?[a-zA-Z0-9-]+(\.[a-zA-Z]{2,})+(\S*)?$/;
    return urlPattern.test(val);
  }, "Please enter a valid website URL"),
  companyDescription: z.string().min(10, "Please provide a brief company description"),
  address: z.string().min(5, "Address is required"),
  city: z.string().min(2, "City is required"),
  zipCode: z.string().min(5, "Valid ZIP code is required"),
});

const jobSchema = z.object({
  title: z.string().min(3, "Job title is required"),
  description: z.string().min(50, "Please provide a detailed job description (min 50 characters)"),
  location: z.string().min(3, "Location is required"),
  type: z.enum(["full_time", "part_time", "contract", "temporary"]),
  salaryMin: z.string().optional(),
  salaryMax: z.string().optional(),
  requirements: z.string().min(10, "Please list job requirements"),
  benefits: z.string().optional(),
});

type EmployerData = z.infer<typeof employerSchema>;
type JobData = z.infer<typeof jobSchema>;

export default function EmployerPortal() {
  const [step, setStep] = useState<'pricing' | 'employer' | 'job' | 'payment' | 'success'>('pricing');
  const [employerId, setEmployerId] = useState<string>("");
  const [selectedPlan, setSelectedPlan] = useState<'basic' | 'premium'>('basic');
  const [quantity, setQuantity] = useState<number>(1);
  const [clientSecret, setClientSecret] = useState<string>("");
  const [paymentData, setPaymentData] = useState<any>(null);
  const { toast } = useToast();

  // Bulk pricing logic with progressive discounts
  const calculatePricing = (basePrice: number, qty: number) => {
    let discount = 0;
    if (qty >= 10) discount = 0.25; // 25% off for 10+
    else if (qty >= 5) discount = 0.15; // 15% off for 5+
    else if (qty >= 3) discount = 0.10; // 10% off for 3+

    const unitPrice = basePrice * (1 - discount);
    const totalPrice = unitPrice * qty;
    const totalSavings = (basePrice * qty) - totalPrice;

    return { unitPrice, totalPrice, totalSavings, discount };
  };

  const basicPrice = 49;
  const premiumPrice = 89;

  const basicPricing = calculatePricing(basicPrice, quantity);
  const premiumPricing = calculatePricing(premiumPrice, quantity);

  const employerForm = useForm<EmployerData>({
    resolver: zodResolver(employerSchema),
    defaultValues: {
      state: "Louisiana",
    },
  });

  const jobForm = useForm<JobData>({
    resolver: zodResolver(jobSchema),
  });

  const registerEmployerMutation = useMutation({
    mutationFn: (data: EmployerData) => 
      apiRequest("POST", "/api/employers/register", data),
    onSuccess: (response) => {
      setEmployerId(response.employerId);
      setStep('job');
      toast({
        title: "Registration Successful",
        description: "Your company has been registered. Now let's create your job posting.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Registration Failed",
        description: error.message || "Failed to register company",
        variant: "destructive",
      });
    },
  });

  const createPaymentIntentMutation = useMutation({
    mutationFn: ({ quantity, planType }: { quantity: number; planType: string }) => 
      apiRequest("POST", "/api/employers/payment-intent", { quantity, planType }),
    onSuccess: (response: any) => {
      setClientSecret(response.clientSecret);
      setPaymentData(response);
      setStep('payment');
      toast({
        title: "Payment Ready",
        description: "Please complete your payment to post your job(s).",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Payment Error",
        description: error.message || "Failed to create payment",
        variant: "destructive",
      });
    },
  });

  const createJobMutation = useMutation({
    mutationFn: (data: JobData & { 
      requirements: string[]; 
      benefits?: string[]; 
      planType: 'basic' | 'premium';
      quantity: number;
      unitPrice: number;
      totalPrice: number;
      discount: number;
    }) => 
      apiRequest("POST", `/api/employers/${employerId}/jobs`, data),
    onSuccess: (response: any) => {
      setStep('success');

      // Show different messages based on AI approval
      if (response.status === 'approved') {
        toast({
          title: "Job Posted Successfully! 🎉",
          description: "Your job posting was automatically approved and is now live!",
        });
      } else {
        toast({
