import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  BookOpen, 
  Play, 
  Pause,
  Square,
  Volume2,
  VolumeX, 
  MessageSquare, 
  CheckCircle, 
  ExternalLink,
  RefreshCw,
  Loader2,
  Settings,
  Clock,
  Timer,
  RotateCcw
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useStudySession } from "@/hooks/use-study-session";
import { useToast } from "@/hooks/use-toast";
import PodcastPlayer from "@/components/podcast-player";

interface ContentViewerProps {
  contentId: string;
  contentType: string;
  title: string;
  courseId: string;
  sectionId?: string;
  onComplete?: () => void;
}

interface ExtractedContent {
  id: string;
  title: string;
  type: string;
  content: {
    extracted?: {
      html?: string;
      text?: string;
      content?: string;
      questions?: any[];
      cards?: any[];
      keyPoints?: string[];
      audioUrl?: string;
      transcript?: string;
      chatContent?: string;
      passingScore?: number;
    };
    chatContent?: string;
    extractedAt?: string;
  };
  quizgeckoUrl?: string;
}

export default function ContentViewer({ contentId, contentType, title, courseId, sectionId, onComplete }: ContentViewerProps) {
  const [audioPlaying, setAudioPlaying] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [speechSynthesis, setSpeechSynthesis] = useState<SpeechSynthesis | null>(null);
  const [currentUtterance, setCurrentUtterance] = useState<SpeechSynthesisUtterance | null>(null);
  const [currentSentence, setCurrentSentence] = useState('');
  const [, setLocation] = useLocation();
  const queryClient = useQueryClient();

  // Initialize study session tracking
  const studySession = useStudySession({
    contentId,
    contentType: contentType as 'lesson' | 'quiz' | 'flashcards' | 'chat' | 'podcast' | 'notes' | 'tools',
    autoStart: true
  });

  // Chat state
  const [chatMessages, setChatMessages] = useState<Array<{role: 'user' | 'assistant', content: string, id?: string}>>([]);
  const [chatInputMessage, setChatInputMessage] = useState('');
  const [isChatLoading, setIsChatLoading] = useState(false);
  const [autoPlayEnabled, setAutoPlayEnabled] = useState(true);
  const [currentPlayingId, setCurrentPlayingId] = useState<string | null>(null);
  const [selectedVoice, setSelectedVoice] = useState<string>('auto');
  const [availableVoices, setAvailableVoices] = useState<SpeechSynthesisVoice[]>([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [userAnswers, setUserAnswers] = useState<Record<number, string>>({});
  const [selectedQuizAnswer, setSelectedQuizAnswer] = useState<string | null>(null);
  const [showQuizResults, setShowQuizResults] = useState(false);
  const [quizScore, setQuizScore] = useState(0);
  const [incorrectQuizAnswers, setIncorrectQuizAnswers] = useState<any[]>([]);
  const [isSubmittingQuiz, setIsSubmittingQuiz] = useState(false);
  const [quizAttempt, setQuizAttempt] = useState<any>(null);

  const { data: content, isLoading, error } = useQuery<ExtractedContent>({
    queryKey: [`/api/content/${contentId}/display`],
  });

  const { toast } = useToast();

  useEffect(() => {
    if (typeof window !== 'undefined' && 'speechSynthesis' in window) {
      setSpeechSynthesis(window.speechSynthesis);

      // Load available voices
      const loadVoices = () => {
        const voices = window.speechSynthesis.getVoices();
        const englishVoices = voices.filter(voice => 
          voice.lang.startsWith('en') && !voice.name.includes('eSpeak')
        );
        setAvailableVoices(englishVoices);

        // Load saved voice preference
        const savedVoice = localStorage.getItem('ai-mentor-voice');
        if (savedVoice) {
          setSelectedVoice(savedVoice);
        }
      };

      loadVoices();
      // Voices might not be loaded immediately, so listen for the voiceschanged event
      window.speechSynthesis.addEventListener('voiceschanged', loadVoices);

      // Load voices if they're not ready yet
      if (window.speechSynthesis.getVoices().length === 0) {
        window.speechSynthesis.addEventListener('voiceschanged', () => {
          // Voices are now loaded
        });
      }
    }
  }, []);

  // Parse text-based quiz questions into structured format
  const parseQuestionsFromText = (text: string): any[] => {
    const questions: any[] = [];

    // Clean up text by removing escaped newlines and extra formatting
    const cleanText = text
      .replace(/\\n/g, ' ')
      .replace(/\\t/g, ' ')
      .replace(/\n+/g, '\n')
      .replace(/\s+/g, ' ')
      .trim();

    // Split by **number. pattern to get question blocks
    const questionBlocks = cleanText.split(/\*\*\d+\./).filter(block => block.trim().length > 0);

    questionBlocks.forEach((block, index) => {
      // Clean the block text
      let cleanBlock = block.replace(/\*\*/g, '').trim();

      // Extract question text (until first option A.)
      const questionMatch = cleanBlock.match(/^([^A]*?)(?=[A-D]\.)/s);
      if (!questionMatch) return;

      const questionText = questionMatch[1].trim();
      if (questionText.length < 10) return;

      // Extract individual options using more precise pattern
      const optionPattern = /([A-D])\.\s*([^A-D]*?)(?=\s*[A-D]\.|$)/gs;
      const optionMatches = [...cleanBlock.matchAll(optionPattern)];

      if (optionMatches.length < 2) return;

      const options: any[] = [];
      const seenTexts = new Set(); // Track duplicate options

      optionMatches.forEach((match, optIndex) => {
        const [, letter, optionText] = match;
        let cleanOptionText = optionText.trim();

        // Remove checkmarks and other symbols
        const isCorrect = cleanOptionText.includes('✓');
        cleanOptionText = cleanOptionText
          .replace(/✓/g, '')
          .replace(/\s+/g, ' ')
          .trim();

        // Only add if text is meaningful and not duplicate
        if (cleanOptionText.length > 3 && !seenTexts.has(cleanOptionText)) {
          seenTexts.add(cleanOptionText);
          options.push({
            id: `${index}-${letter}`,
            text: cleanOptionText,
            isCorrect: isCorrect
          });
        }
      });

      // Only add question if we have valid options and at least one correct answer
      if (options.length >= 2 && options.some(opt => opt.isCorrect)) {
        questions.push({
          id: `q-${index}`,
          question: questionText,
          options: options,
          type: 'multiple-choice',
          explanation: `Reference: Louisiana State Plumbing Code`,
          reference: 'Louisiana State Plumbing Code'
        });
      }
    });

    return questions.slice(0, 20);
  };

