  const playAudio = (transcript: string) => {
    if (!speechSynthesis || !transcript) return;

    if (isPaused && currentUtterance) {
      speechSynthesis.resume();
      setIsPaused(false);
      setIsPlaying(true);
      return;
    }

    // Stop any existing speech
    speechSynthesis.cancel();
    setCurrentSentence('');

    // Split transcript into sentences - improved parsing
    console.log('Full transcript length:', transcript.length);
    console.log('Transcript preview:', transcript.substring(0, 200));

    // More comprehensive sentence splitting
    const sentences = transcript
      .split(/[.!?]+(?:\s|$)/) // Split on punctuation followed by space or end
      .map(s => s.trim())
      .filter(s => s.length > 10) // Filter out very short fragments
      .map(s => s.replace(/^\W+/, '')) // Remove leading punctuation
      .filter(s => s.length > 0);

    console.log('Total sentences found:', sentences.length);
    console.log('First few sentences:', sentences.slice(0, 3));
    let currentSentenceIndex = 0;

    const speakNextSentence = () => {
      if (currentSentenceIndex >= sentences.length) {
        setIsPlaying(false);
        setIsPaused(false);
        setCurrentUtterance(null);
        setCurrentSentence('');
        return;
      }

      const sentence = sentences[currentSentenceIndex].trim();
      if (!sentence) {
        currentSentenceIndex++;
        speakNextSentence();
        return;
      }

      // Display current sentence
      setCurrentSentence(sentence);

      // Keep playing state true throughout the entire sequence
      setIsPlaying(true);

      const utterance = new SpeechSynthesisUtterance(sentence);

      // Get available voices and select the best one
      const voices = speechSynthesis.getVoices();
      let selectedVoice = null;

      // Prefer high-quality voices in this order
      const preferredVoices = [
        'Microsoft Zira - English (United States)',
        'Microsoft David - English (United States)', 
        'Google US English Female',
        'Google US English Male',
        'Samantha',
        'Alex',
        'Victoria',
        'Daniel'
      ];

      for (const voiceName of preferredVoices) {
        selectedVoice = voices.find(voice => voice.name.includes(voiceName) || voice.name === voiceName);
        if (selectedVoice) break;
      }

      // Fallback to first English voice if no preferred voice found
      if (!selectedVoice) {
        selectedVoice = voices.find(voice => voice.lang.includes('en')) || voices[0];
      }

      if (selectedVoice) {
        utterance.voice = selectedVoice;
      }

      // Optimize speech settings for educational content
      utterance.rate = 0.85; // Slightly slower for better comprehension
      utterance.pitch = 1.1; // Slightly higher pitch for engagement
      utterance.volume = 1;

      utterance.onend = () => {
        console.log(`Finished sentence ${currentSentenceIndex + 1}/${sentences.length}: "${sentence.substring(0, 50)}..."`);
        currentSentenceIndex++;

        if (currentSentenceIndex < sentences.length) {
          console.log(`Moving to sentence ${currentSentenceIndex + 1}`);
          // Clear current sentence and move to next
          setTimeout(() => {
            setCurrentSentence('');
            setTimeout(() => speakNextSentence(), 300);
          }, 500);
        } else {
          console.log('Reached end of all sentences - podcast complete!');
          setTimeout(() => {
            setCurrentSentence('');
            setIsPlaying(false);
            setIsPaused(false);
            setCurrentUtterance(null);
          }, 500);
        }
      };

      utterance.onerror = () => {
        setIsPlaying(false);
        setIsPaused(false);
        setCurrentUtterance(null);
        setCurrentSentence('');
      };

      setCurrentUtterance(utterance);
      speechSynthesis.speak(utterance);
    };

    setIsPlaying(true);
    setIsPaused(false);
    speakNextSentence();
  };

