import { useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Plus, BookOpen, HelpCircle, FileText, Video, Headphones, Upload, Mic, Clock, Brain } from "lucide-react";
import { Link } from "wouter";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

export default function ContentManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedCourse, setSelectedCourse] = useState("");

  // Fetch courses
  const { data: courses } = useQuery({
    queryKey: ["/api/courses"],
  });

  // Fetch course content based on selected course
  const { data: courseContent } = useQuery({
    queryKey: ["/api/admin/course-content", selectedCourse],
    enabled: !!selectedCourse,
  });

  // Fetch course content stats for overview
  const { data: contentStats } = useQuery({
    queryKey: ["/api/admin/course-content", selectedCourse, "stats"],
    enabled: !!selectedCourse,
  });

  const safeContent = courseContent || { lessons: [], chapters: [], questions: [] };
  const safeStats = {
    questions: (contentStats as any)?.questions || 0,
    flashcards: (contentStats as any)?.flashcards || 0,
    studyNotes: (contentStats as any)?.studyNotes || 0,
    studyPlans: (contentStats as any)?.studyPlans || 0,
    podcasts: (contentStats as any)?.podcasts || 0,
    aiChat: (contentStats as any)?.aiChat || 0,
    lessons: (contentStats as any)?.lessons || 0,
    breakdowns: {
      questions: {
        byChapter: (contentStats as any)?.breakdowns?.questions?.byChapter || [],
        byDifficulty: (contentStats as any)?.breakdowns?.questions?.byDifficulty || [],
        bySection: (contentStats as any)?.breakdowns?.questions?.bySection || []
      },
      flashcards: {
        byChapter: (contentStats as any)?.breakdowns?.flashcards?.byChapter || [],
        byDifficulty: (contentStats as any)?.breakdowns?.flashcards?.byDifficulty || [],
        bySection: (contentStats as any)?.breakdowns?.flashcards?.bySection || []
      },
      lessons: {
        byChapter: (contentStats as any)?.breakdowns?.lessons?.byChapter || [],
        byDifficulty: (contentStats as any)?.breakdowns?.lessons?.byDifficulty || [],
        bySection: (contentStats as any)?.breakdowns?.lessons?.bySection || []
      },
      studyNotes: {
        byChapter: (contentStats as any)?.breakdowns?.studyNotes?.byChapter || [],
        byDifficulty: (contentStats as any)?.breakdowns?.studyNotes?.byDifficulty || [],
        bySection: (contentStats as any)?.breakdowns?.studyNotes?.bySection || []
      },
      studyPlans: {
        byChapter: (contentStats as any)?.breakdowns?.studyPlans?.byChapter || [],
        byDifficulty: (contentStats as any)?.breakdowns?.studyPlans?.byDifficulty || [],
        bySection: (contentStats as any)?.breakdowns?.studyPlans?.bySection || []
      },
      podcasts: {
        byChapter: (contentStats as any)?.breakdowns?.podcasts?.byChapter || [],
        byDifficulty: (contentStats as any)?.breakdowns?.podcasts?.byDifficulty || [],
        bySection: (contentStats as any)?.breakdowns?.podcasts?.bySection || []
      }
    }
  };

  const addLessonMutation = useMutation({
    mutationFn: async (data: any) => await apiRequest("POST", "/api/admin/lessons", data),
    onSuccess: () => {
      toast({ title: "Lesson added successfully!" });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/course-content", selectedCourse] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/course-content", selectedCourse, "stats"] });
    },
  });

  const addQuestionMutation = useMutation({
    mutationFn: async (data: any) => await apiRequest("POST", "/api/admin/questions", data),
    onSuccess: () => {
      toast({ title: "Question added successfully!" });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/course-content", selectedCourse] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/course-content", selectedCourse, "stats"] });
    },
  });

  const addChapterMutation = useMutation({
    mutationFn: async (data: any) => await apiRequest("POST", "/api/admin/chapters", data),
    onSuccess: () => {
      toast({ title: "Chapter added successfully!" });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/course-content", selectedCourse] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/course-content", selectedCourse, "stats"] });
    },
  });

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-6xl mx-auto">
        <div className="mb-8">
          <h1 className="text-3xl font-bold mb-2">Content Management</h1>
          <p className="text-muted-foreground">
            Create and manage course content, lessons, and questions
          </p>
        </div>

        {/* Course Selection */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle>Select Course</CardTitle>
            <CardDescription>Choose which course to manage</CardDescription>
          </CardHeader>
          <CardContent>
            <Select value={selectedCourse} onValueChange={setSelectedCourse}>
              <SelectTrigger className="w-full max-w-md">
                <SelectValue placeholder="Select a course to manage" />
              </SelectTrigger>
              <SelectContent>
                {(courses as any[])?.map((course: any) => (
                  <SelectItem key={course.id} value={course.id}>
                    {course.title}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </CardContent>
        </Card>

        {selectedCourse && (
          <Tabs defaultValue="overview" className="space-y-6">
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="overview">Overview</TabsTrigger>
              <TabsTrigger value="imports">Bulk Imports</TabsTrigger>
            </TabsList>


            <TabsContent value="overview">
              <div className="space-y-6">
                {/* Content Statistics */}

                {/* Detailed Breakdowns */}
                {(safeStats.questions > 0 || safeStats.flashcards > 0 || safeStats.lessons > 0 || safeStats.studyNotes > 0 || safeStats.podcasts > 0) && (
                  <div className="space-y-6">
                    <h2 className="text-2xl font-bold">Content Breakdowns</h2>

                    {/* Questions Breakdown */}
                    {safeStats.questions > 0 && (
                      <Card>
                        <CardHeader>
                          <CardTitle className="flex items-center gap-2">
                            <HelpCircle className="h-5 w-5" />
                            Questions Breakdown ({safeStats.questions} total)
                          </CardTitle>
                          <CardDescription>
                            Distribution of questions by chapter, section, and difficulty level
                          </CardDescription>
                        </CardHeader>
                        <CardContent>
                          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                            <div>
                              <h4 className="font-semibold mb-3">By Chapter</h4>
                              <div className="space-y-2">
                                {safeStats.breakdowns?.questions?.byChapter?.map((item: any, index: number) => (
                                  <div key={index} className="flex justify-between items-center p-2 bg-muted rounded">
                                    <span className="text-sm">{item.category}</span>
                                    <span className="font-semibold">{item.count}</span>
                                  </div>
                                ))}
                              </div>
                            </div>

                            <div>
                              <h4 className="font-semibold mb-3">By Section</h4>
                              <div className="space-y-2">
                                {safeStats.breakdowns?.questions?.bySection?.map((item: any, index: number) => (
                                  <div key={index} className="flex justify-between items-center p-2 bg-muted rounded">
                                    <span className="text-sm">{item.codeReference}</span>
                                    <span className="font-semibold">{item.count}</span>
                                  </div>
                                ))}
                              </div>
                            </div>

                            <div>
                              <h4 className="font-semibold mb-3">By Difficulty</h4>
                              <div className="space-y-2">
                                {safeStats.breakdowns?.questions?.byDifficulty?.map((item: any, index: number) => (
                                  <div key={index} className="flex justify-between items-center p-2 bg-muted rounded">
                                    <span className={`text-sm px-2 py-1 rounded text-white ${
                                      item.difficulty === 'easy' ? 'bg-green-500' :
                                      item.difficulty === 'hard' ? 'bg-orange-500' :
                                      'bg-red-500'
