import { useState, useEffect } from 'react';
import { useStripe, useElements, PaymentElement, Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import { useLocation, Link } from 'wouter';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { ArrowLeft, CheckCircle, XCircle } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

// Make sure to call `loadStripe` outside of a component's render to avoid
// recreating the `Stripe` object on every render.
if (!import.meta.env.VITE_STRIPE_PUBLIC_KEY) {
  throw new Error('Missing required Stripe key: VITE_STRIPE_PUBLIC_KEY');
}
const stripePromise = loadStripe(import.meta.env.VITE_STRIPE_PUBLIC_KEY);

const CheckoutForm = ({ service, amount, onSuccess, onError }: {
  service: string;
  amount: number;
  onSuccess: (paymentIntentId: string) => void;
  onError: (error: string) => void;
}) => {
  const stripe = useStripe();
  const elements = useElements();
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    setIsLoading(true);

    try {
      const { error, paymentIntent } = await stripe.confirmPayment({
        elements,
        redirect: 'if_required',
      });

      if (error) {
        onError(error.message || 'Payment failed');
        toast({
          title: "Payment Failed",
          description: error.message,
          variant: "destructive",
        });
      } else if (paymentIntent && paymentIntent.status === 'succeeded') {
        onSuccess(paymentIntent.id);
        toast({
          title: "Payment Successful",
          description: `Your ${service} payment has been processed!`,
        });
      }
    } catch (err: any) {
      onError(err.message || 'Payment failed');
      toast({
        title: "Payment Error",
        description: err.message || 'An unexpected error occurred',
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <PaymentElement />
      <Button
        type="submit"
        className="w-full"
        disabled={!stripe || isLoading}
        data-testid="confirm-payment-button"
      >
        {isLoading ? 'Processing...' : `Pay $${amount.toFixed(2)}`}
      </Button>
    </form>
  );
};

export default function PayPerUseCheckout() {
  const [, setLocation] = useLocation();
  const [clientSecret, setClientSecret] = useState("");
  const [service, setService] = useState("");
  const [amount, setAmount] = useState(0);
  const [paymentStatus, setPaymentStatus] = useState<'pending' | 'processing' | 'succeeded' | 'failed'>('pending');
  const [result, setResult] = useState<any>(null);

  useEffect(() => {
    // Get service type from URL params
    const urlParams = new URLSearchParams(window.location.search);
    const serviceType = urlParams.get('service');
    const data = urlParams.get('data');

    if (!serviceType) {
      setLocation('/tools');
      return;
    }

    const initializePayment = async () => {
      try {
        let endpoint = '';
        let serviceDisplayName = '';

        switch (serviceType) {
          case 'photo-analysis':
            endpoint = '/api/pay-per-use/photo-analysis';
            serviceDisplayName = 'Photo Analysis';
            break;
          case 'plan-analysis':
            endpoint = '/api/pay-per-use/plan-analysis';
            serviceDisplayName = 'Plan Analysis';
            break;
          case 'mentor-question':
            endpoint = '/api/pay-per-use/mentor-question';
            serviceDisplayName = 'AI Mentor Question';
            break;
          default:
            setLocation('/tools');
            return;
        }

        const response = await apiRequest("POST", endpoint, {
          [serviceType === 'photo-analysis' ? 'imageData' : 
           serviceType === 'plan-analysis' ? 'planData' : 'question']: data
        });

        const result = await response.json();
        setClientSecret(result.clientSecret);
        setAmount(result.amount);
        setService(serviceDisplayName);
      } catch (error: any) {
        console.error('Failed to initialize payment:', error);
        setLocation('/tools');
      }
    };

    initializePayment();
  }, [setLocation]);

  const handlePaymentSuccess = async (paymentIntentId: string) => {
    setPaymentStatus('processing');

    try {
      const urlParams = new URLSearchParams(window.location.search);
      const serviceType = urlParams.get('service');
      const data = urlParams.get('data');

      const response = await apiRequest("POST", "/api/pay-per-use/process", {
        paymentIntentId,
        service: serviceType,
        data: {
          [serviceType === 'photo-analysis' ? 'imageData' : 
           serviceType === 'plan-analysis' ? 'planData' : 'question']: data
        }
      });

      const result = await response.json();
      setResult(result.result);
      setPaymentStatus('succeeded');
    } catch (error: any) {
      console.error('Failed to process service:', error);
      setPaymentStatus('failed');
    }
  };

  const handlePaymentError = (error: string) => {
    console.error('Payment error:', error);
    setPaymentStatus('failed');
  };

  if (!clientSecret) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  if (paymentStatus === 'processing') {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <Card className="w-full max-w-md mx-4">
          <CardContent className="p-8 text-center">
            <div className="animate-spin w-12 h-12 border-4 border-primary border-t-transparent rounded-full mx-auto mb-4" />
            <h2 className="text-xl font-semibold mb-2">Processing Your Request</h2>
            <p className="text-muted-foreground">
              Your payment was successful! We're now analyzing your data...
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (paymentStatus === 'succeeded') {
    return (
      <div className="min-h-screen bg-background p-4">
        <div className="max-w-4xl mx-auto">
          <Card>
            <CardHeader>
              <div className="flex items-center space-x-2 text-green-600">
                <CheckCircle className="w-6 h-6" />
                <CardTitle>Analysis Complete!</CardTitle>
              </div>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="bg-green-50 dark:bg-green-900/20 p-4 rounded-lg">
                <p className="text-green-800 dark:text-green-200">
                  Your {service} has been completed successfully.
                </p>
              </div>

              {result && (
                <div className="space-y-4">
                  <h3 className="text-lg font-semibold">Results:</h3>
                  <div className="bg-muted p-4 rounded-lg">
                    <pre className="whitespace-pre-wrap text-sm">{JSON.stringify(result, null, 2)}</pre>
                  </div>
                </div>
              )}

              <div className="flex space-x-4">
                <Link href="/tools" className="flex-1">
                  <Button variant="outline" className="w-full">
                    <ArrowLeft className="w-4 h-4 mr-2" />
                    Back to Tools
                  </Button>
                </Link>
                <Link href="/tools/ai-pricing" className="flex-1">
                  <Button className="w-full">
                    Upgrade to Unlimited
                  </Button>
                </Link>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (paymentStatus === 'failed') {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <Card className="w-full max-w-md mx-4">
          <CardHeader>
            <div className="flex items-center space-x-2 text-red-600">
              <XCircle className="w-6 h-6" />
              <CardTitle>Payment Failed</CardTitle>
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            <p className="text-muted-foreground">
              There was an issue processing your payment. Please try again.
            </p>
            <Link href="/tools" className="block">
              <Button variant="outline" className="w-full">
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Tools
              </Button>
            </Link>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background p-4">
      <div className="max-w-lg mx-auto">
        <Card>
          <CardHeader>
            <CardTitle className="text-center">Pay Per Use - {service}</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="mb-6 p-4 bg-muted rounded-lg">
              <div className="flex justify-between items-center">
                <span className="font-medium">{service}</span>
                <span className="text-2xl font-bold text-primary">${amount.toFixed(2)}</span>
              </div>
            </div>

            <Elements stripe={stripePromise} options={{ clientSecret }}>
              <CheckoutForm
                service={service}
                amount={amount}
                onSuccess={handlePaymentSuccess}
                onError={handlePaymentError}
              />
            </Elements>

            <div className="mt-6 text-center">
              <Link href="/tools">
                <Button variant="ghost" size="sm">
                  <ArrowLeft className="w-4 h-4 mr-2" />
                  Cancel and go back
                </Button>
              </Link>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/payment-settings.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { DollarSign, CreditCard, User, ArrowLeft, CheckCircle, Clock, XCircle, AlertTriangle } from "lucide-react";
import { Link } from "wouter";

interface PaymentSettings {
  preferredPaymentMethod: "paypal" | "stripe_transfer" | "account_credit";
  paypalEmail?: string;
  accountBalance: number;
  minimumPayout: number;
}

interface PayoutRequest {
  id: string;
  amount: number;
  paymentMethod: string;
  status: "pending" | "processing" | "completed" | "failed" | "cancelled";
  requestedAt: string;
  processedAt?: string;
  completedAt?: string;
  failureReason?: string;
  transactionId?: string;
}

interface AccountTransaction {
  id: string;
  amount: number;
  type: "credit" | "debit" | "refund";
  description: string;
  balanceBefore: number;
  balanceAfter: number;
  createdAt: string;
}

export default function PaymentSettings() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isUpdating, setIsUpdating] = useState(false);
  const [payoutType, setPayoutType] = useState<"full" | "custom">("full");
  const [customAmount, setCustomAmount] = useState<string>("");

  // Fetch payment settings
  const { data: settings, isLoading: settingsLoading } = useQuery({
    queryKey: ["/api/payment/settings"],
    retry: false,
  });

  // Fetch payout requests
  const { data: payoutRequests, isLoading: payoutsLoading } = useQuery({
    queryKey: ["/api/payment/payouts"],
    retry: false,
  });

  // Fetch account transactions
  const { data: transactions, isLoading: transactionsLoading } = useQuery({
    queryKey: ["/api/payment/transactions"],
    retry: false,
  });

  // Update payment preferences mutation
  const updatePreferencesMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest("PUT", "/api/payment/settings", data);
    },
    onSuccess: () => {
      toast({
        title: "Settings Updated",
        description: "Your payment preferences have been saved.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/payment/settings"] });
    },
    onError: (error: any) => {
      toast({
        title: "Update Failed",
        description: error.message || "Failed to update payment settings",
        variant: "destructive",
      });
    },
  });

  // Request payout mutation
  const requestPayoutMutation = useMutation({
    mutationFn: async (data: any) => {
      return await apiRequest("POST", "/api/payment/request-payout", data);
    },
    onSuccess: () => {
      toast({
        title: "Payout Requested",
        description: "Your payout request has been submitted for processing.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/payment/payouts"] });
      queryClient.invalidateQueries({ queryKey: ["/api/referrals/stats"] });
    },
    onError: (error: any) => {
      toast({
        title: "Request Failed",
        description: error.message || "Failed to request payout",
        variant: "destructive",
      });
    },
  });

  const handleUpdateSettings = (formData: FormData) => {
    const paymentMethod = formData.get("paymentMethod") as string;
    const paypalEmail = formData.get("paypalEmail") as string;
    const minimumPayout = parseFloat(formData.get("minimumPayout") as string);

    updatePreferencesMutation.mutate({
      paymentMethod,
      paypalEmail: paymentMethod === "paypal" ? paypalEmail : undefined,
      minimumPayout,
    });
  };

  const handleRequestPayout = () => {
    let requestAmount = settings?.unpaidEarnings || 0;

    if (payoutType === "custom") {
      requestAmount = parseFloat(customAmount);
      if (isNaN(requestAmount) || requestAmount <= 0) {
        toast({
          title: "Invalid Amount",
          description: "Please enter a valid payout amount",
          variant: "destructive",
        });
        return;
      }
      if (requestAmount > (settings?.unpaidEarnings || 0)) {
        toast({
          title: "Amount Too High",
          description: `Maximum available: $${settings?.unpaidEarnings?.toFixed(2)}`,
          variant: "destructive",
        });
        return;
      }
    }

    if (requestAmount < (settings?.minimumPayout || 25)) {
      toast({
        title: "Amount Too Low",
        description: `Minimum payout amount is $${settings?.minimumPayout || 25}`,
        variant: "destructive",
      });
      return;
    }

    requestPayoutMutation.mutate({
      paymentMethod: settings.preferredPaymentMethod,
      amount: requestAmount,
    });
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "completed":
        return <CheckCircle className="h-4 w-4 text-green-600" />;
      case "processing":
        return <Clock className="h-4 w-4 text-blue-600" />;
      case "failed":
        return <XCircle className="h-4 w-4 text-red-600" />;
      case "cancelled":
        return <XCircle className="h-4 w-4 text-gray-600" />;
      default:
        return <Clock className="h-4 w-4 text-yellow-600" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "completed":
        return "text-green-600 bg-green-50";
      case "processing":
        return "text-blue-600 bg-blue-50";
      case "failed":
        return "text-red-600 bg-red-50";
      case "cancelled":
        return "text-gray-600 bg-gray-50";
      default:
        return "text-yellow-600 bg-yellow-50";
    }
  };

  if (settingsLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <section className="py-8">
        <div className="container mx-auto px-4">
          <div className="mb-6">
            <Link href="/referrals">
              <Button variant="outline" className="mb-4" data-testid="back-to-referrals">
                <ArrowLeft className="h-4 w-4 mr-2" />
                Back to Referrals
              </Button>
            </Link>
            <h1 className="text-3xl font-bold">Payment Settings</h1>
            <p className="text-muted-foreground mt-2">
              Manage your payment preferences and view payout history
            </p>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Left Column - Settings */}
            <div className="space-y-6">
              {/* Account Balance */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <DollarSign className="h-5 w-5 text-green-600" />
                    Account Balance
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="text-center">
                      <div className="text-3xl font-bold text-green-600">
                        ${settings?.accountBalance?.toFixed(2) || "0.00"}
                      </div>
                      <div className="text-sm text-muted-foreground">Available Balance</div>
                    </div>
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div className="text-center p-3 bg-gray-50 rounded">
                        <div className="font-bold">${settings?.unpaidEarnings?.toFixed(2) || "0.00"}</div>
                        <div className="text-muted-foreground">Unpaid Earnings</div>
                      </div>
                      <div className="text-center p-3 bg-gray-50 rounded">
                        <div className="font-bold">${settings?.minimumPayout?.toFixed(2) || "25.00"}</div>
                        <div className="text-muted-foreground">Min Payout</div>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Payment Preferences */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <CreditCard className="h-5 w-5" />
                    Payment Preferences
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <form action={handleUpdateSettings} className="space-y-4">
                    <div>
                      <Label htmlFor="paymentMethod">Preferred Payment Method</Label>
                      <Select name="paymentMethod" defaultValue={settings?.preferredPaymentMethod || "paypal"}>
                        <SelectTrigger data-testid="payment-method-select">
                          <SelectValue placeholder="Select payment method" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="paypal">PayPal</SelectItem>
                          <SelectItem value="stripe_transfer">Bank Transfer (Stripe)</SelectItem>
                          <SelectItem value="account_credit">Account Credit</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>

                    <div>
                      <Label htmlFor="paypalEmail">PayPal Email (if using PayPal)</Label>
                      <Input
                        id="paypalEmail"
                        name="paypalEmail"
                        type="email"
                        defaultValue={settings?.paypalEmail || ""}
                        placeholder="your-paypal@email.com"
                        data-testid="paypal-email"
                      />
                    </div>

                    <div>
                      <Label htmlFor="minimumPayout">Minimum Payout Amount</Label>
                      <Input
                        id="minimumPayout"
                        name="minimumPayout"
                        type="number"
                        min="25"
                        step="0.01"
                        defaultValue={settings?.minimumPayout || 25}
                        data-testid="minimum-payout"
                      />
                      <div className="text-xs text-muted-foreground mt-1">
                        Minimum $25 for PayPal/Stripe, $10 for account credit
                      </div>
                    </div>

                    <Button
                      type="submit"
                      disabled={updatePreferencesMutation.isPending}
                      className="w-full"
                      data-testid="update-settings"
                    >
                      {updatePreferencesMutation.isPending ? "Updating..." : "Update Settings"}
                    </Button>
                  </form>
                </CardContent>
              </Card>

              {/* Request Payout */}
              <Card className="border-green-200 bg-green-50">
                <CardHeader>
                  <CardTitle className="text-green-800">Request Payout</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="text-sm text-green-700">
                      Available for payout: <strong>${settings?.unpaidEarnings?.toFixed(2) || "0.00"}</strong>
                    </div>

                    <RadioGroup value={payoutType} onValueChange={(value: "full" | "custom") => setPayoutType(value)}>
                      <div className="flex items-center space-x-2">
                        <RadioGroupItem value="full" id="full" />
                        <Label htmlFor="full" className="text-sm">
                          Request full amount (${settings?.unpaidEarnings?.toFixed(2) || "0.00"})
                        </Label>
                      </div>
                      <div className="flex items-center space-x-2">
                        <RadioGroupItem value="custom" id="custom" />
                        <Label htmlFor="custom" className="text-sm">
                          Custom amount
                        </Label>
                      </div>
                    </RadioGroup>

                    {payoutType === "custom" && (
                      <div>
                        <Label htmlFor="customAmount" className="text-sm">Custom Payout Amount</Label>
                        <Input
                          id="customAmount"
                          type="number"
                          min="0"
                          max={settings?.unpaidEarnings || 0}
                          step="0.01"
                          value={customAmount}
                          onChange={(e) => setCustomAmount(e.target.value)}
                          placeholder={`Min: $${settings?.minimumPayout || 25}, Max: $${settings?.unpaidEarnings?.toFixed(2) || "0.00"}`}
                          className="mt-1"
                          data-testid="custom-payout-amount"
                        />
                      </div>
                    )}

                    <div className="text-xs text-green-600">
                      {payoutType === "full" 
                        ? "You'll receive the full available amount"
                        : "Specify how much you want to withdraw"
                      }
                    </div>

                    <Button
                      onClick={handleRequestPayout}
                      disabled={!settings?.unpaidEarnings || (settings?.unpaidEarnings < (settings?.minimumPayout || 25)) || requestPayoutMutation.isPending}
                      className="w-full bg-green-600 hover:bg-green-700"
                      data-testid="request-payout"
                    >
                      {requestPayoutMutation.isPending 
                        ? "Processing..." 
                        : `Request ${payoutType === "full" ? "Full" : "Custom"} Payout`
                      }
                    </Button>
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* Right Column - History */}
            <div className="lg:col-span-2 space-y-6">
              {/* Payout Requests */}
              <Card>
                <CardHeader>
                  <CardTitle>Payout Requests</CardTitle>
                </CardHeader>
                <CardContent>
                  {payoutsLoading ? (
                    <div className="flex items-center justify-center py-8">
                      <div className="animate-spin w-6 h-6 border-4 border-primary border-t-transparent rounded-full" />
                    </div>
                  ) : payoutRequests && payoutRequests.length > 0 ? (
                    <div className="space-y-4">
                      {payoutRequests.map((payout: PayoutRequest) => (
                        <div key={payout.id} className="flex justify-between items-center p-4 border rounded-lg">
                          <div>
                            <div className="font-medium">${payout.amount.toFixed(2)}</div>
                            <div className="text-sm text-muted-foreground">
                              {new Date(payout.requestedAt).toLocaleDateString()} • {payout.paymentMethod}
                            </div>
                            {payout.failureReason && (
                              <div className="text-xs text-red-600 mt-1">
                                <AlertTriangle className="h-3 w-3 inline mr-1" />
                                {payout.failureReason}
                              </div>
                            )}
                          </div>
                          <div className="text-right">
                            <div className={`inline-flex items-center gap-1 px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(payout.status)}`}>
                              {getStatusIcon(payout.status)}
                              {payout.status.charAt(0).toUpperCase() + payout.status.slice(1)}
                            </div>
                            {payout.transactionId && (
                              <div className="text-xs text-muted-foreground mt-1">
                                ID: {payout.transactionId}
                              </div>
                            )}
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-8 text-muted-foreground">
                      No payout requests yet
                    </div>
                  )}
                </CardContent>
              </Card>

              {/* Account Transactions */}
              <Card>
                <CardHeader>
                  <CardTitle>Account Transactions</CardTitle>
                </CardHeader>
                <CardContent>
                  {transactionsLoading ? (
                    <div className="flex items-center justify-center py-8">
                      <div className="animate-spin w-6 h-6 border-4 border-primary border-t-transparent rounded-full" />
                    </div>
                  ) : transactions && transactions.length > 0 ? (
                    <div className="space-y-3">
                      {transactions.slice(0, 10).map((transaction: AccountTransaction) => (
                        <div key={transaction.id} className="flex justify-between items-center p-3 border rounded">
                          <div>
                            <div className="font-medium">{transaction.description}</div>
                            <div className="text-sm text-muted-foreground">
                              {new Date(transaction.createdAt).toLocaleDateString()}
                            </div>
                          </div>
                          <div className="text-right">
                            <div className={`font-bold ${transaction.type === 'debit' ? 'text-red-600' : 'text-green-600'}`}>
                              {transaction.type === 'debit' ? '-' : '+'}${transaction.amount.toFixed(2)}
                            </div>
                            <div className="text-xs text-muted-foreground">
                              Balance: ${transaction.balanceAfter.toFixed(2)}
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-8 text-muted-foreground">
                      No transactions yet
                    </div>
                  )}
                </CardContent>
              </Card>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
}

client/src/pages/podcast-view.tsx.content:
import { useRoute } from "wouter";
import { useQuery } from "@tanstack/react-query";
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { ArrowLeft, Loader2 } from "lucide-react";
import { Link } from "wouter";
import PodcastPlayer from "@/components/podcast-player";

export default function PodcastView() {
  const [match, params] = useRoute("/course/:courseId/podcast/:contentId");
  const contentId = params?.contentId;
  const courseId = params?.courseId;
  const [podcastProgress, setPodcastProgress] = useState(0);
  const [isCompleted, setIsCompleted] = useState(false);

  const { data: content, isLoading, error } = useQuery({
    queryKey: [`/api/content/${contentId}/display`],
    enabled: !!contentId,
  });

  // Load saved progress from localStorage
  useEffect(() => {
    if (contentId) {
      const savedProgress = localStorage.getItem(`podcast-progress-${contentId}`);
      if (savedProgress) {
        const progress = JSON.parse(savedProgress);
        setPodcastProgress(progress.sentenceIndex || 0);
        setIsCompleted(progress.completed || false);
      }
    }
  }, [contentId]);

  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <Loader2 className="w-8 h-8 animate-spin" />
      </div>
    );
  }

  if (error || !content) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <p className="text-red-500 mb-4">Failed to load content</p>
          <Button asChild>
            <Link href="/courses">Back to Courses</Link>
          </Button>
        </div>
      </div>
    );
  }

  // Extract podcast content safely
  const podcastContent = (content as any)?.content?.extracted?.transcript || 
                        (content as any)?.content?.extracted?.content || 
                        (content as any)?.content?.extracted?.text || 
                        (content as any)?.content?.podcast?.transcript ||
                        (content as any)?.content?.text || 
                        'No podcast content available';

  // Check for autostart from URL
  const urlParams = new URLSearchParams(window.location.search);
  const shouldAutoStart = urlParams.get('autostart') === 'true';

  const handleProgressUpdate = (sentenceIndex: number, totalSentences: number) => {
    const progress = (sentenceIndex / totalSentences) * 100;
    setPodcastProgress(sentenceIndex);

    // Save progress to localStorage
    localStorage.setItem(`podcast-progress-${contentId}`, JSON.stringify({
      sentenceIndex,
      totalSentences,
      completed: sentenceIndex >= totalSentences - 1,
      timestamp: Date.now()
    }));

    // Mark as completed if finished
    if (sentenceIndex >= totalSentences - 1) {
      setIsCompleted(true);
    }
  };

  const handleReturnToLesson = () => {
    // Navigate back to lesson page
    window.location.href = `/course/${courseId}/lesson/${content?.section || ''}`;
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 py-8">
        <Button asChild variant="outline" className="mb-6">
          <Link href="/courses">
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Courses
          </Link>
        </Button>

        <Card className="mb-6">
          <CardHeader className="text-center">
            <div className="w-16 h-16 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center mx-auto mb-4">
              <span className="text-2xl">🎧</span>
            </div>
            <CardTitle className="text-2xl">
              {content?.title || 'Podcast Lesson'}
            </CardTitle>
            <p className="text-muted-foreground">
              Audio lesson with interactive transcript
            </p>
          </CardHeader>
        </Card>

        <PodcastPlayer 
          content={podcastContent} 
          autoStart={shouldAutoStart}
          initialProgress={podcastProgress}
          onProgressUpdate={handleProgressUpdate}
        />

        <Card className="mt-6">
          <CardContent className="p-6 text-center">
            <Button 
              className="w-full max-w-sm mx-auto"
              onClick={handleReturnToLesson}
            >
              <ArrowLeft className="w-4 h-4 mr-2" />
              Return to Lesson {isCompleted && '(Completed)'}
            </Button>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/admin/bulk-studyplan-im.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Upload, FileText, CheckCircle, AlertCircle, Clock, BookOpen } from "lucide-react";
import { Input } from "@/components/ui/input";
import { PLUMBING_CODE_SECTIONS, filterSectionsByChapter } from "@/lib/plumbing-code-sections";

export default function BulkStudyPlanImport() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedCourse, setSelectedCourse] = useState("");
  const [selectedChapter, setSelectedChapter] = useState("");
  const [selectedSection, setSelectedSection] = useState("");
  const [selectedDifficulty, setSelectedDifficulty] = useState("medium");

  // Filter sections based on selected chapter
  const filteredSections = selectedChapter ? filterSectionsByChapter(selectedChapter) : [];

  // Reset section when chapter changes
  useEffect(() => {
    setSelectedSection("");
  }, [selectedChapter]);
  const [studyPlanText, setStudyPlanText] = useState("");
  const [previewPlans, setPreviewPlans] = useState<any[]>([]);
  const [importStatus, setImportStatus] = useState<"idle" | "parsing" | "previewing" | "importing" | "success">("idle");

  // Fetch courses
  const { data: courses } = useQuery({
    queryKey: ["/api/courses"],
  });

  const bulkImportMutation = useMutation({
    mutationFn: async (data: any) => await apiRequest("POST", "/api/admin/study-plans/bulk-import", data),
    onSuccess: (result: any) => {
      const { imported, duplicatesSkipped, totalSubmitted } = result;

      if (duplicatesSkipped > 0) {
        toast({ 
          title: "Import completed with duplicates detected", 
          description: `Added ${imported} new study plans. Skipped ${duplicatesSkipped} duplicates out of ${totalSubmitted} total.`,
          variant: "default"
        });
      } else {
        toast({ 
          title: "All study plans imported successfully!", 
          description: `Added ${imported} new study plans to the course.`
        });
      }

      setImportStatus("success");
      setStudyPlanText("");
      setPreviewPlans([]);
      queryClient.invalidateQueries({ queryKey: ["/api/admin/course-content"] });
    },
    onError: (error: any) => {
      toast({
        title: "Import failed",
        description: error.message || "Failed to import study plans",
        variant: "destructive"
      });
      setImportStatus("idle");
    }
  });

  const parseStudyPlans = () => {
    if (!studyPlanText.trim()) {
      toast({
        title: "No content",
        description: "Please enter study plans to parse",
        variant: "destructive"
      });
      return;
    }

    setImportStatus("parsing");

    try {
      const parsed = parseStudyPlansFromText(studyPlanText);
      if (parsed.length === 0) {
        throw new Error("No valid study plans found. Please check the format.");
      }

      setPreviewPlans(parsed);
      setImportStatus("previewing");
      toast({
        title: "Study plans parsed successfully!",
        description: `Found ${parsed.length} study plan sessions ready for import.`
      });
    } catch (error: any) {
      toast({
        title: "Parse error",
        description: error.message,
        variant: "destructive"
      });
      setImportStatus("idle");
    }
  };

  const isSectionValid = selectedSection && selectedSection.trim().length > 0;

  const importStudyPlans = () => {
    if (!selectedCourse) {
      toast({
        title: "No course selected",
        description: "Please select a course first",
        variant: "destructive"
      });
      return;
    }

    if (!selectedChapter || !selectedSection) {
      toast({
        title: "Chapter and Section required",
        description: "Please select both chapter and section for organization",
        variant: "destructive"
      });
      return;
    }

    if (!isSectionValid) {
      toast({
        title: "Invalid section number",
        description: "Enter a valid section number (e.g., 101)",
        variant: "destructive"
      });
      return;
    }

    // Update study plans with current chapter and section values to prevent stale data
    const payloadPlans = previewPlans.map(plan => ({
      ...plan,
      chapter: selectedChapter,
      category: selectedChapter,
      section: selectedSection,
      codeReference: `${selectedChapter} - Section ${selectedSection}`,
      difficulty: selectedDifficulty
    }));

    setImportStatus("importing");
    bulkImportMutation.mutate({
      courseId: selectedCourse,
      studyPlans: payloadPlans
    });
  };

  const parseStudyPlansFromText = (text: string) => {
    const studyPlans: any[] = [];

    // Split by time duration headers (10 minutes, 15 minutes, 1 hour, etc.)
    const timeSections = text.split(/(?=\d+(?:\.\d+)?\s+(?:minutes?|hours?)\n)/i);

    timeSections.forEach((section, index) => {
      const lines = section.trim().split('\n').filter(line => line.trim());
      if (lines.length < 3) return;

      // Extract duration from first line - handle both minutes and hours
      const minuteMatch = lines[0].match(/(\d+)\s+minutes?/i);
      const hourMatch = lines[0].match(/(\d+(?:\.\d+)?)\s+hours?/i);

      let duration = 0;
      if (minuteMatch) {
        duration = parseInt(minuteMatch[1]);
      } else if (hourMatch) {
        duration = Math.round(parseFloat(hourMatch[1]) * 60); // Convert hours to minutes
      } else {
        return; // No valid duration found
      }

      // Look for study plan content after the duration
      let planTitle = "";
      let planContent = "";
      let startIndex = 1;

      // Find the plan title (usually has "Study Plan" in it)
      for (let i = 1; i < lines.length; i++) {
        if (lines[i].includes("Study Plan") || lines[i].includes("study plan")) {
          planTitle = lines[i].trim();
          startIndex = i + 1;
          break;
        }
      }

      // If no specific title found, create one
      if (!planTitle) {
        planTitle = `${duration}-Minute Study Plan`;
        startIndex = 1;
      }

      // Collect all content after the title
      planContent = lines.slice(startIndex).join('\n').trim();

      if (planTitle.length > 3 && planContent.length > 20) {
        studyPlans.push({
          id: crypto.randomUUID(),
          title: planTitle,
          content: planContent,
          duration: duration,
          type: 'study_plan',
          createdAt: new Date(),
        });
      }
    });

    // If no time-based sections found, try parsing by major headings
    if (studyPlans.length === 0) {
      const sections = text.split(/\n(?=[A-Z].*Study Plan)/);

      sections.forEach((section, index) => {
        const lines = section.trim().split('\n').filter(line => line.trim());
        if (lines.length < 2) return;

        const title = lines[0].trim();
        const content = lines.slice(1).join('\n').trim();

        // Try to extract duration from content
        const durationMatch = content.match(/(\d+)[-\s]*minute/i);
        const duration = durationMatch ? parseInt(durationMatch[1]) : 30; // Default 30 minutes

        if (title.length > 3 && content.length > 20) {
          studyPlans.push({
            id: crypto.randomUUID(),
            title: title,
            content: content,
            duration: duration,
            type: 'study_plan',
            createdAt: new Date(),
          });
        }
      });
    }

    return studyPlans;
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-4xl mx-auto">
        <div className="mb-8">
          <h1 className="text-3xl font-bold mb-2">Bulk Study Plan Import</h1>
          <p className="text-muted-foreground">
            Import adaptive study plans with time-based sessions and interactive content
          </p>
        </div>

        {/* Course and Chapter/Section Selection */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle>Course & Louisiana Plumbing Code Organization</CardTitle>
            <CardDescription>Choose course, chapter, section and difficulty level for all study plans in this batch</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div>
                <Label htmlFor="course">Select Course</Label>
                <Select value={selectedCourse} onValueChange={setSelectedCourse}>
                  <SelectTrigger className="w-full">
                    <SelectValue placeholder="Select a course" />
                  </SelectTrigger>
                  <SelectContent>
                    {(courses as any[])?.map((course: any) => (
                      <SelectItem key={course.id} value={course.id}>
                        {course.title}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="chapter">Chapter</Label>
                <Select value={selectedChapter} onValueChange={setSelectedChapter}>
                  <SelectTrigger className="w-full">
                    <SelectValue placeholder="Select chapter" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="Chapter 1">Chapter 1 - Administration</SelectItem>
                    <SelectItem value="Chapter 2">Chapter 2 - Definitions</SelectItem>
                    <SelectItem value="Chapter 3">Chapter 3 - General Regulations</SelectItem>
                    <SelectItem value="Chapter 4">Chapter 4 - Fixtures, Faucets and Fittings</SelectItem>
                    <SelectItem value="Chapter 5">Chapter 5 - Water Heaters</SelectItem>
                    <SelectItem value="Chapter 6">Chapter 6 - Water Supply and Distribution</SelectItem>
                    <SelectItem value="Chapter 7">Chapter 7 - Sanitary Drainage</SelectItem>
                    <SelectItem value="Chapter 8">Chapter 8 - Indirect/Special Waste</SelectItem>
                    <SelectItem value="Chapter 9">Chapter 9 - Vents</SelectItem>
                    <SelectItem value="Chapter 10">Chapter 10 - Traps, Interceptors and Separators</SelectItem>
                    <SelectItem value="Chapter 11">Chapter 11 - Storm Drainage</SelectItem>
                    <SelectItem value="Chapter 12">Chapter 12 - Special Piping and Storage Systems</SelectItem>
                    <SelectItem value="Chapter 13">Chapter 13 - Health Care Facilities and Other Special Occupancies</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="section">Section</Label>
                <Input 
                  id="section"
                  type="text"
                  value={selectedSection} 
                  onChange={(e) => setSelectedSection(e.target.value)}
                  placeholder={selectedChapter ? `Enter section number (e.g., 101, 103, 105...)` : "Select chapter first"}
                  disabled={!selectedChapter}
                  className="w-full"
                />
              </div>
              <div>
                <Label htmlFor="difficulty">Difficulty Level</Label>
                <Select value={selectedDifficulty} onValueChange={setSelectedDifficulty}>
                  <SelectTrigger className="w-full">
                    <SelectValue placeholder="Select difficulty" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="easy">Easy - Basic recall</SelectItem>
                    <SelectItem value="hard">Hard - Analysis</SelectItem>
                    <SelectItem value="very_hard">Very Hard - Synthesis</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardContent>
        </Card>


        {/* Study Plan Input */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle>Paste Your Study Plans</CardTitle>
            <CardDescription>Copy and paste time-based study plans with structured learning sessions</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div>
                <Label htmlFor="studyplans">Study Plans Content</Label>
                <Textarea
                  id="studyplans"
                  value={studyPlanText}
                  onChange={(e) => setStudyPlanText(e.target.value)}
                  rows={15}
                  placeholder={`Paste your study plans here...

Example:
10 minutes

10-Minute Study Plan: LSPC Administration & Authority

Minutes 0-2: Core Adoption & Naming
Focus on how the Department of Health and Hospitals, Office of Public Health, adopts Part XIV (Plumbing) of the Sanitary Code, State of Louisiana (LAC 51:XIV).
Understand that this is also known as the "Louisiana State Plumbing Code" (LSPC) and that these terms are interchangeable.

Minutes 2-5: Source of Authority
Identify R.S. 36:258(B) as the first source of authority for the sanitary code.
Note that more specific provisions are found in Chapters 1 and 4 of Title 40 of the Louisiana Revised Statutes.

15 minutes

15-Minute Study Plan: LSPC Administration & Authority

Minutes 0-2: Recap & Core Adoption
Quickly review that the Department of Health and Hospitals, Office of Public Health, adopts Part XIV...`}
                  className="font-mono text-sm"
                />
              </div>

              <div className="flex gap-2">
                <Button 
                  onClick={parseStudyPlans}
                  disabled={!studyPlanText.trim() || importStatus === "parsing"}
                  className="flex items-center gap-2"
                >
                  <FileText className="h-4 w-4" />
                  {importStatus === "parsing" ? "Parsing..." : "Parse Study Plans"}
                </Button>

                {previewPlans.length > 0 && (
                  <Button 
                    onClick={importStudyPlans}
                    disabled={!selectedCourse || importStatus === "importing"}
                    className="flex items-center gap-2"
                  >
                    <Upload className="h-4 w-4" />
                    {importStatus === "importing" ? "Importing..." : `Import ${previewPlans.length} Study Plans`}
                  </Button>
                )}
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Preview */}
        {previewPlans.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <CheckCircle className="h-5 w-5 text-green-600" />
                Preview ({previewPlans.length} study plans found)
              </CardTitle>
              <CardDescription>Review the parsed study plans before importing</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4 max-h-96 overflow-y-auto">
                {previewPlans.map((plan, index) => (
                  <div key={index} className="border rounded-lg p-4">
                    <div className="space-y-3">
                      <div className="flex items-center justify-between">
                        <div>
                          <h4 className="font-semibold text-blue-600 mb-1">Study Plan Title</h4>
                          <p className="text-sm bg-blue-50 p-2 rounded font-medium">
                            {plan.title}
                          </p>
                        </div>
                        <div className="flex items-center gap-1 text-sm text-muted-foreground bg-gray-100 px-2 py-1 rounded">
                          <Clock className="h-3 w-3" />
                          {plan.duration} min
                        </div>
                      </div>
                      <div>
                        <h4 className="font-semibold text-green-600 mb-2">Content Preview</h4>
                        <div className="text-sm bg-green-50 p-3 rounded whitespace-pre-line max-h-32 overflow-y-auto">
                          {plan.content.substring(0, 500)}{plan.content.length > 500 ? "..." : ""}
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        )}

        {importStatus === "success" && (
          <Alert className="mt-6">
            <CheckCircle className="h-4 w-4" />
            <AlertDescription>
              Study plans imported successfully! You can now view them in the Content Management section.
            </AlertDescription>
          </Alert>
        )}
      </div>
    </div>
  );
}

client/src/pages/checkout/success.tsx.content:
import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { CheckCircle, Mail, Lock, Building2 } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export default function CheckoutSuccess() {
  const [location] = useLocation();
  const [isProcessing, setIsProcessing] = useState(true);
  const [credentials, setCredentials] = useState<any>(null);
  const [customerEmail, setCustomerEmail] = useState("");
  const { toast } = useToast();

  useEffect(() => {
    const urlParams = new URLSearchParams(location.split('?')[1] || '');
    const plan = urlParams.get('plan');
    const email = urlParams.get('email');
    const paymentIntentId = urlParams.get('payment_intent');

    if (email) {
      setCustomerEmail(decodeURIComponent(email));
    }

    if (paymentIntentId && plan && email) {
      // Process the successful payment and create account
      processSuccessfulPayment(paymentIntentId, plan, decodeURIComponent(email));
    } else {
      setIsProcessing(false);
    }
  }, [location]);

  const processSuccessfulPayment = async (paymentIntentId: string, plan: string, email: string) => {
    try {
      const response = await apiRequest("POST", "/api/process-payment-success", {
        paymentIntentId,
        plan,
        email
      });
      const data = await response.json();

      setCredentials(data.credentials);
      setIsProcessing(false);

      toast({
        title: "Account Created Successfully!",
        description: "Check your email for login credentials and receipt",
      });
    } catch (error: any) {
      console.error("Error processing payment success:", error);
      setIsProcessing(false);
      toast({
        title: "Processing Error",
        description: "Payment successful, but there was an issue creating your account. Please contact support.",
        variant: "destructive",
      });
    }
  };

  if (isProcessing) {
    return (
      <div className="min-h-screen bg-background py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-2xl mx-auto">
          <div className="text-center">
            <div className="flex items-center justify-center space-x-2 mb-6">
              <div className="w-12 h-12 bg-primary rounded-lg flex items-center justify-center">
                <Building2 className="w-7 h-7 text-primary-foreground" />
              </div>
              <div className="text-left">
                <h1 className="text-xl font-bold text-primary">LA Plumb Prep</h1>
                <p className="text-xs text-muted-foreground">Official State Prep Course</p>
