import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { Separator } from "@/components/ui/separator";
import { 
  ChevronLeft, 
  ChevronRight, 
  CheckCircle, 
  XCircle, 
  BookOpen,
  RotateCcw,
  ArrowRight,
  MessageCircle
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface QuizQuestion {
  id: string;
  question: string;
  options: string[];
  correctAnswer: number;
  explanation?: string;
  codeReference?: string;
  difficulty: 'easy' | 'medium' | 'hard' | 'very_hard';
}

interface QuizAttempt {
  id: string;
  questions: QuizQuestion[];
  userAnswers: { [questionIndex: number]: number };
  score?: number;
  passed?: boolean;
  incorrectQuestions?: QuizQuestion[];
}

interface InteractiveQuizProps {
  section: string;
  contentId: string;
  title?: string;
  onComplete?: () => void;
}

export default function InteractiveQuiz({ section, contentId, title, onComplete }: InteractiveQuizProps) {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState<{ [key: number]: number }>({});
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [showReview, setShowReview] = useState(false);
  const [startTime] = useState(Date.now());
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [, setLocation] = useLocation();

  // Fetch quiz questions for this section
  const { data: quizData, isLoading } = useQuery<QuizAttempt>({
    queryKey: [`/api/quiz/${section}/start`],
    queryFn: async () => {
      const response = await fetch(`/api/quiz/${section}/start`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
      });
      if (!response.ok) throw new Error("Failed to start quiz");
      return response.json();
    },
  });

  // Submit quiz mutation
  const submitMutation = useMutation({
    mutationFn: async () => {
      const timeSpent = Math.floor((Date.now() - startTime) / 1000);
      const questionsWithAnswers = quizData?.questions.map((q, index) => ({
        id: q.id,
        question: q.question,  // Keep consistent field naming
        userAnswer: answers[index] ?? null,
        correctAnswer: q.correctAnswer,
        isCorrect: answers[index] === q.correctAnswer
      }));

      return await apiRequest("POST", `/api/quiz/${section}/submit`, {
        contentId,
        questions: questionsWithAnswers,
        timeSpent
      });
    },
    onSuccess: (result: any) => {
      setIsSubmitted(true);
      queryClient.invalidateQueries({ queryKey: [`/api/quiz/${section}`] });

      if (result.passed) {
        toast({
          title: "Congratulations!",
          description: `You passed with ${result.score}%! Moving to next section.`,
        });
        setTimeout(() => onComplete?.(), 2000);
      } else {
        toast({
          title: "Keep studying!",
          description: `You scored ${result.score}%. Let's review your mistakes with AI help.`,
          variant: "destructive",
        });
        // Automatically redirect to AI chat instead of showing review
        handleGetAIHelp();
      }
    },
    onError: (error: any) => {
      toast({
        title: "Submission failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
          <p>Loading quiz questions...</p>
        </div>
      </div>
    );
  }

  if (!quizData?.questions?.length) {
    return (
      <Card>
        <CardContent className="p-6 text-center">
          <p className="text-muted-foreground">No questions available for this section.</p>
        </CardContent>
      </Card>
    );
  }

  const questions = quizData.questions;
  const currentQ = questions[currentQuestion];
  const progress = ((currentQuestion + 1) / questions.length) * 100;
  const answeredCount = Object.keys(answers).length;
  const allAnswered = answeredCount === questions.length;

  const handleAnswerChange = (answerIndex: number) => {
    setAnswers(prev => ({
      ...prev,
      [currentQuestion]: answerIndex
    }));
  };

  const handleNext = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    }
  };

  const handlePrevious = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(currentQuestion - 1);
    }
  };

  const handleQuestionJump = (questionIndex: number) => {
    setCurrentQuestion(questionIndex);
  };

  const handleSubmit = () => {
    if (!allAnswered) {
      toast({
        title: "Incomplete Quiz",
        description: "Please answer all questions before submitting.",
        variant: "destructive",
      });
      return;
    }
    submitMutation.mutate();
  };

  const handleRetry = () => {
    setAnswers({});
    setCurrentQuestion(0);
    setIsSubmitted(false);
    setShowReview(false);
    queryClient.invalidateQueries({ queryKey: [`/api/quiz/${section}`] });
  };

  const handleGetAIHelp = () => {
    // Calculate results for AI context
    const incorrectQuestions = questions.filter((_, index) => 
      answers[index] !== undefined && answers[index] !== questions[index].correctAnswer
    );
    const score = Math.round((Object.keys(answers).length - incorrectQuestions.length) / questions.length * 100);

    // Create a summary of incorrect questions for AI context
    const incorrectQuestionsText = incorrectQuestions.map((question, index) => {
      const originalIndex = questions.findIndex(q => q.id === question.id);
      const userAnswer = answers[originalIndex];
      const correctAnswer = question.correctAnswer;

      return `Question ${originalIndex + 1}: ${question.question}\n` +
        `Your answer: ${question.options[userAnswer]}\n` +
        `Correct answer: ${question.options[correctAnswer]}\n` +
        `${question.explanation ? `Explanation: ${question.explanation}\n` : ''}` +
        `${question.codeReference ? `Code Reference: ${question.codeReference}\n` : ''}`;
    }).join('\n---\n');

    // Navigate to AI mentor with context about incorrect questions
    const aiContext = {
      type: 'quiz_review',
      section: section,
      title: title || `Section ${section} Quiz`,
      incorrectCount: incorrectQuestions.length,
      totalQuestions: questions.length,
      score: score,
      questions: incorrectQuestionsText
    };

    // Store context in session storage for the AI mentor to use
    sessionStorage.setItem('aiMentorContext', JSON.stringify(aiContext));

    // Navigate to study companion (AI mentor)
    setLocation('/study-companion');
  };

  // Calculate results for completion check
  const score = Math.round((answeredCount - questions.filter((_, index) => 
    answers[index] !== undefined && answers[index] !== questions[index].correctAnswer
  ).length) / questions.length * 100);

  // Check if this is a chapter review (requires 80%) or regular quiz (requires 70%)
  const isChapterReview = title?.toLowerCase().includes('chapter review') || false;
  const requiredScore = isChapterReview ? 80 : 70;
  const passed = score >= requiredScore;

  if (isSubmitted && passed) {
    return (
      <Card>
        <CardContent className="p-6 text-center space-y-4">
          <CheckCircle className="w-16 h-16 text-green-500 mx-auto" />
          <div className="space-y-2">
            <h2 className="text-2xl font-bold text-green-700">Congratulations!</h2>
            <p className="text-lg">You passed with {score}%</p>
            <p className="text-muted-foreground">
              You can now proceed to the next section.
            </p>
          </div>

          <Button onClick={() => onComplete?.()} className="w-full max-w-sm">
            <ArrowRight className="w-4 h-4 mr-2" />
            Continue to Next Section
          </Button>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Progress and Navigation */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Section {section} Quiz</CardTitle>
            <Badge variant="secondary">
              Question {currentQuestion + 1} of {questions.length}
            </Badge>
          </div>
          <Progress value={progress} className="w-full" />
          <div className="flex justify-between text-sm text-muted-foreground">
            <span>Progress: {Math.round(progress)}%</span>
            <span>Answered: {answeredCount}/{questions.length}</span>
          </div>
        </CardHeader>
      </Card>

      {/* Question Navigation */}
      <Card>
        <CardContent className="p-4">
          <div className="flex flex-wrap gap-2">
            {questions.map((_, index) => (
              <Button
                key={index}
                variant={
                  index === currentQuestion 
                    ? "default" 
                    : answers[index] !== undefined 
                    ? "secondary" 
                    : "outline"
                }
                size="sm"
                onClick={() => handleQuestionJump(index)}
                className="w-10 h-10"
                data-testid={`question-nav-${index + 1}`}
              >
                {index + 1}
              </Button>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Current Question */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg">
            Question {currentQuestion + 1}
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          <p className="text-lg font-medium">{currentQ.question}</p>

          <RadioGroup
            value={answers[currentQuestion]?.toString()}
            onValueChange={(value) => handleAnswerChange(parseInt(value))}
          >
            {currentQ.options.map((option, index) => (
              <div key={index} className="flex items-center space-x-2 p-3 rounded border hover:bg-gray-50">
                <RadioGroupItem 
                  value={index.toString()} 
                  id={`option-${index}`}
                  data-testid={`answer-option-${String.fromCharCode(65 + index)}`}
                />
                <Label 
                  htmlFor={`option-${index}`} 
                  className="flex-1 cursor-pointer"
                >
                  <span className="font-medium mr-2">
                    {String.fromCharCode(65 + index)}.
                  </span>
                  {option}
                </Label>
              </div>
            ))}
          </RadioGroup>
        </CardContent>
      </Card>

      {/* Navigation Controls */}
      <Card>
        <CardContent className="p-4">
          <div className="flex justify-between items-center">
            <Button
              variant="outline"
              onClick={handlePrevious}
              disabled={currentQuestion === 0}
              data-testid="button-previous-question"
            >
              <ChevronLeft className="w-4 h-4 mr-2" />
              Previous
            </Button>

            <div className="flex gap-2">
              {currentQuestion < questions.length - 1 ? (
                <Button
                  onClick={handleNext}
                  data-testid="button-next-question"
                >
                  Next
                  <ChevronRight className="w-4 h-4 ml-2" />
                </Button>
              ) : (
                <Button
                  onClick={handleSubmit}
                  disabled={!allAnswered || submitMutation.isPending}
                  variant={allAnswered ? "default" : "secondary"}
                  data-testid="button-submit-quiz"
                >
                  {submitMutation.isPending ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      Submitting...
                    </>
                  ) : (
                    <>
                      Submit Quiz
                      <CheckCircle className="w-4 h-4 ml-2" />
                    </>
                  )}
                </Button>
              )}
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Answer Summary */}
      {answeredCount > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="text-sm font-medium">Answer Summary</CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-sm text-muted-foreground">
              {answeredCount} of {questions.length} questions answered
              {!allAnswered && (
                <span className="text-orange-600 ml-2">
                  ({questions.length - answeredCount} remaining)
                </span>
              )}
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/cart.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Trash2, Plus, Minus, ShoppingBag } from "lucide-react";
import { Link } from "wouter";
import { useToast } from "@/hooks/use-toast";

export default function Cart() {
  const [guestCartItems, setGuestCartItems] = useState<any[]>([]);
  const { toast } = useToast();

  const { data: user } = useQuery({
    queryKey: ["/api/auth/me"],
    retry: false,
  });

  const { data: apiCartItems } = useQuery({
    queryKey: ["/api/cart"],
    enabled: !!user,
    retry: false,
  });

  // Load guest cart from localStorage
  useEffect(() => {
    if (!user) {
      const guestCart = JSON.parse(localStorage.getItem("guest-cart") || "[]");
      setGuestCartItems(guestCart);
    }
  }, [user]);

  const cartItems = user ? (apiCartItems || []) : guestCartItems;

  const updateGuestCartQuantity = (productId: string, newQuantity: number) => {
    const updatedCart = guestCartItems.map(item => 
      item.productId === productId 
        ? { ...item, quantity: newQuantity }
        : item
    ).filter(item => item.quantity > 0);

    setGuestCartItems(updatedCart);
    localStorage.setItem("guest-cart", JSON.stringify(updatedCart));
    window.dispatchEvent(new Event('cartUpdated'));
  };

  const removeGuestCartItem = (productId: string) => {
    const updatedCart = guestCartItems.filter(item => item.productId !== productId);
    setGuestCartItems(updatedCart);
    localStorage.setItem("guest-cart", JSON.stringify(updatedCart));
    window.dispatchEvent(new Event('cartUpdated'));

    toast({
      title: "Item removed",
      description: "Product has been removed from your cart",
    });
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => {
      const price = parseFloat(item.product?.price || item.price || "0");
      return total + (price * item.quantity);
    }, 0).toFixed(2);
  };

  if (cartItems.length === 0) {
    return (
      <div className="min-h-screen bg-background py-8">
        <div className="container mx-auto px-4">
          <h1 className="text-3xl font-bold mb-8">Shopping Cart</h1>
          <Card className="text-center py-12">
            <CardContent>
              <ShoppingBag className="w-16 h-16 mx-auto mb-4 text-muted-foreground" />
              <h2 className="text-xl font-semibold mb-2">Your cart is empty</h2>
              <p className="text-muted-foreground mb-6">
                Add some products to get started
              </p>
              <Link href="/store">
                <Button>Continue Shopping</Button>
              </Link>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background py-8">
      <div className="container mx-auto px-4">
        <h1 className="text-3xl font-bold mb-8">Shopping Cart</h1>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Cart Items */}
          <div className="lg:col-span-2 space-y-4">
            {cartItems.map((item, index) => {
              const product = item.product || item;
              return (
                <Card key={item.productId || index}>
                  <CardContent className="p-6">
                    <div className="flex items-center space-x-4">
                      {/* Product Image */}
                      <div className="flex-shrink-0">
                        <img
                          src={product.image || "/placeholder-product.jpg"}
                          alt={product.title || product.name}
                          className="w-20 h-20 object-cover rounded-lg"
                        />
                      </div>

                      {/* Product Details */}
                      <div className="flex-1">
                        <h3 className="font-semibold text-lg">
                          {product.title || product.name}
                        </h3>
                        <p className="text-muted-foreground text-sm">
                          {product.category}
                        </p>
                        <div className="flex items-center justify-between mt-2">
                          <span className="text-lg font-bold text-primary">
                            ${parseFloat(product.price || "0").toFixed(2)}
                          </span>
                          {product.rating && (
                            <Badge variant="outline">
                              ⭐ {product.rating}
                            </Badge>
                          )}
                        </div>
                      </div>

                      {/* Quantity Controls */}
                      <div className="flex items-center space-x-2">
                        <Button
                          variant="outline"
                          size="icon"
                          onClick={() => 
                            user 
                              ? {} // TODO: API call for authenticated users
                              : updateGuestCartQuantity(item.productId, item.quantity - 1)
                          }
                          disabled={item.quantity <= 1}
                        >
                          <Minus className="h-4 w-4" />
                        </Button>
                        <span className="w-12 text-center font-medium">
                          {item.quantity}
                        </span>
                        <Button
                          variant="outline"
                          size="icon"
                          onClick={() => 
                            user 
                              ? {} // TODO: API call for authenticated users
                              : updateGuestCartQuantity(item.productId, item.quantity + 1)
                          }
                        >
                          <Plus className="h-4 w-4" />
                        </Button>
                      </div>

                      {/* Remove Button */}
                      <Button
                        variant="outline"
                        size="icon"
                        onClick={() => 
                          user 
                            ? {} // TODO: API call for authenticated users
                            : removeGuestCartItem(item.productId)
                        }
                        className="text-red-600 hover:text-red-700"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              );
            })}
          </div>

          {/* Order Summary */}
          <div className="lg:col-span-1">
            <Card className="sticky top-8">
              <CardHeader>
                <CardTitle>Order Summary</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex justify-between">
                  <span>Items ({cartItems.length})</span>
                  <span>${calculateTotal()}</span>
                </div>
                <div className="flex justify-between">
                  <span>Shipping</span>
                  <span className="text-green-600">Free</span>
                </div>
                <Separator />
                <div className="flex justify-between text-lg font-bold">
                  <span>Total</span>
                  <span>${calculateTotal()}</span>
                </div>

                {!user && (
                  <div className="bg-blue-50 p-3 rounded-lg">
                    <p className="text-sm text-blue-800 mb-2">
                      Sign in to save your cart and complete your purchase
                    </p>
                    <div className="space-y-2">
                      <Link href="/login">
                        <Button variant="outline" size="sm" className="w-full">
                          Sign In
                        </Button>
                      </Link>
                      <Link href="/register">
                        <Button size="sm" className="w-full">
                          Create Account
                        </Button>
                      </Link>
                    </div>
                  </div>
                )}

                <Button className="w-full" size="lg">
                  Proceed to Checkout
                </Button>

                <Link href="/store">
                  <Button variant="outline" className="w-full">
                    Continue Shopping
                  </Button>
                </Link>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/ai-photo-pricing.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { CheckCircle, Camera, FileText, Clock, Star, ArrowRight, Upload } from "lucide-react";
import { Link } from "wouter";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

export default function AIPhotoPricing() {
  const [isProcessing, setIsProcessing] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysis, setAnalysis] = useState<any>(null);
  const { toast } = useToast();

  // Check authentication and subscription status
  const { data: user } = useQuery({
    queryKey: ["/api/auth/me"],
    retry: false,
  });

  const { data: subscription } = useQuery({
    queryKey: ["/api/subscription-status"],
    enabled: !!user,
  });

  // Pay-per-use photo analysis with payment collection
  const analyzePhotoPayment = useMutation({
    mutationFn: async ({ file, paymentMethodId }: { file: File, paymentMethodId: string }) => {
      const formData = new FormData();
      formData.append('photo', file);
      formData.append('paymentMethodId', paymentMethodId);

      const response = await fetch("/api/photo-analysis/pay-per-use", {
        method: "POST",
        credentials: "include",
        body: formData,
      });
      if (!response.ok) throw new Error("Failed to process photo analysis");
      return response.json();
    },
    onSuccess: (data) => {
      setAnalysis(data);
      setIsAnalyzing(false);
      toast({
        title: "Analysis Complete",
        description: `Charged $${data.cost} for professional analysis.`,
      });
    },
    onError: (error: any) => {
      setIsAnalyzing(false);
      toast({
        title: "Analysis Failed",
        description: error.message || "Failed to analyze photo",
        variant: "destructive",
      });
    },
  });

  // Create payment intent for pay-per-use
  const createPaymentIntent = useMutation({
    mutationFn: async () => {
      const response = await fetch("/api/create-photo-payment-intent", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ amount: 499 }), // $4.99
      });
      if (!response.ok) throw new Error("Failed to create payment intent");
      return response.json();
    },
  });

  // Create payment session for photo analysis credits (bulk purchase)
  const purchasePhotoAnalysis = useMutation({
    mutationFn: async ({ credits, amount, planName }: { credits: number, amount: number, planName: string }) => {
      const response = await fetch("/api/create-photo-credits-payment", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ credits, amount, planName }),
      });
      if (!response.ok) throw new Error("Failed to create payment session");
      return response.json();
    },
    onSuccess: (data) => {
      if (data.url) {
        window.location.href = data.url;
      }
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to process payment",
        variant: "destructive",
      });
    },
  });

  const plans = [
    {
      name: "Starter Pack",
      credits: 10,
      price: 19.99,
      description: "Perfect for small projects",
      features: [
        "10 photo analyses",
        "Code compliance checking",
        "Installation review",
        "Detailed reports",
        "Email support",
      ],
      popular: false,
    },
    {
      name: "Professional",
      credits: 25,
      price: 39.99,
      description: "Most popular for contractors",
      features: [
        "25 photo analyses",
        "Code compliance checking", 
        "Installation review",
        "Detailed reports",
        "Priority support",
        "Save 20% per analysis",
      ],
      popular: true,
    },
    {
      name: "Business",
      credits: 50,
      price: 69.99,
      description: "Best value for companies",
      features: [
        "50 photo analyses",
        "Code compliance checking",
        "Installation review", 
        "Detailed reports",
        "Priority support",
        "Save 30% per analysis",
        "Bulk processing",
      ],
      popular: false,
    },
  ];

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      if (file.size > 10 * 1024 * 1024) { // 10MB limit
        toast({
          title: "File too large",
          description: "Please choose a file smaller than 10MB.",
          variant: "destructive",
        });
        return;
      }
      setSelectedFile(file);
      setAnalysis(null);
    }
  };

  const handlePayPerUseAnalysis = async () => {
    if (!selectedFile) return;

    if (!user) {
      toast({
        title: "Sign in required",
        description: "Please sign in to use photo analysis",
      });
      return;
    }

    setIsAnalyzing(true);

    try {
      // Create payment intent
      const paymentData = await createPaymentIntent.mutateAsync();

      // Redirect to Stripe Checkout for payment
      window.location.href = paymentData.url;
    } catch (error: any) {
      setIsAnalyzing(false);
      toast({
        title: "Payment Error",
        description: error.message || "Failed to initialize payment",
        variant: "destructive",
      });
    }
  };

  const handlePurchase = (plan: any) => {
    if (!user) {
      toast({
        title: "Sign in required",
        description: "Please sign in to purchase photo analysis credits",
      });
      return;
    }

    setIsProcessing(true);
    purchasePhotoAnalysis.mutate({ 
      credits: plan.credits, 
      amount: plan.price,
      planName: plan.name
    });
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* Header */}
        <div className="text-center mb-16">
          <div className="flex items-center justify-center mb-4">
            <Camera className="h-12 w-12 text-blue-600 mr-4" />
            <h1 className="text-4xl font-bold text-gray-900 dark:text-white">
              AI Photo Analysis
            </h1>
          </div>
          <p className="text-xl text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            Get instant code compliance checking and professional installation reviews using our advanced AI technology
          </p>
        </div>

        {/* Pay-Per-Use Analysis Tool */}
        <Card className="mb-12 bg-gradient-to-r from-blue-50 to-indigo-50 border-blue-200">
          <CardHeader className="text-center">
            <CardTitle className="text-2xl text-blue-900">Try Analysis Now - Pay Per Use</CardTitle>
            <CardDescription className="text-blue-700">
              $4.99 per photo analysis - Only pay when you use it
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {/* Upload Section */}
              <div className="space-y-4">
                <h3 className="text-lg font-semibold">Upload Photo</h3>
                <div className="border-2 border-dashed border-blue-300 rounded-lg p-8 text-center bg-white">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handleFileSelect}
                    className="hidden"
                    id="photo-upload"
                  />
                  <label
                    htmlFor="photo-upload"
                    className="cursor-pointer flex flex-col items-center"
                  >
                    <Camera className="h-12 w-12 text-blue-400 mb-4" />
                    <span className="text-lg font-medium text-blue-700 mb-2">
                      Choose photo to analyze
                    </span>
                    <span className="text-sm text-blue-600">
                      Max 10MB • JPG, PNG, HEIC
                    </span>
                  </label>
                </div>

                {selectedFile && (
                  <div className="bg-green-50 p-4 rounded-lg border border-green-200">
                    <div className="flex items-center">
                      <CheckCircle className="h-5 w-5 text-green-600 mr-2" />
                      <span className="font-medium text-green-800">
                        {selectedFile.name}
                      </span>
                    </div>
                  </div>
                )}

                <Button
                  onClick={handlePayPerUseAnalysis}
                  disabled={!selectedFile || isAnalyzing}
                  className="w-full"
                  size="lg"
                >
                  {isAnalyzing ? (
                    <>
                      <Clock className="h-4 w-4 mr-2 animate-spin" />
                      Analyzing...
                    </>
                  ) : (
                    <>
                      <Camera className="h-4 w-4 mr-2" />
                      Analyze Photo ($4.99)
                    </>
                  )}
                </Button>
              </div>

              {/* Results Section */}
              <div className="space-y-4">
                <h3 className="text-lg font-semibold">Analysis Results</h3>
                <div className="bg-white rounded-lg p-6 border border-gray-200 min-h-[200px]">
                  {!analysis ? (
                    <div className="text-center py-8 text-gray-500">
                      Upload and analyze a photo to see professional results
                    </div>
                  ) : (
                    <div className="space-y-4">
                      <div className={`p-4 rounded-lg ${analysis.isCompliant ? 'bg-green-50' : 'bg-yellow-50'}`}>
                        <div className="flex items-center mb-2">
