        credentials: {
          username,
          password: tempPassword
        },
        user: {
          id: newUser.id,
          email: newUser.email,
          firstName: newUser.firstName
        }
      });
    } catch (error: any) {
      console.error('Error processing payment success:', error);
      res.status(500).json({ message: "Error processing payment: " + error.message });
