        case 'plan-analysis':
          result = await analyzePlans(data.planData);
          break;
        case 'mentor-question':
          result = await getMentorResponse(data.question);
          break;
        default:
          return res.status(400).json({ message: "Unknown service type" });
      }

      res.json({
        service,
        result,
        paymentIntentId
      });
