});

export const insertEmployerSchema = createInsertSchema(employers).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertJobSchema = createInsertSchema(jobs).omit({
  id: true,
  status: true,
  approvedBy: true,
  approvedAt: true,
  createdAt: true,
  updatedAt: true,
});

export const insertJobApplicationSchema = createInsertSchema(jobApplications).omit({
  id: true,
  appliedAt: true,
});

export const insertCourseContentSchema = createInsertSchema(courseContent).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertPrivateCodeBookSchema = createInsertSchema(privateCodeBooks).omit({
  id: true,
  createdAt: true,
});

export const insertChatAnswerSchema = createInsertSchema(chatAnswers).omit({
  id: true,
  createdAt: true,
});

export const insertStudySessionSchema = createInsertSchema(studySessions).omit({
  id: true,
  createdAt: true,
});

export const insertQuizAttemptSchema = createInsertSchema(quizAttempts).omit({
  id: true,
  completedAt: true,
  createdAt: true,
});

export const insertSectionProgressSchema = createInsertSchema(sectionProgress).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertProductSchema = createInsertSchema(products).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertCartItemSchema = createInsertSchema(cartItems).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertProductReviewSchema = createInsertSchema(productReviews).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertReferralSchema = createInsertSchema(referrals).omit({
  id: true,
  createdAt: true,
  paidAt: true,
});

export const insertMonthlyCommissionSchema = createInsertSchema(monthlyCommissions).omit({
  id: true,
  createdAt: true,
  paidAt: true,
});

export const insertPayoutRequestSchema = createInsertSchema(payoutRequests).omit({
  id: true,
  requestedAt: true,
  processedAt: true,
  completedAt: true,
  createdAt: true,
});

export const insertAccountCreditTransactionSchema = createInsertSchema(accountCreditTransactions).omit({
  id: true,
  createdAt: true,
});

export const insertAchievementSchema = createInsertSchema(achievements).omit({
  id: true,
  createdAt: true,
});

export const insertUserAchievementSchema = createInsertSchema(userAchievements).omit({
  id: true,
  earnedAt: true,
});

export const insertMonthlyCompetitionSchema = createInsertSchema(monthlyCompetitions).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertCompetitionAttemptSchema = createInsertSchema(competitionAttempts).omit({
  id: true,
  startedAt: true,
  completedAt: true,
});

export const insertCompetitionQuestionSchema = createInsertSchema(competitionQuestions).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertUserPointsHistorySchema = createInsertSchema(userPointsHistory).omit({
  id: true,
  createdAt: true,
});

// Email campaigns
export const emailCampaigns = pgTable("email_campaigns", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  type: emailCampaignTypeEnum("type").notNull(),
  subject: text("subject").notNull(),
  content: text("content").notNull(),
  sequenceOrder: integer("sequence_order").notNull(),
  delayDays: integer("delay_days").notNull(),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
});

// Email queue for scheduled sending
export const emailQueue = pgTable("email_queue", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  recipientEmail: text("recipient_email").notNull(),
  recipientName: text("recipient_name"),
  campaignId: text("campaign_id").notNull(),
  campaignType: emailCampaignTypeEnum("campaign_type").notNull(),
  subject: text("subject").notNull(),
  content: text("content").notNull(),
  scheduledFor: timestamp("scheduled_for").notNull(),
  status: emailStatusEnum("status").default("pending"),
  sentAt: timestamp("sent_at"),
  errorMessage: text("error_message"),
  createdAt: timestamp("created_at").defaultNow(),
});

// Email unsubscribes
export const emailUnsubscribes = pgTable("email_unsubscribes", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: text("email").notNull().unique(),
  campaignType: emailCampaignTypeEnum("campaign_type"),
  unsubscribedAt: timestamp("unsubscribed_at").defaultNow(),
});

// Bulk enrollment pricing tiers
export const bulkEnrollmentTiers = pgTable("bulk_enrollment_tiers", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  tierName: text("tier_name").notNull(), // e.g., "Small Team", "Medium Team", "Large Company"
  minStudents: integer("min_students").notNull(),
  maxStudents: integer("max_students"), // null for unlimited
  discountPercent: decimal("discount_percent", { precision: 5, scale: 2 }).notNull(), // e.g., 15.00 for 15%
  basePrice: decimal("base_price", { precision: 10, scale: 2 }).notNull(), // price per student
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
});

// Bulk enrollment requests from employers
export const bulkEnrollmentRequests = pgTable("bulk_enrollment_requests", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  employerId: text("employer_id").notNull(),
  studentCount: integer("student_count").notNull(),
  courseIds: text("course_ids").array().notNull(), // Array of course IDs
  totalPrice: decimal("total_price", { precision: 10, scale: 2 }).notNull(),
  discountPercent: decimal("discount_percent", { precision: 5, scale: 2 }).notNull(),
  finalPrice: decimal("final_price", { precision: 10, scale: 2 }).notNull(),
  status: bulkEnrollmentStatusEnum("status").default("pending"),
  notes: text("notes"), // Additional requirements or notes
  contactEmail: text("contact_email").notNull(),
  contactPhone: text("contact_phone"),
  requestedStartDate: timestamp("requested_start_date"),
  approvedBy: text("approved_by"),
  approvedAt: timestamp("approved_at"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// Individual student enrollments within bulk requests
export const bulkStudentEnrollments = pgTable("bulk_student_enrollments", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  bulkRequestId: text("bulk_request_id").notNull(),
  studentEmail: text("student_email").notNull(),
  studentFirstName: text("student_first_name").notNull(),
  studentLastName: text("student_last_name"),
  userId: text("user_id"), // Set when student creates account
  isActivated: boolean("is_activated").default(false),
  activatedAt: timestamp("activated_at"),
  inviteSentAt: timestamp("invite_sent_at"),
  createdAt: timestamp("created_at").defaultNow(),
});

// Lead magnet downloads table (contractor-focused)
export const leadMagnetDownloads = pgTable("lead_magnet_downloads", {
  id: serial("id").primaryKey(),
  email: varchar("email", { length: 255 }).notNull(),
  firstName: varchar("first_name", { length: 100 }).notNull(),
  lastName: varchar("last_name", { length: 100 }).notNull(),
  companyName: varchar("company_name", { length: 255 }).notNull(),
  position: varchar("position", { length: 100 }),
  leadSource: leadSourceEnum("lead_source").default("website"),
  downloadedAt: timestamp("downloaded_at").defaultNow(),
  emailSent: boolean("email_sent").default(false),
});

// Student lead magnet downloads table (student-focused)
export const studentLeadMagnetDownloads = pgTable("student_lead_magnet_downloads", {
  id: serial("id").primaryKey(),
  email: varchar("email", { length: 255 }).notNull(),
  firstName: varchar("first_name", { length: 100 }).notNull(),
  lastName: varchar("last_name", { length: 100 }).notNull(),
  currentLevel: varchar("current_level", { length: 100 }).notNull(),
  goals: text("goals"),
  leadSource: leadSourceEnum("lead_source").default("website"),
  downloadedAt: timestamp("downloaded_at").defaultNow(),
  emailSent: boolean("email_sent").default(false),
});

// Competition notifications table
export const competitionNotifications = pgTable("competition_notifications", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  competitionId: text("competition_id").notNull(),
  userId: text("user_id").notNull(),
  notificationType: text("notification_type").notNull(), // "advance_notice", "day_of", "results"
  channel: text("channel").notNull(), // "email", "dashboard", "in_app"
  title: text("title").notNull(),
  message: text("message").notNull(),
  sentAt: timestamp("sent_at"),
  readAt: timestamp("read_at"),
  isRead: boolean("is_read").default(false),
  createdAt: timestamp("created_at").defaultNow(),
});

// Beta feedback system
export const betaFeedbackQuestions = pgTable("beta_feedback_questions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  questionText: text("question_text").notNull(),
  questionType: text("question_type").notNull(), // text, rating, multiple_choice, boolean
  options: jsonb("options"), // For multiple choice questions
  isRequired: boolean("is_required").default(true),
  displayOrder: integer("display_order").notNull(),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
});

export const betaFeedbackCampaigns = pgTable("beta_feedback_campaigns", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description"),
  monthYear: text("month_year").notNull(), // Format: "2025-01" 
  emailSentAt: timestamp("email_sent_at"),
  dueDate: timestamp("due_date").notNull(),
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").defaultNow(),
});

export const betaFeedbackResponses = pgTable("beta_feedback_responses", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  campaignId: text("campaign_id").notNull(),
  userId: text("user_id").notNull(),
  responses: jsonb("responses").notNull(), // {"questionId": "answer"}
  completedAt: timestamp("completed_at").defaultNow(),
  submissionToken: text("submission_token").unique(), // For email link access
});

// Create insert schemas
export const insertEmailCampaignSchema = createInsertSchema(emailCampaigns);
export const insertEmailQueueSchema = createInsertSchema(emailQueue);
export const insertEmailUnsubscribeSchema = createInsertSchema(emailUnsubscribes);
export const insertBulkEnrollmentTierSchema = createInsertSchema(bulkEnrollmentTiers);
export const insertBulkEnrollmentRequestSchema = createInsertSchema(bulkEnrollmentRequests);
export const insertBulkStudentEnrollmentSchema = createInsertSchema(bulkStudentEnrollments);
export const insertLeadMagnetDownloadSchema = createInsertSchema(leadMagnetDownloads).omit({
  id: true,
  downloadedAt: true,
  emailSent: true
});
export const insertStudentLeadMagnetDownloadSchema = createInsertSchema(studentLeadMagnetDownloads).omit({
  id: true,
  downloadedAt: true,
  emailSent: true
});
export const insertBetaFeedbackQuestionSchema = createInsertSchema(betaFeedbackQuestions).omit({
  id: true,
  createdAt: true
});
export const insertBetaFeedbackCampaignSchema = createInsertSchema(betaFeedbackCampaigns).omit({
  id: true,
  createdAt: true
});
export const insertBetaFeedbackResponseSchema = createInsertSchema(betaFeedbackResponses).omit({
  id: true,
  completedAt: true
});

// Types
export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;
export type Course = typeof courses.$inferSelect;
export type InsertCourse = z.infer<typeof insertCourseSchema>;
export type Job = typeof jobs.$inferSelect;
