import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { 
  Users, 
  Eye, 
  Download, 
  Mail, 
  Calendar,
  TrendingUp,
  FileText,
  Clock,
  CheckCircle,
  UserCheck
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import type { Job, JobApplication } from "@/../../shared/schema";

interface JobWithApplications extends Job {
  applications: (JobApplication & {
    user: {
      firstName: string;
      lastName: string;
      email: string;
      phone?: string;
    };
  })[];
}

export default function JobAnalytics() {
  const [selectedJobId, setSelectedJobId] = useState<string>("");
  const { toast } = useToast();

  // This would typically require authentication to get employer's jobs
  const { data: employerJobs = [], isLoading } = useQuery<JobWithApplications[]>({
    queryKey: ["/api/employer/jobs"],
    enabled: false, // Disable for now since we need employer auth
  });

  const selectedJob = employerJobs.find(job => job.id === selectedJobId);

  const getApplicationStats = (applications: JobApplication[]) => {
    const total = applications.length;
    const pending = applications.filter(app => app.status === "pending").length;
    const reviewed = applications.filter(app => app.status === "reviewed").length;
    const hired = applications.filter(app => app.status === "hired").length;

    return { total, pending, reviewed, hired };
  };

  const handleDownloadResume = (resumeUrl: string, applicantName: string) => {
    if (!resumeUrl) {
      toast({
        title: "No Resume Available",
        description: "This applicant hasn't uploaded a resume yet.",
        variant: "destructive",
      });
      return;
    }

    // Create download link
    const link = document.createElement('a');
    link.href = resumeUrl;
    link.download = `${applicantName.replace(/\s+/g, '_')}_resume.pdf`;
    link.click();

    toast({
      title: "Resume Downloaded",
      description: `Downloaded resume for ${applicantName}`,
    });
  };

  const formatDate = (date: Date | null) => {
    if (!date) return "Unknown";
    return new Date(date).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background p-8">
        <div className="max-w-6xl mx-auto">
          <div className="flex items-center justify-center py-16">
            <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <div className="bg-white border-b">
        <div className="max-w-6xl mx-auto px-4 py-6">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold" data-testid="analytics-title">
                Job Application Analytics
              </h1>
              <p className="text-muted-foreground mt-1">
                Track applications and download resumes from apprentice plumbers
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-6xl mx-auto px-4 py-8">
        {employerJobs.length === 0 ? (
          <Card>
            <CardContent className="py-16 text-center">
              <Users className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-xl font-semibold mb-2">No Job Postings Yet</h3>
              <p className="text-muted-foreground mb-6">
                Create your first job posting to start receiving applications from apprentice plumbers.
              </p>
              <Button onClick={() => window.location.href = "/employer-portal"}>
                Post Your First Job
              </Button>
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-6">
            {/* Job Selector */}
            <Card>
              <CardHeader>
                <CardTitle>Select Job Posting</CardTitle>
              </CardHeader>
              <CardContent>
                <Select value={selectedJobId} onValueChange={setSelectedJobId}>
                  <SelectTrigger className="w-full max-w-md">
                    <SelectValue placeholder="Choose a job posting to view analytics" />
                  </SelectTrigger>
                  <SelectContent>
                    {employerJobs.map((job) => (
                      <SelectItem key={job.id} value={job.id}>
                        {job.title} - {job.applications.length} applications
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </CardContent>
            </Card>

            {selectedJob && (
              <div className="space-y-6">
                {/* Analytics Overview */}
                <div className="grid md:grid-cols-4 gap-6">
                  {(() => {
                    const stats = getApplicationStats(selectedJob.applications);
                    return (
                      <>
                        <Card>
                          <CardContent className="p-6">
                            <div className="flex items-center space-x-2">
                              <Users className="w-8 h-8 text-blue-500" />
                              <div>
                                <p className="text-2xl font-bold">{stats.total}</p>
                                <p className="text-sm text-muted-foreground">Total Applications</p>
                              </div>
                            </div>
                          </CardContent>
                        </Card>

                        <Card>
                          <CardContent className="p-6">
                            <div className="flex items-center space-x-2">
                              <Clock className="w-8 h-8 text-orange-500" />
                              <div>
                                <p className="text-2xl font-bold">{stats.pending}</p>
                                <p className="text-sm text-muted-foreground">Pending Review</p>
                              </div>
                            </div>
                          </CardContent>
                        </Card>

                        <Card>
                          <CardContent className="p-6">
                            <div className="flex items-center space-x-2">
                              <Eye className="w-8 h-8 text-blue-500" />
                              <div>
                                <p className="text-2xl font-bold">{stats.reviewed}</p>
                                <p className="text-sm text-muted-foreground">Reviewed</p>
                              </div>
                            </div>
                          </CardContent>
                        </Card>

                        <Card>
                          <CardContent className="p-6">
                            <div className="flex items-center space-x-2">
                              <UserCheck className="w-8 h-8 text-green-500" />
                              <div>
                                <p className="text-2xl font-bold">{stats.hired}</p>
                                <p className="text-sm text-muted-foreground">Hired</p>
                              </div>
                            </div>
                          </CardContent>
                        </Card>
                      </>
                    );
                  })()}
                </div>

                {/* Applications List */}
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center space-x-2">
                      <FileText className="w-5 h-5" />
                      <span>Applications for: {selectedJob.title}</span>
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    {selectedJob.applications.length === 0 ? (
                      <div className="text-center py-8">
                        <Users className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
                        <p className="text-muted-foreground">No applications yet for this job posting.</p>
                      </div>
                    ) : (
                      <div className="space-y-4">
                        {selectedJob.applications.map((application) => (
                          <div 
                            key={application.id} 
                            className="border rounded-lg p-4 hover:bg-muted/50 transition-colors"
                            data-testid={`application-${application.id}`}
                          >
                            <div className="flex items-start justify-between">
                              <div className="space-y-2 flex-1">
                                <div className="flex items-center space-x-3">
                                  <h4 className="font-semibold text-lg">
                                    {application.user.firstName} {application.user.lastName}
                                  </h4>
                                  <Badge 
                                    variant={
                                      application.status === 'hired' ? 'default' :
                                      application.status === 'reviewed' ? 'secondary' :
                                      'outline'
                                    }
                                  >
                                    {application.status}
                                  </Badge>
                                </div>

                                <div className="grid md:grid-cols-2 gap-4 text-sm text-muted-foreground">
                                  <div className="flex items-center space-x-2">
                                    <Mail className="w-4 h-4" />
                                    <span>{application.user.email}</span>
                                  </div>
                                  {application.user.phone && (
                                    <div className="flex items-center space-x-2">
                                      <span>📞</span>
                                      <span>{application.user.phone}</span>
                                    </div>
                                  )}
                                  <div className="flex items-center space-x-2">
                                    <Calendar className="w-4 h-4" />
                                    <span>Applied: {formatDate(application.appliedAt)}</span>
                                  </div>
                                </div>

                                {application.coverLetter && (
                                  <div className="mt-3">
                                    <p className="text-sm font-medium mb-1">Cover Letter:</p>
                                    <p className="text-sm text-muted-foreground bg-muted/50 p-3 rounded">
                                      {application.coverLetter}
                                    </p>
                                  </div>
                                )}
                              </div>

                              <div className="flex flex-col space-y-2 ml-4">
                                {application.resumeUrl ? (
                                  <Button
                                    size="sm"
                                    onClick={() => handleDownloadResume(
                                      application.resumeUrl!, 
                                      `${application.user.firstName}_${application.user.lastName}`
                                    )}
                                    data-testid={`download-resume-${application.id}`}
                                  >
                                    <Download className="w-4 h-4 mr-2" />
                                    Download Resume
                                  </Button>
                                ) : (
                                  <Badge variant="outline" className="text-xs">
                                    No Resume
                                  </Badge>
                                )}

                                <Button
                                  size="sm"
                                  variant="outline"
                                  onClick={() => window.location.href = `mailto:${application.user.email}?subject=Re: ${selectedJob.title} Application`}
                                  data-testid={`contact-applicant-${application.id}`}
                                >
                                  <Mail className="w-4 h-4 mr-2" />
                                  Contact
                                </Button>
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>
                    )}
                  </CardContent>
                </Card>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

client/src/components/beta-banner.tsx.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent } from "@/components/ui/card";
import { Clock, Users, Zap, Star } from "lucide-react";
import { Link } from "wouter";

interface BetaBannerProps {
  className?: string;
  showCTA?: boolean;
}

export default function BetaBanner({ className = "", showCTA = true }: BetaBannerProps) {
  // Mock beta tester count - in real app this would come from API
  const [betaSignups, setBetaSignups] = useState(23); // Current beta signups
  const maxBetaSignups = 100;
  const remainingSpots = maxBetaSignups - betaSignups;

  // Timer for urgency effect (resets every 24 hours)
  const [timeLeft, setTimeLeft] = useState({
    hours: 23,
    minutes: 45,
    seconds: 30
  });

  useEffect(() => {
    const timer = setInterval(() => {
      setTimeLeft(prev => {
        if (prev.seconds > 0) {
          return { ...prev, seconds: prev.seconds - 1 };
        } else if (prev.minutes > 0) {
          return { ...prev, minutes: prev.minutes - 1, seconds: 59 };
        } else if (prev.hours > 0) {
          return { hours: prev.hours - 1, minutes: 59, seconds: 59 };
        }
        return { hours: 23, minutes: 59, seconds: 59 }; // Reset daily
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  // Simulate occasional signup updates for demo
  useEffect(() => {
    const signupTimer = setInterval(() => {
      if (Math.random() < 0.1 && betaSignups < maxBetaSignups) { // 10% chance every 30 seconds
        setBetaSignups(prev => Math.min(prev + 1, maxBetaSignups));
      }
    }, 30000);

    return () => clearInterval(signupTimer);
  }, [betaSignups, maxBetaSignups]);

  if (betaSignups >= maxBetaSignups) {
    return null; // Hide banner when beta is full
  }

  return (
    <Card className={`border-2 border-primary/20 bg-gradient-to-r from-primary/5 via-primary/10 to-primary/5 ${className}`} data-testid="beta-banner">
      <CardContent className="p-6">
        <div className="flex flex-col lg:flex-row items-center justify-between gap-4">
          {/* Left side - Beta info */}
          <div className="flex-1 text-center lg:text-left">
            <div className="flex items-center justify-center lg:justify-start gap-2 mb-2">
              <Badge className="bg-primary text-primary-foreground font-bold px-3 py-1">
                <Star className="w-4 h-4 mr-1" />
                BETA EXCLUSIVE
              </Badge>
              <Badge variant="outline" className="border-orange-500 text-orange-600 font-semibold">
                <Users className="w-4 h-4 mr-1" />
                {remainingSpots} spots left
              </Badge>
            </div>

            <h3 className="text-xl lg:text-2xl font-bold text-foreground mb-2" data-testid="beta-title">
              First 100 Beta Testers Get <span className="text-primary">Extra 25% Off</span>
            </h3>

            <div className="flex flex-wrap items-center justify-center lg:justify-start gap-4 text-sm text-muted-foreground">
              <div className="flex items-center gap-1">
                <Zap className="w-4 h-4 text-primary" />
                <span><strong>50% off first month</strong></span>
              </div>
              <div className="flex items-center gap-1">
                <Zap className="w-4 h-4 text-primary" />
                <span><strong>25% off for full year</strong></span>
              </div>
              <div className="flex items-center gap-1">
                <Zap className="w-4 h-4 text-primary" />
                <span><strong>Stacks with annual discount</strong></span>
              </div>
            </div>
          </div>

          {/* Middle - Progress & Timer */}
          <div className="flex flex-col items-center gap-3">
            {/* Progress bar */}
            <div className="w-48">
              <div className="flex justify-between text-xs text-muted-foreground mb-1">
                <span>{betaSignups} signed up</span>
                <span>{maxBetaSignups} total</span>
              </div>
              <div className="w-full bg-muted rounded-full h-2">
                <div 
                  className="bg-primary h-2 rounded-full transition-all duration-1000"
                  style={{ width: `${(betaSignups / maxBetaSignups) * 100}%` }}
                  data-testid="beta-progress"
                />
              </div>
            </div>

            {/* Countdown timer */}
            <div className="flex items-center gap-2 bg-background/50 rounded-lg px-3 py-2" data-testid="beta-timer">
              <Clock className="w-4 h-4 text-primary" />
              <div className="flex gap-1 font-mono text-sm">
                <span className="bg-primary text-primary-foreground px-2 py-1 rounded text-xs font-bold">
                  {timeLeft.hours.toString().padStart(2, '0')}
                </span>
                <span className="text-muted-foreground">:</span>
                <span className="bg-primary text-primary-foreground px-2 py-1 rounded text-xs font-bold">
                  {timeLeft.minutes.toString().padStart(2, '0')}
                </span>
                <span className="text-muted-foreground">:</span>
                <span className="bg-primary text-primary-foreground px-2 py-1 rounded text-xs font-bold">
                  {timeLeft.seconds.toString().padStart(2, '0')}
                </span>
              </div>
            </div>
            <p className="text-xs text-muted-foreground text-center">
              Beta pricing expires soon
            </p>
          </div>

          {/* Right side - CTA */}
          {showCTA && (
            <div className="flex flex-col gap-2">
              <Link href="/pricing">
                <Button size="lg" className="bg-primary hover:bg-primary/90 text-primary-foreground font-bold px-8" data-testid="beta-cta-button">
                  Claim Beta Discount
                </Button>
              </Link>
              <p className="text-xs text-muted-foreground text-center">
                No commitment • Cancel anytime
              </p>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/pages/referrals.tsx.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { useQuery } from "@tanstack/react-query";
import { DollarSign, Users, Copy, Share2, TrendingUp, Info, MessageSquare, Instagram, Facebook, Twitter, Settings } from "lucide-react";
import { SiLinkedin, SiWhatsapp } from "react-icons/si";
import { Link } from "wouter";

interface ReferralStats {
  referralCode: string;
  planTier: "basic" | "professional" | "master";
  totalReferrals: number;
  earnings: {
    total: number;
    unpaid: number;
    paid: number;
  };
  recentReferrals: any[];
}

interface CommissionPreview {
  referrerTier: string;
  commissionPreviews: {
    basic: { eligibleTier: string; eligiblePrice: number; commissionAmount: number };
    professional: { eligibleTier: string; eligiblePrice: number; commissionAmount: number };
    master: { eligibleTier: string; eligiblePrice: number; commissionAmount: number };
  };
  note: string;
}

export default function Referrals() {
  const { toast } = useToast();
  const [referralUrl, setReferralUrl] = useState("");

  // Fetch referral stats
  const { data: stats, isLoading: statsLoading } = useQuery<ReferralStats>({
    queryKey: ["/api/referrals/stats"],
  });

  // Fetch commission preview
  const { data: preview, isLoading: previewLoading } = useQuery<CommissionPreview>({
    queryKey: ["/api/referrals/commission-preview"],
  });

  // Fetch monthly earnings summary
  const { data: monthlyEarnings, isLoading: monthlyLoading } = useQuery({
    queryKey: ["/api/referrals/monthly-earnings-summary"],
  });

  useEffect(() => {
    if (stats?.referralCode) {
      setReferralUrl(`${window.location.origin}?ref=${stats.referralCode}`);
    }
  }, [stats]);

  const copyReferralUrl = async () => {
    try {
      await navigator.clipboard.writeText(referralUrl);
      toast({
        title: "Copied!",
        description: "Referral link copied to clipboard",
      });
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to copy referral link",
        variant: "destructive",
      });
    }
  };

  const shareReferralUrl = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: "LA Plumb Prep - Louisiana Plumbing Certification",
          text: "Join me on LA Plumb Prep for Louisiana plumbing certification prep!",
          url: referralUrl,
        });
      } catch (error) {
        // User cancelled or error occurred
      }
    } else {
      copyReferralUrl();
    }
  };

  const getTierBadgeColor = (tier: string) => {
    switch (tier) {
      case "basic":
        return "bg-blue-100 text-blue-800";
      case "professional":
        return "bg-purple-100 text-purple-800";
      case "master":
        return "bg-orange-100 text-orange-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  const getTierDisplayName = (tier: string) => {
    return tier.charAt(0).toUpperCase() + tier.slice(1);
  };

  // Social media post templates
  const getPostTemplates = (referralCode: string, referralUrl: string) => [
    {
      id: "success-story",
      title: "Success Story",
      platform: "General",
      icon: MessageSquare,
      text: `🔧 Just passed my Louisiana plumbing exam with LA Plumb Prep! Their practice tests and AI mentor were game-changers. \n\nIf you're studying for your Louisiana plumbing certification, check them out: ${referralUrl} \n\n#LouisianaPlumber #PlumbingCertification #StudySuccess`,
      hashtags: "#LouisianaPlumber #PlumbingCertification #StudySuccess"
    },
    {
      id: "professional-tip",
      title: "Professional Tip",
      platform: "LinkedIn",
      icon: MessageSquare,
      text: `💡 Pro tip for Louisiana plumbers: LA Plumb Prep's AI-powered tools have revolutionized how I prepare for certifications and handle code compliance on job sites.\n\nTheir platform covers everything from Journeyman prep to specialized certifications. Worth checking out: ${referralUrl}\n\n#PlumbingProfessional #LouisianaPlumbing #ContinuingEducation`,
      hashtags: "#PlumbingProfessional #LouisianaPlumbing #ContinuingEducation"
    },
    {
      id: "recommendation",
      title: "Colleague Recommendation",
      platform: "Facebook",
      icon: MessageSquare,
      text: `🚰 Fellow plumbers! I've been using LA Plumb Prep for my Louisiana certifications and it's been incredible. The practice tests mirror the real exams perfectly, and their AI mentor answers any code questions instantly.\n\nDefinitely recommend checking it out if you're serious about advancing your plumbing career: ${referralUrl}`,
      hashtags: ""
    },
    {
      id: "quick-tip",
      title: "Quick Study Tip",
      platform: "Twitter/X",
      icon: MessageSquare,
      text: `🔧 Louisiana plumbers: LA Plumb Prep's AI mentor is like having a master plumber available 24/7 for code questions! Game changer for exam prep 💯\n\n${referralUrl}\n\n#PlumbingLife #LouisianaPlumber #ExamPrep`,
      hashtags: "#PlumbingLife #LouisianaPlumber #ExamPrep"
    }
  ];

  const shareToSocialMedia = (template: any, platform: string) => {
    const baseUrls = {
      facebook: 'https://www.facebook.com/sharer/sharer.php?u=',
      twitter: 'https://twitter.com/intent/tweet?text=',
      linkedin: 'https://www.linkedin.com/sharing/share-offsite/?url=',
      whatsapp: 'https://wa.me/?text='
    };

    let shareUrl = '';

    switch (platform.toLowerCase()) {
      case 'facebook':
        shareUrl = `${baseUrls.facebook}${encodeURIComponent(referralUrl)}&quote=${encodeURIComponent(template.text)}`;
        break;
      case 'twitter':
        shareUrl = `${baseUrls.twitter}${encodeURIComponent(template.text)}`;
        break;
      case 'linkedin':
        shareUrl = `${baseUrls.linkedin}${encodeURIComponent(referralUrl)}&summary=${encodeURIComponent(template.text)}`;
        break;
      case 'whatsapp':
        shareUrl = `${baseUrls.whatsapp}${encodeURIComponent(template.text)}`;
        break;
      default:
        // Copy to clipboard as fallback
        navigator.clipboard.writeText(template.text);
        toast({
          title: "Copied!",
          description: "Post template copied to clipboard",
        });
        return;
    }

    window.open(shareUrl, '_blank', 'width=600,height=400');
  };

  const copyTemplate = (text: string) => {
    navigator.clipboard.writeText(text);
    toast({
      title: "Copied!",
      description: "Post template copied to clipboard",
    });
  };

  if (statsLoading || previewLoading || monthlyLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <section className="py-12 bg-gradient-to-br from-primary/10 to-accent/10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h1 className="text-4xl lg:text-5xl font-bold text-foreground mb-4" data-testid="referral-header">
              Referral Program
            </h1>
            <p className="text-xl text-muted-foreground max-w-3xl mx-auto">
              Earn money by referring plumbers to LA Plumb Prep. Commission is capped at your plan tier or lower.
            </p>
          </div>
        </div>
      </section>

      {/* Main Content */}
      <section className="py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">

            {/* Stats Cards */}
            <div className="lg:col-span-2 space-y-6">

              {/* Overview Stats */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <Card>
                  <CardContent className="p-6">
                    <div className="flex items-center">
                      <DollarSign className="h-8 w-8 text-green-600" />
                      <div className="ml-4">
                        <p className="text-sm font-medium text-muted-foreground">Total Earnings</p>
                        <p className="text-2xl font-bold text-foreground" data-testid="total-earnings">
                          ${stats?.earnings.total.toFixed(2) || "0.00"}
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="p-6">
                    <div className="flex items-center">
                      <Users className="h-8 w-8 text-blue-600" />
                      <div className="ml-4">
                        <p className="text-sm font-medium text-muted-foreground">Total Referrals</p>
                        <p className="text-2xl font-bold text-foreground" data-testid="total-referrals">
                          {stats?.totalReferrals || 0}
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>

                <Card>
                  <CardContent className="p-6">
                    <div className="flex items-center">
                      <TrendingUp className="h-8 w-8 text-purple-600" />
                      <div className="ml-4">
                        <p className="text-sm font-medium text-muted-foreground">Unpaid</p>
                        <p className="text-2xl font-bold text-foreground" data-testid="unpaid-earnings">
                          ${stats?.earnings.unpaid.toFixed(2) || "0.00"}
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </div>

              {/* Commission Rates */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Info className="h-5 w-5" />
                    Your Commission Rates
                    <Badge className={getTierBadgeColor(stats?.planTier || "basic")}>
                      {getTierDisplayName(stats?.planTier || "basic")} Plan
                    </Badge>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="text-sm text-muted-foreground mb-4">
                      {preview?.note}
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      {preview && Object.entries(preview.commissionPreviews).map(([tier, commission]) => (
                        <div key={tier} className="p-4 border rounded-lg">
                          <div className="flex justify-between items-center mb-2">
                            <span className="font-medium">{getTierDisplayName(tier)}</span>
                            <Badge variant="outline" className="text-xs">
                              ${commission.commissionAmount.toFixed(2)}
                            </Badge>
                          </div>
                          <div className="text-sm text-muted-foreground">
                            10% of ${commission.eligiblePrice}/month
                          </div>
                          {commission.eligibleTier !== tier && (
                            <div className="text-xs text-orange-600 mt-1">
                              Capped at {getTierDisplayName(commission.eligibleTier)} tier
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Monthly Recurring Earnings */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <TrendingUp className="h-5 w-5 text-green-600" />
                    Monthly Recurring Earnings
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <div className="p-4 bg-green-50 rounded-lg">
                        <div className="text-sm text-muted-foreground">Total Monthly</div>
                        <div className="text-2xl font-bold text-green-600">
                          ${monthlyEarnings?.totalMonthlyEarnings?.toFixed(2) || "0.00"}
                        </div>
                      </div>
                      <div className="p-4 bg-orange-50 rounded-lg">
                        <div className="text-sm text-muted-foreground">Unpaid Monthly</div>
                        <div className="text-2xl font-bold text-orange-600">
                          ${monthlyEarnings?.unpaidMonthlyEarnings?.toFixed(2) || "0.00"}
                        </div>
                      </div>
                    </div>

                    {monthlyEarnings?.monthlyBreakdown && monthlyEarnings.monthlyBreakdown.length > 0 ? (
                      <div className="space-y-3">
                        <div className="text-sm font-medium text-muted-foreground">Monthly Breakdown:</div>
                        {monthlyEarnings.monthlyBreakdown.slice(0, 6).map((month: any) => (
                          <div key={month.month} className="flex justify-between items-center p-3 border rounded-lg">
                            <div>
                              <div className="font-medium">
                                {new Date(month.month + '-01').toLocaleDateString('en-US', { 
                                  year: 'numeric', 
                                  month: 'long' 
                                })}
                              </div>
                              <div className="text-sm text-muted-foreground">
                                {month.count} commission{month.count !== 1 ? 's' : ''}
                              </div>
                            </div>
                            <div className="text-right">
                              <div className="font-bold text-green-600">
                                ${month.total.toFixed(2)}
                              </div>
                              <div className="text-xs text-muted-foreground">
                                {month.unpaid > 0 ? `$${month.unpaid.toFixed(2)} unpaid` : 'All paid'}
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>
                    ) : (
