import { useState, useEffect } from "react";
import { useForm, useFieldArray } from "react-hook-form";
import { z } from "zod";
import { zodResolver } from "@hookform/resolvers/zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Plus, Trash2, Calculator, Users, DollarSign, Percent } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

const studentSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().optional(),
});

const bulkEnrollmentSchema = z.object({
  employerId: z.string().min(1, "Employer ID is required"),
  contactEmail: z.string().email("Please enter a valid email address"),
  contactPhone: z.string().optional(),
  notes: z.string().optional(),
  requestedStartDate: z.string().optional(),
  students: z.array(studentSchema).min(1, "Minimum 1 student required for bulk enrollment"),
});

type BulkEnrollmentForm = z.infer<typeof bulkEnrollmentSchema>;

interface BulkPricingTier {
  id: string;
  tierName: string;
  minStudents: number;
  maxStudents: number | null;
  discountPercent: string;
  basePrice: string;
}

interface PricingCalculation {
  studentCount: number;
  basePricePerStudent: number;
  totalBasePrice: number;
  appliedTier: BulkPricingTier | null;
  discountPercent: number;
  discountAmount: number;
  finalPrice: number;
  pricePerStudent: number;
}

export default function BulkEnrollment() {
  const { toast } = useToast();
  const [pricingTiers, setPricingTiers] = useState<BulkPricingTier[]>([]);
  const [pricingCalculation, setPricingCalculation] = useState<PricingCalculation | null>(null);
  const [isCalculating, setIsCalculating] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const form = useForm<BulkEnrollmentForm>({
    resolver: zodResolver(bulkEnrollmentSchema),
    defaultValues: {
      employerId: "",
      contactEmail: "",
      contactPhone: "",
      notes: "",
      requestedStartDate: "",
      students: [
        { email: "", firstName: "", lastName: "" },
        { email: "", firstName: "", lastName: "" },
        { email: "", firstName: "", lastName: "" },
        { email: "", firstName: "", lastName: "" },
        { email: "", firstName: "", lastName: "" },
      ],
    },
  });

  const { fields, append, remove } = useFieldArray({
    control: form.control,
    name: "students",
  });

  // Load pricing tiers on component mount
  useEffect(() => {
    const fetchPricingTiers = async () => {
      try {
        const response = await apiRequest("GET", "/api/bulk-pricing/tiers");
        const data = await response.json();
        setPricingTiers(data.tiers);
      } catch (error) {
        console.error("Error fetching pricing tiers:", error);
      }
    };

    fetchPricingTiers();
  }, []);

  // Calculate pricing when student count changes
  useEffect(() => {
    const subscription = form.watch((value) => {
      const studentCount = value.students?.filter(s => s.email && s.firstName).length || 0;
      if (studentCount >= 1) {
        calculatePricing(studentCount);
      } else {
        setPricingCalculation(null);
      }
    });

    return () => subscription.unsubscribe();
  }, [form.watch]);

  const calculatePricing = async (studentCount: number) => {
    setIsCalculating(true);
    try {
      const response = await apiRequest("POST", "/api/bulk-pricing/calculate", {
        studentCount,
        courseIds: ["journeyman"]
      });
      const data = await response.json();
      setPricingCalculation(data.pricing);
    } catch (error) {
      console.error("Error calculating pricing:", error);
    } finally {
      setIsCalculating(false);
    }
  };

  const onSubmit = async (data: BulkEnrollmentForm) => {
    setIsSubmitting(true);
    try {
      const validStudents = data.students.filter(s => s.email && s.firstName);

      const response = await apiRequest("POST", "/api/bulk-enrollment/request", {
        ...data,
        studentEmails: validStudents,
        courseIds: ["journeyman"]
      });

      if (response.ok) {
        toast({
          title: "Request Submitted Successfully!",
          description: "We'll review your bulk enrollment request and get back to you within 24 hours.",
        });

        // Reset form
        form.reset();
        setPricingCalculation(null);
      } else {
        throw new Error("Failed to submit request");
      }
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to submit bulk enrollment request.",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const addStudent = () => {
    append({ email: "", firstName: "", lastName: "" });
  };

  const removeStudent = (index: number) => {
    if (fields.length > 1) {
      remove(index);
    }
  };

  const validStudentCount = form.watch("students")?.filter(s => s.email && s.firstName).length || 0;

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-8">
      <div className="text-center space-y-4">
        <h1 className="text-4xl font-bold text-foreground" data-testid="page-title">
          Bulk Student Enrollment
        </h1>
        <p className="text-xl text-muted-foreground max-w-2xl mx-auto">
          Enroll multiple apprentices or students at discounted rates. Perfect for construction companies, 
          plumbing contractors, and training programs.
        </p>
      </div>

      {/* Pricing Tiers Overview */}
      <div className="grid md:grid-cols-3 gap-4">
        {pricingTiers.map((tier) => (
          <Card key={tier.id} className="relative">
            <CardHeader className="text-center">
              <CardTitle className="text-lg">{tier.tierName}</CardTitle>
              <CardDescription>
                {tier.minStudents}
                {tier.maxStudents ? `-${tier.maxStudents}` : "+"} students
              </CardDescription>
            </CardHeader>
            <CardContent className="text-center space-y-2">
              <div className="text-2xl font-bold text-green-600">
                {tier.discountPercent}% OFF
              </div>
              <div className="text-sm text-muted-foreground">
                ${(49 * (1 - parseFloat(tier.discountPercent) / 100)).toFixed(0)}/student
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Pricing Calculator */}
      {pricingCalculation && (
        <Card className="bg-green-50 border-green-200">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Calculator className="w-5 h-5" />
              Your Bulk Pricing Quote
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid md:grid-cols-4 gap-4 text-center">
              <div>
                <div className="flex items-center justify-center gap-1 mb-2">
                  <Users className="w-4 h-4 text-blue-600" />
                  <span className="text-sm font-medium">Students</span>
                </div>
                <div className="text-2xl font-bold">{pricingCalculation.studentCount}</div>
              </div>
              <div>
                <div className="flex items-center justify-center gap-1 mb-2">
                  <Percent className="w-4 h-4 text-green-600" />
                  <span className="text-sm font-medium">Discount</span>
                </div>
                <div className="text-2xl font-bold text-green-600">
                  {pricingCalculation.discountPercent}%
                </div>
              </div>
              <div>
                <div className="flex items-center justify-center gap-1 mb-2">
                  <DollarSign className="w-4 h-4 text-orange-600" />
                  <span className="text-sm font-medium">You Save</span>
                </div>
                <div className="text-2xl font-bold text-orange-600">
                  ${pricingCalculation.discountAmount.toFixed(0)}
                </div>
              </div>
              <div>
                <div className="flex items-center justify-center gap-1 mb-2">
                  <DollarSign className="w-4 h-4 text-blue-600" />
                  <span className="text-sm font-medium">Total</span>
                </div>
                <div className="text-2xl font-bold text-blue-600">
                  ${pricingCalculation.finalPrice.toFixed(0)}
                </div>
              </div>
            </div>

            {pricingCalculation.appliedTier && (
              <div className="text-center">
                <Badge className="bg-green-100 text-green-800">
                  {pricingCalculation.appliedTier.tierName} - 
                  ${pricingCalculation.pricePerStudent.toFixed(0)} per student
                </Badge>
              </div>
            )}
          </CardContent>
        </Card>
      )}

      {/* Enrollment Form */}
      <Card>
        <CardHeader>
          <CardTitle>Bulk Enrollment Request</CardTitle>
          <CardDescription>
            Complete the form below to request bulk enrollment for your students.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
              {/* Company Information */}
              <div className="grid md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="employerId"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Company/Employer ID</FormLabel>
                      <FormControl>
                        <Input placeholder="Your company ID" {...field} data-testid="input-employer-id" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="contactEmail"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Contact Email</FormLabel>
                      <FormControl>
                        <Input type="email" placeholder="company@example.com" {...field} data-testid="input-contact-email" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <div className="grid md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="contactPhone"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Contact Phone (Optional)</FormLabel>
                      <FormControl>
                        <Input placeholder="(555) 123-4567" {...field} data-testid="input-contact-phone" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="requestedStartDate"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Requested Start Date (Optional)</FormLabel>
                      <FormControl>
                        <Input type="date" {...field} data-testid="input-start-date" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              {/* Student List */}
              <div className="space-y-4">
                <div className="flex justify-between items-center">
                  <h3 className="text-lg font-semibold">Student Information</h3>
                  <div className="text-sm text-muted-foreground">
                    {validStudentCount} valid students ({validStudentCount >= 5 ? "✓" : "⚠️"} Min: 5)
                  </div>
                </div>

                {fields.map((field, index) => (
                  <div key={field.id} className="grid md:grid-cols-4 gap-2 items-end">
                    <FormField
                      control={form.control}
                      name={`students.${index}.email`}
                      render={({ field }) => (
                        <FormItem>
                          {index === 0 && <FormLabel>Email</FormLabel>}
                          <FormControl>
                            <Input placeholder="student@email.com" {...field} data-testid={`input-student-email-${index}`} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name={`students.${index}.firstName`}
                      render={({ field }) => (
                        <FormItem>
                          {index === 0 && <FormLabel>First Name</FormLabel>}
                          <FormControl>
                            <Input placeholder="John" {...field} data-testid={`input-student-firstname-${index}`} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <FormField
                      control={form.control}
                      name={`students.${index}.lastName`}
                      render={({ field }) => (
                        <FormItem>
                          {index === 0 && <FormLabel>Last Name (Optional)</FormLabel>}
                          <FormControl>
                            <Input placeholder="Doe" {...field} data-testid={`input-student-lastname-${index}`} />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                    <div>
                      {fields.length > 5 && (
                        <Button
                          type="button"
                          variant="outline"
                          size="sm"
                          onClick={() => removeStudent(index)}
                          data-testid={`button-remove-student-${index}`}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      )}
                    </div>
                  </div>
                ))}

                <Button
                  type="button"
                  variant="outline"
                  onClick={addStudent}
                  className="w-full"
                  data-testid="button-add-student"
                >
                  <Plus className="w-4 h-4 mr-2" />
                  Add Another Student
                </Button>
              </div>

              {/* Notes */}
              <FormField
                control={form.control}
                name="notes"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Additional Notes (Optional)</FormLabel>
                    <FormControl>
                      <Textarea 
                        placeholder="Any special requirements or notes about your enrollment request..."
                        rows={3}
                        {...field}
                        data-testid="textarea-notes"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {/* Submit Button */}
              <Button
                type="submit"
                size="lg"
                className="w-full"
                disabled={isSubmitting || validStudentCount < 5}
                data-testid="button-submit-request"
              >
                {isSubmitting ? "Submitting Request..." : "Submit Bulk Enrollment Request"}
              </Button>
            </form>
          </Form>
        </CardContent>
      </Card>

      {/* Contact Information */}
      <Card className="bg-blue-50 border-blue-200">
        <CardContent className="pt-6">
          <div className="text-center space-y-2">
            <h3 className="font-semibold">Need Help?</h3>
            <p className="text-sm text-muted-foreground">
              Contact our team at <a href="mailto:bulk@laplumbprep.com" className="text-blue-600 hover:underline">bulk@laplumbprep.com</a> or 
              call <a href="tel:+15551234567" className="text-blue-600 hover:underline">(555) 123-4567</a> for assistance with bulk enrollments.
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

server/bulk-pricing.ts.content:
import { db } from "./db";
import { 
  bulkEnrollmentTiers, 
  bulkEnrollmentRequests, 
  bulkStudentEnrollments,
  courses,
  type BulkEnrollmentTier,
  type InsertBulkEnrollmentRequest,
  type InsertBulkStudentEnrollment
} from "@shared/schema";
import { eq, and, gte, lte, or, isNull } from "drizzle-orm";

export interface BulkPricingCalculation {
  studentCount: number;
  courseIds: string[];
  basePricePerStudent: number;
  totalBasePrice: number;
  appliedTier: BulkEnrollmentTier | null;
  discountPercent: number;
  discountAmount: number;
  finalPrice: number;
  pricePerStudent: number;
}

export interface StudentEnrollmentData {
  email: string;
  firstName: string;
  lastName?: string;
}

export class BulkPricingService {

  /**
   * Initialize default bulk enrollment tiers
   */
  async initializeBulkTiers() {
    try {
      // Check if tiers already exist
      const existingTiers = await db.select().from(bulkEnrollmentTiers).limit(1);
      if (existingTiers.length > 0) {
        return; // Already initialized
      }

      // Create single flat discount tier for all employer enrollments
      const defaultTiers = [
        {
          tierName: "Employer Bulk Discount",
          minStudents: 1,
          maxStudents: null, // No upper limit
          discountPercent: "25.00", // 25% discount for all employer enrollments
          basePrice: "49.00" // Base price per student
        }
      ];

      await db.insert(bulkEnrollmentTiers).values(defaultTiers);
      console.log("Bulk enrollment tiers initialized");
    } catch (error) {
      console.error("Error initializing bulk enrollment tiers:", error);
    }
  }

  /**
   * Calculate bulk pricing for a given number of students
   */
  async calculateBulkPricing(
    studentCount: number, 
    courseIds: string[] = ["journeyman"] // Default to journeyman course
  ): Promise<BulkPricingCalculation> {

    // Get available pricing tiers
    const tiers = await db
      .select()
      .from(bulkEnrollmentTiers)
      .where(eq(bulkEnrollmentTiers.isActive, true));

    // Find applicable tier
    const applicableTier = tiers.find(tier => {
      const meetsMin = studentCount >= tier.minStudents;
      const meetsMax = tier.maxStudents === null || studentCount <= tier.maxStudents;
      return meetsMin && meetsMax;
    });

    // Base pricing calculation
    const basePricePerStudent = 49; // Basic tier price
    const totalBasePrice = basePricePerStudent * studentCount * courseIds.length;

    // Apply bulk discount if applicable
    const discountPercent = applicableTier ? parseFloat(applicableTier.discountPercent) : 0;
    const discountAmount = totalBasePrice * (discountPercent / 100);
    const finalPrice = totalBasePrice - discountAmount;
    const pricePerStudent = finalPrice / studentCount;

    return {
      studentCount,
      courseIds,
      basePricePerStudent,
      totalBasePrice,
      appliedTier: applicableTier || null,
      discountPercent,
      discountAmount,
      finalPrice,
      pricePerStudent
    };
  }

  /**
   * Get all active bulk pricing tiers
   */
  async getBulkPricingTiers() {
    return await db
      .select()
      .from(bulkEnrollmentTiers)
      .where(eq(bulkEnrollmentTiers.isActive, true));
  }

  /**
   * Create a bulk enrollment request
   */
  async createBulkEnrollmentRequest(
    employerId: string,
    studentEmails: StudentEnrollmentData[],
    courseIds: string[],
    contactEmail: string,
    contactPhone?: string,
    notes?: string,
    requestedStartDate?: Date
  ) {
    try {
      const studentCount = studentEmails.length;

      // Calculate pricing
      const pricing = await this.calculateBulkPricing(studentCount, courseIds);

      // Create bulk enrollment request
      const [bulkRequest] = await db
        .insert(bulkEnrollmentRequests)
        .values({
          employerId,
          studentCount,
          courseIds,
          totalPrice: pricing.totalBasePrice.toString(),
          discountPercent: pricing.discountPercent.toString(),
          finalPrice: pricing.finalPrice.toString(),
          contactEmail,
          contactPhone,
          notes,
          requestedStartDate,
        })
        .returning();

      // Create individual student enrollment records
      const studentEnrollments = studentEmails.map(student => ({
        bulkRequestId: bulkRequest.id,
        studentEmail: student.email,
        studentFirstName: student.firstName,
        studentLastName: student.lastName || null,
      }));

      await db.insert(bulkStudentEnrollments).values(studentEnrollments);

      return {
        bulkRequest,
        pricing,
        studentCount
      };
    } catch (error) {
      console.error("Error creating bulk enrollment request:", error);
      throw error;
    }
  }

  /**
   * Get bulk enrollment requests for an employer
   */
  async getBulkEnrollmentRequests(employerId: string) {
    return await db
      .select()
      .from(bulkEnrollmentRequests)
      .where(eq(bulkEnrollmentRequests.employerId, employerId));
  }

  /**
   * Get student enrollments for a bulk request
   */
  async getBulkStudentEnrollments(bulkRequestId: string) {
    return await db
      .select()
      .from(bulkStudentEnrollments)
      .where(eq(bulkStudentEnrollments.bulkRequestId, bulkRequestId));
  }

  /**
   * Approve a bulk enrollment request
   */
  async approveBulkEnrollmentRequest(requestId: string, approvedBy: string) {
    try {
      // Update request status
      const [updatedRequest] = await db
        .update(bulkEnrollmentRequests)
        .set({
          status: "approved",
          approvedBy,
          approvedAt: new Date(),
          updatedAt: new Date()
        })
        .where(eq(bulkEnrollmentRequests.id, requestId))
        .returning();

      // TODO: Send enrollment invites to students
      // This would trigger email invitations to each student

      return updatedRequest;
    } catch (error) {
      console.error("Error approving bulk enrollment request:", error);
      throw error;
    }
  }
}

export const bulkPricingService = new BulkPricingService();

