import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  BookOpen, 
  Play, 
  Pause,
  Square,
  Volume2,
  VolumeX, 
  MessageSquare, 
  CheckCircle, 
  ExternalLink,
  RefreshCw,
  Loader2,
  Settings,
  Clock,
  Timer,
  RotateCcw
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useStudySession } from "@/hooks/use-study-session";
import { useToast } from "@/hooks/use-toast";
import PodcastPlayer from "@/components/podcast-player";

interface ContentViewerProps {
  contentId: string;
  contentType: string;
  title: string;
  courseId: string;
  sectionId?: string;
  onComplete?: () => void;
}

interface ExtractedContent {
  id: string;
  title: string;
  type: string;
  content: {
    extracted?: {
      html?: string;
      text?: string;
      content?: string;
      questions?: any[];
      cards?: any[];
      keyPoints?: string[];
      audioUrl?: string;
      transcript?: string;
      chatContent?: string;
      passingScore?: number;
    };
    chatContent?: string;
    extractedAt?: string;
  };
  quizgeckoUrl?: string;
}

export default function ContentViewer({ contentId, contentType, title, courseId, sectionId, onComplete }: ContentViewerProps) {
  const [audioPlaying, setAudioPlaying] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [speechSynthesis, setSpeechSynthesis] = useState<SpeechSynthesis | null>(null);
  const [currentUtterance, setCurrentUtterance] = useState<SpeechSynthesisUtterance | null>(null);
  const [currentSentence, setCurrentSentence] = useState('');
  const [, setLocation] = useLocation();
  const queryClient = useQueryClient();

  // Initialize study session tracking
  const studySession = useStudySession({
    contentId,
    contentType: contentType as 'lesson' | 'quiz' | 'flashcards' | 'chat' | 'podcast' | 'notes' | 'tools',
    autoStart: true
  });

  // Chat state
  const [chatMessages, setChatMessages] = useState<Array<{role: 'user' | 'assistant', content: string, id?: string}>>([]);
  const [chatInputMessage, setChatInputMessage] = useState('');
  const [isChatLoading, setIsChatLoading] = useState(false);
  const [autoPlayEnabled, setAutoPlayEnabled] = useState(true);
  const [currentPlayingId, setCurrentPlayingId] = useState<string | null>(null);
  const [selectedVoice, setSelectedVoice] = useState<string>('auto');
  const [availableVoices, setAvailableVoices] = useState<SpeechSynthesisVoice[]>([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [userAnswers, setUserAnswers] = useState<Record<number, string>>({});
  const [selectedQuizAnswer, setSelectedQuizAnswer] = useState<string | null>(null);
  const [showQuizResults, setShowQuizResults] = useState(false);
  const [quizScore, setQuizScore] = useState(0);
  const [incorrectQuizAnswers, setIncorrectQuizAnswers] = useState<any[]>([]);
  const [isSubmittingQuiz, setIsSubmittingQuiz] = useState(false);
  const [quizAttempt, setQuizAttempt] = useState<any>(null);

  const { data: content, isLoading, error } = useQuery<ExtractedContent>({
    queryKey: [`/api/content/${contentId}/display`],
  });

  const { toast } = useToast();

  useEffect(() => {
    if (typeof window !== 'undefined' && 'speechSynthesis' in window) {
      setSpeechSynthesis(window.speechSynthesis);

      // Load available voices
      const loadVoices = () => {
        const voices = window.speechSynthesis.getVoices();
        const englishVoices = voices.filter(voice => 
          voice.lang.startsWith('en') && !voice.name.includes('eSpeak')
        );
        setAvailableVoices(englishVoices);

        // Load saved voice preference
        const savedVoice = localStorage.getItem('ai-mentor-voice');
        if (savedVoice) {
          setSelectedVoice(savedVoice);
        }
      };

      loadVoices();
      // Voices might not be loaded immediately, so listen for the voiceschanged event
      window.speechSynthesis.addEventListener('voiceschanged', loadVoices);

      // Load voices if they're not ready yet
      if (window.speechSynthesis.getVoices().length === 0) {
        window.speechSynthesis.addEventListener('voiceschanged', () => {
          // Voices are now loaded
        });
      }
    }
  }, []);

  // Parse text-based quiz questions into structured format
  const parseQuestionsFromText = (text: string): any[] => {
    const questions: any[] = [];

    // Clean up text by removing escaped newlines and extra formatting
    const cleanText = text
      .replace(/\\n/g, ' ')
      .replace(/\\t/g, ' ')
      .replace(/\n+/g, '\n')
      .replace(/\s+/g, ' ')
      .trim();

    // Split by **number. pattern to get question blocks
    const questionBlocks = cleanText.split(/\*\*\d+\./).filter(block => block.trim().length > 0);

    questionBlocks.forEach((block, index) => {
      // Clean the block text
      let cleanBlock = block.replace(/\*\*/g, '').trim();

      // Extract question text (until first option A.)
      const questionMatch = cleanBlock.match(/^([^A]*?)(?=[A-D]\.)/s);
      if (!questionMatch) return;

      const questionText = questionMatch[1].trim();
      if (questionText.length < 10) return;

      // Extract individual options using more precise pattern
      const optionPattern = /([A-D])\.\s*([^A-D]*?)(?=\s*[A-D]\.|$)/gs;
      const optionMatches = [...cleanBlock.matchAll(optionPattern)];

      if (optionMatches.length < 2) return;

      const options: any[] = [];
      const seenTexts = new Set(); // Track duplicate options

      optionMatches.forEach((match, optIndex) => {
        const [, letter, optionText] = match;
        let cleanOptionText = optionText.trim();

        // Remove checkmarks and other symbols
        const isCorrect = cleanOptionText.includes('✓');
        cleanOptionText = cleanOptionText
          .replace(/✓/g, '')
          .replace(/\s+/g, ' ')
          .trim();

        // Only add if text is meaningful and not duplicate
        if (cleanOptionText.length > 3 && !seenTexts.has(cleanOptionText)) {
          seenTexts.add(cleanOptionText);
          options.push({
            id: `${index}-${letter}`,
            text: cleanOptionText,
            isCorrect: isCorrect
          });
        }
      });

      // Only add question if we have valid options and at least one correct answer
      if (options.length >= 2 && options.some(opt => opt.isCorrect)) {
        questions.push({
          id: `q-${index}`,
          question: questionText,
          options: options,
          type: 'multiple-choice',
          explanation: `Reference: Louisiana State Plumbing Code`,
          reference: 'Louisiana State Plumbing Code'
        });
      }
    });

    return questions.slice(0, 20);
  };

  // Auto-start podcast audio for immediate playback
  useEffect(() => {
    if (contentType === 'podcast' && content?.content?.extracted) {
      if (content?.content?.extracted?.audioUrl) {
        // If AI audio exists, play it
        setTimeout(() => {
          const audioElement = document.querySelector('audio') as HTMLAudioElement;
          if (audioElement) {
            audioElement.play().catch(error => {
              console.log('Auto-play prevented by browser:', error);
            });
          }
        }, 100);
      } else if (content?.content?.extracted?.transcript || content?.content?.extracted?.content || content?.content?.extracted?.text) {
        // Stop any existing speech before starting new one
        if (speechSynthesis) {
          speechSynthesis.cancel();
        }

        // Reset states
        setIsPlaying(false);
        setIsPaused(false);
        setCurrentUtterance(null);
        setCurrentSentence('');

        // Otherwise use TTS with clean text
        const getCleanTextForAutoStart = () => {
          const extracted = content?.content?.extracted;
          let text = extracted?.transcript || extracted?.content || extracted?.html || extracted?.text || '';

          if (text) {
            text = text
              .replace(/\*\[This would be.*?\]\*/g, '')
              .replace(/\\n\\n/g, '. ')
              .replace(/\\n/g, '. ')
              .replace(/\*\*(.*?)\*\*/g, '$1')
              .replace(/### (.*$)/gm, '$1. ')
              .replace(/## (.*$)/gm, '$1. ')
              .replace(/# (.*$)/gm, '$1. ')
              .replace(/\(\d{1,2}:\d{2}-\d{1,2}:\d{2}\)/g, '')
              .replace(/###\s*/g, '')
              .replace(/\?\s+/g, '? ')
              .replace(/\.\s*\./g, '.')
              .replace(/\s+/g, ' ')
              .replace(/([a-z])([A-Z])/g, '$1. $2')
              .trim();
          }

          return text;
        };

        // Removed auto-start - will only start when user clicks Review button
      }
    }

    // Cleanup: stop audio when component unmounts or content type changes
    return () => {
      if (speechSynthesis) {
        speechSynthesis.cancel();
      }
      setIsPlaying(false);
      setIsPaused(false);
      setCurrentUtterance(null);
      setCurrentSentence('');
    };
  }, [contentType, content?.content?.extracted?.transcript, content?.content?.extracted?.content, content?.content?.extracted?.text, content?.content?.extracted?.audioUrl, speechSynthesis]);

  const extractMutation = useMutation({
    mutationFn: () => fetch(`/api/extract-content/${contentId}`, { 
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(res => res.json()),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/content/${contentId}/display`] });
    }
  });

  const generateAudioMutation = useMutation({
    mutationFn: () => fetch(`/api/generate-audio/${contentId}`, { 
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      }
    }).then(res => res.json()),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/content/${contentId}/display`] });
    }
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-8">
        <Loader2 className="w-8 h-8 animate-spin" />
      </div>
    );
  }

  if (error || !content) {
    return (
      <div className="text-center p-8">
        <p className="text-red-600 mb-4">Failed to load content</p>
        <Button onClick={() => extractMutation.mutate()} disabled={extractMutation.isPending}>
          {extractMutation.isPending ? <Loader2 className="w-4 h-4 animate-spin mr-2" /> : <RefreshCw className="w-4 h-4 mr-2" />}
          Try Again
        </Button>
      </div>
    );
  }

  // All content is pre-extracted and stored in the database
  const hasExtractedContent = !!content?.content?.extracted;

  // Helper function to get content text from various possible locations
  const getContentText = () => {
    const extracted = content?.content?.extracted;
    let text = extracted?.transcript || extracted?.content || extracted?.html || extracted?.text || '';

    // Convert literal \n characters to actual HTML formatting
    if (text) {
      text = text
        .replace(/\\n\\n/g, '</p><p class="mb-4">')
        .replace(/\\n/g, '<br/>')
        .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
        .replace(/### (.*$)/gm, '<h3 class="text-lg font-semibold mt-6 mb-3">$1</h3>')
        .replace(/## (.*$)/gm, '<h2 class="text-xl font-bold mt-8 mb-4">$1</h2>')
        .replace(/# (.*$)/gm, '<h1 class="text-2xl font-bold mt-8 mb-6">$1</h1>');

      // Wrap in paragraph tags if not already HTML
      if (!text.includes('<')) {
        text = '<p class="mb-4">' + text + '</p>';
      }
    }

    return text;
  };

  // Helper function to get clean text for audio (no HTML/markdown)
  const getCleanTextForAudio = () => {
    const extracted = content?.content?.extracted;
    let text = extracted?.transcript || extracted?.content || extracted?.html || extracted?.text || '';

    // Clean up all formatting for audio reading and display
    if (text) {
      text = text
        .replace(/\*\[This would be.*?\]\*/g, '')  // Remove placeholder instructions
        .replace(/\\n\\n/g, '. ')  // Convert paragraph breaks to sentences with periods
        .replace(/\\n/g, '. ')  // Convert line breaks to sentence breaks with periods
        .replace(/\*\*(.*?)\*\*/g, '$1')  // Remove bold markers **text**
        .replace(/### (.*$)/gm, '$1. ')  // Remove ### headers and add periods
        .replace(/## (.*$)/gm, '$1. ')   // Remove ## headers and add periods
        .replace(/# (.*$)/gm, '$1. ')    // Remove # headers and add periods
        .replace(/\(\d{1,2}:\d{2}-\d{1,2}:\d{2}\)/g, '')  // Remove timestamps (0:00-1:30)
        .replace(/###\s*/g, '')  // Remove any remaining ###
        .replace(/\?\s+/g, '? ')  // Clean up question marks
        .replace(/\.\s*\./g, '.')  // Remove double periods
        .replace(/\s+/g, ' ')  // Replace multiple spaces with single space
        .replace(/([a-z])([A-Z])/g, '$1. $2')  // Add periods between sentences that run together
        .trim();
    }

    return text;
  };

  const handleComplete = async () => {
    // Stop any playing audio before completing
    if (speechSynthesis) {
      speechSynthesis.cancel();
      setIsPlaying(false);
      setIsPaused(false);
      setCurrentUtterance(null);
      setCurrentSentence('');
    }

    // End the study session tracking
    if (studySession.session && studySession.isActive) {
      await studySession.endSession();
    }

    onComplete?.();

    // Navigate back to lesson page
    const lessonPath = sectionId 
      ? `/course/${courseId}/lesson/${sectionId}`
      : `/course/${courseId}/lesson`;
    setLocation(lessonPath);
  };

  const renderLessonContent = () => {
    const extracted = content.content?.extracted;

    const lessonContent = getContentText();
    if (!lessonContent) {
      return (
        <div className="text-center p-6">
          <p className="mb-4">No content available.</p>
        </div>
      );
    }

    return (
      <div className="space-y-6">
        <div 
          className="prose max-w-none"
          dangerouslySetInnerHTML={{ __html: lessonContent }}
        />
        {extracted.text && !extracted.html && (
          <div className="whitespace-pre-wrap text-foreground">
            {extracted.text}
          </div>
        )}

      </div>
    );
  };

  const renderQuizContent = () => {
    let questions = content.content?.extracted?.questions || [];

    // If no structured questions exist, try to parse from text content
    if (questions.length === 0 && content.content?.extracted?.content) {
      questions = parseQuestionsFromText(content.content.extracted.content);
    }

    // Chapter Review sections require 80%, all others require 70%
    const isChapterReview = content.title?.toLowerCase().includes('chapter review') || 
                           content.title?.toLowerCase().includes('review') && content.title?.toLowerCase().includes('chapter');
    const passingScore = isChapterReview ? 80 : (content.content?.extracted?.passingScore || 70);


    if (questions.length === 0) {
      return (
        <div className="text-center p-6">
          <p className="mb-4">No quiz content available.</p>
          <Button onClick={() => extractMutation.mutate()} disabled={extractMutation.isPending}>
            {extractMutation.isPending ? <Loader2 className="w-4 h-4 animate-spin mr-2" /> : <RefreshCw className="w-4 h-4 mr-2" />}
            Extract Quiz
          </Button>
        </div>
      );
    }

    const handleAnswerSelect = (optionId: string) => {
      setSelectedQuizAnswer(optionId);
      setUserAnswers(prev => ({
        ...prev,
        [currentQuestion]: optionId
      }));
    };

    const nextQuestion = () => {
      if (currentQuestion < questions.length - 1) {
        setCurrentQuestion(prev => prev + 1);
        setSelectedQuizAnswer(userAnswers[currentQuestion + 1] ?? null);
      }
    };

    const prevQuestion = () => {
      setCurrentQuestion(prev => prev - 1);
      setSelectedQuizAnswer(userAnswers[currentQuestion - 1] || null);
    };

    const submitQuiz = async () => {
      setIsSubmittingQuiz(true);
      // Chapter Review sections require 80%, all others require 70%
      const isChapterReview = content.title?.toLowerCase().includes('chapter review') || 
                             content.title?.toLowerCase().includes('review') && content.title?.toLowerCase().includes('chapter');
      const passingScore = isChapterReview ? 80 : (content?.content?.extracted?.passingScore || 70);

      try {
        // Record the last answer if not already saved
        const finalAnswers = {
          ...userAnswers,
          [currentQuestion]: selectedQuizAnswer || ''
        };

        // Prepare questions with user answers
        const questionsWithAnswers = questions.map((question: any, index: number) => ({
          id: question.id,
          question: question.question,
          options: question.options,
          userAnswer: finalAnswers[index] || null,
          correctAnswer: question.options?.find((opt: any) => opt.isCorrect)?.id,
          isCorrect: finalAnswers[index] === question.options?.find((opt: any) => opt.isCorrect)?.id,
          explanation: question.explanation,
          reference: question.reference
        }));

        // Calculate score
        const correctAnswers = questionsWithAnswers.filter(q => q.isCorrect).length;
        const score = Math.round((correctAnswers / questions.length) * 100);
        const passed = score >= passingScore;

        // Submit to backend
        const response = await apiRequest('POST', '/api/quiz-attempts', {
          contentId,
          courseId,
          section: content?.section || 1,
          chapter: content?.chapter || 1,
          questions: questionsWithAnswers,
          totalQuestions: questions.length,
          correctAnswers,
          score,
          passed
        });

        const attempt = await response.json();
        setQuizAttempt(attempt);
        setQuizScore(score);

        // Prepare incorrect answers for review
        const incorrect = questionsWithAnswers.filter(q => !q.isCorrect).map(q => ({
          question: q.question,
          userAnswer: q.userAnswer ? q.options.find((opt: any) => opt.id === q.userAnswer)?.text || 'No answer' : 'No answer',
          correctAnswer: q.options.find((opt: any) => opt.id === q.correctAnswer)?.text,
          explanation: q.explanation,
          reference: q.reference
        }));

        setIncorrectQuizAnswers(incorrect);
        setShowQuizResults(true);

        // Invalidate queries to refresh progress
        queryClient.invalidateQueries({ queryKey: ['/api/section-progress'] });

      } catch (error) {
        console.error('Failed to submit quiz:', error);
        toast({
          title: "Error",
          description: "Failed to submit quiz. Please try again.",
          variant: "destructive"
        });
      } finally {
        setIsSubmittingQuiz(false);
      }
    };

    const restartQuiz = () => {
      setCurrentQuestion(0);
      setUserAnswers({});
      setSelectedQuizAnswer(null);
      setShowQuizResults(false);
      setQuizScore(0);
      setIncorrectQuizAnswers([]);
      setQuizAttempt(null);
    };

    if (showQuizResults) {
      return (
        <div className="space-y-6">
          <div className="text-center">
            <h3 className="text-2xl font-bold mb-2">Quiz Results</h3>
            <div className={`text-4xl font-bold mb-4 ${quizScore >= passingScore ? 'text-green-600' : 'text-red-600'}`}>
              {quizScore}%
            </div>
            <p className="text-lg mb-4">
              You got {questions.length - incorrectQuizAnswers.length} out of {questions.length} questions correct
            </p>
            <p className={`text-lg font-semibold ${quizScore >= passingScore ? 'text-green-600' : 'text-red-600'}`}>
              {quizScore >= passingScore ? '🎉 PASSED!' : `❌ FAILED - Need ${passingScore}% to pass`}
            </p>
            {isChapterReview && (
              <p className="text-sm text-blue-600 font-medium mt-2">
                📚 Chapter Review - Higher passing score required (80%)
              </p>
            )}
          </div>

          {incorrectQuizAnswers.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle className="text-red-600">Review Incorrect Answers</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                {incorrectQuizAnswers.map((item, index) => (
                  <div key={index} className="border-l-4 border-red-500 pl-4 space-y-2">
                    <p className="font-semibold">{item.question}</p>
                    <div className="space-y-1">
                      <p><span className="text-red-600 font-medium">Your answer:</span> {item.userAnswer}</p>
                      <p><span className="text-green-600 font-medium">Correct answer:</span> {item.correctAnswer}</p>
                      <p className="text-muted-foreground italic">{item.explanation}</p>
                      <p className="text-sm font-medium text-blue-600">📚 Reference: {item.reference}</p>
                    </div>
                  </div>
                ))}
              </CardContent>
            </Card>
          )}

          <div className="flex gap-4">
            <Button onClick={restartQuiz} variant="outline" className="flex-1">
              <RefreshCw className="w-4 h-4 mr-2" />
              Retake Quiz
            </Button>
            {quizScore >= passingScore ? (
              <Button onClick={handleComplete} className="flex-1">
                Continue to Next Lesson
              </Button>
            ) : (
              <Button disabled className="flex-1 opacity-50">
                🔒 Must Pass to Continue
              </Button>
            )}
          </div>
        </div>
      );
    }

    const currentQ = questions[currentQuestion];
    const isLastQuestion = currentQuestion === questions.length - 1;

    return (
      <div className="space-y-6">
        <div className="flex justify-between items-center">
          <div className="flex items-center gap-3">
            <h3 className="text-xl font-semibold">Louisiana Plumbing Code Quiz</h3>
            {isChapterReview && (
              <Badge className="bg-blue-100 text-blue-800">
                📚 Chapter Review - 80% Required
              </Badge>
            )}
          </div>
          <Badge variant="outline">
            Question {currentQuestion + 1} of {questions.length}
          </Badge>
        </div>

        <Progress value={(currentQuestion / questions.length) * 100} />

        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Question {currentQuestion + 1}</CardTitle>
          </CardHeader>
          <CardContent>
            <p className="text-lg mb-6">{currentQ.question}</p>
            <div className="space-y-3">
              {currentQ.options?.map((option: any, optIndex: number) => (
                <div
                  key={optIndex}
                  onClick={() => handleAnswerSelect(option.id)}
                  className={`p-4 rounded-lg border cursor-pointer transition-colors ${
                    selectedQuizAnswer === option.id
                      ? 'border-primary bg-primary/10 text-primary'
                      : 'border-border hover:bg-muted'
                  }`}
                >
                  <div className="flex items-center space-x-3">
                    <div className={`w-5 h-5 rounded-full border-2 flex items-center justify-center ${
                      selectedQuizAnswer === option.id ? 'border-primary bg-primary' : 'border-muted-foreground'
                    }`}>
                      {selectedQuizAnswer === option.id && (
                        <div className="w-2 h-2 rounded-full bg-white"></div>
                      )}
                    </div>
                    <span className="text-base">{option.text}</span>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        <div className="flex justify-between">
          <Button
            variant="outline"
            onClick={prevQuestion}
            disabled={currentQuestion === 0}
          >
            Previous
          </Button>

          {isLastQuestion ? (
            <Button
              onClick={submitQuiz}
              disabled={selectedQuizAnswer === null || isSubmittingQuiz}
              className="bg-green-600 hover:bg-green-700"
            >
              {isSubmittingQuiz ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Submitting...
                </>
              ) : (
                'Submit Quiz'
              )}
            </Button>
          ) : (
            <Button
              onClick={nextQuestion}
              disabled={selectedQuizAnswer === null}
            >
              Next Question
            </Button>
          )}
        </div>
      </div>
    );
  };

  const playAudio = (transcript: string) => {
    if (!speechSynthesis || !transcript) return;

    if (isPaused && currentUtterance) {
      speechSynthesis.resume();
      setIsPaused(false);
      setIsPlaying(true);
      return;
    }

    // Stop any existing speech
    speechSynthesis.cancel();
    setCurrentSentence('');

    // Split transcript into sentences - improved parsing
    console.log('Full transcript length:', transcript.length);
    console.log('Transcript preview:', transcript.substring(0, 200));

    // More comprehensive sentence splitting
    const sentences = transcript
      .split(/[.!?]+(?:\s|$)/) // Split on punctuation followed by space or end
      .map(s => s.trim())
      .filter(s => s.length > 10) // Filter out very short fragments
      .map(s => s.replace(/^\W+/, '')) // Remove leading punctuation
      .filter(s => s.length > 0);

    console.log('Total sentences found:', sentences.length);
    console.log('First few sentences:', sentences.slice(0, 3));
    let currentSentenceIndex = 0;

    const speakNextSentence = () => {
      if (currentSentenceIndex >= sentences.length) {
        setIsPlaying(false);
        setIsPaused(false);
        setCurrentUtterance(null);
        setCurrentSentence('');
        return;
      }

      const sentence = sentences[currentSentenceIndex].trim();
      if (!sentence) {
        currentSentenceIndex++;
        speakNextSentence();
        return;
      }

      // Display current sentence
      setCurrentSentence(sentence);

      // Keep playing state true throughout the entire sequence
      setIsPlaying(true);

      const utterance = new SpeechSynthesisUtterance(sentence);

      // Get available voices and select the best one
      const voices = speechSynthesis.getVoices();
      let selectedVoice = null;

      // Prefer high-quality voices in this order
      const preferredVoices = [
        'Microsoft Zira - English (United States)',
        'Microsoft David - English (United States)', 
        'Google US English Female',
        'Google US English Male',
        'Samantha',
        'Alex',
        'Victoria',
        'Daniel'
      ];

      for (const voiceName of preferredVoices) {
        selectedVoice = voices.find(voice => voice.name.includes(voiceName) || voice.name === voiceName);
        if (selectedVoice) break;
      }

      // Fallback to first English voice if no preferred voice found
      if (!selectedVoice) {
        selectedVoice = voices.find(voice => voice.lang.includes('en')) || voices[0];
      }

      if (selectedVoice) {
        utterance.voice = selectedVoice;
      }

      // Optimize speech settings for educational content
      utterance.rate = 0.85; // Slightly slower for better comprehension
      utterance.pitch = 1.1; // Slightly higher pitch for engagement
      utterance.volume = 1;

      utterance.onend = () => {
        console.log(`Finished sentence ${currentSentenceIndex + 1}/${sentences.length}: "${sentence.substring(0, 50)}..."`);
        currentSentenceIndex++;

        if (currentSentenceIndex < sentences.length) {
          console.log(`Moving to sentence ${currentSentenceIndex + 1}`);
          // Clear current sentence and move to next
          setTimeout(() => {
            setCurrentSentence('');
            setTimeout(() => speakNextSentence(), 300);
          }, 500);
        } else {
          console.log('Reached end of all sentences - podcast complete!');
          setTimeout(() => {
            setCurrentSentence('');
            setIsPlaying(false);
            setIsPaused(false);
            setCurrentUtterance(null);
          }, 500);
        }
      };

      utterance.onerror = () => {
        setIsPlaying(false);
        setIsPaused(false);
        setCurrentUtterance(null);
        setCurrentSentence('');
      };

      setCurrentUtterance(utterance);
      speechSynthesis.speak(utterance);
    };

    setIsPlaying(true);
    setIsPaused(false);
    speakNextSentence();
  };

