      .replace(/:/g, ', ') // Replace colons with commas for better flow
      .replace(/;/g, ', and ') // Replace semicolons for more natural speech
      .replace(/\.\s*\./g, '.') // Remove double periods
      .trim();

    // Split into sentences for more dynamic delivery
    const sentences = cleanText.split(/[.!?]+/).filter(s => s.trim().length > 0);

    let currentSentenceIndex = 0;

    const speakNextSentence = () => {
      if (currentSentenceIndex >= sentences.length) {
        setCurrentPlayingId(null);
        return;
      }

      const sentence = sentences[currentSentenceIndex].trim();
      if (!sentence) {
        currentSentenceIndex++;
        speakNextSentence();
        return;
      }

      const utterance = new SpeechSynthesisUtterance(sentence);

      // Select voice based on user preference
      let preferredVoice = null;

      if (selectedVoice === 'auto') {
        // Auto-select best voice
        preferredVoice = availableVoices.find(voice => 
          voice.name.includes('Google') || 
          voice.name.includes('Microsoft') ||
          voice.name.includes('Alex') || 
          voice.name.includes('Samantha') || 
          voice.name.includes('Daniel') || 
          voice.quality === 'high' ||
          voice.localService === true
        ) || availableVoices[0];
      } else {
        // Use user-selected voice
        preferredVoice = availableVoices.find(voice => voice.name === selectedVoice) || availableVoices[0];
      }

      if (preferredVoice) {
        utterance.voice = preferredVoice;
      }

      // Dynamic speech parameters based on sentence content
      if (sentence.includes('Key') || sentence.includes('Important') || sentence.includes('Primary')) {
        utterance.rate = 0.8; // Slower for important points
        utterance.pitch = 1.1; // Slightly higher pitch for emphasis
      } else if (sentence.includes('?')) {
        utterance.rate = 0.9; // Normal speed for questions
        utterance.pitch = 1.2; // Higher pitch for questions
      } else {
        utterance.rate = 0.85; // Good educational pace
        utterance.pitch = 1.05; // Slightly varied pitch
      }

      utterance.volume = 0.85;

      utterance.onend = () => {
        currentSentenceIndex++;
        // Small pause between sentences
        setTimeout(speakNextSentence, 200);
      };

      utterance.onerror = () => {
        setCurrentPlayingId(null);
      };

      speechSynthesis.speak(utterance);
    };

    setCurrentPlayingId(messageId);
    setCurrentUtterance(null); // We'll manage multiple utterances
    speakNextSentence();
  };

  const stopSpeech = () => {
    if (speechSynthesis) {
      speechSynthesis.cancel();
      setCurrentPlayingId(null);
    }
  };

  const handleVoiceChange = (voiceName: string) => {
    setSelectedVoice(voiceName);
    localStorage.setItem('ai-mentor-voice', voiceName);

    // Preview the voice with a sample message
    setTimeout(() => {
      previewVoice(voiceName);
    }, 100);
  };

  const previewVoice = (voiceName: string) => {
    if (!speechSynthesis) return;

    speechSynthesis.cancel(); // Stop any current speech

    const previewText = "Hello! I'm your AI mentor for Louisiana Plumbing Code. I'm here to help you learn.";
    const utterance = new SpeechSynthesisUtterance(previewText);

    let voice = null;
    if (voiceName === 'auto') {
      voice = availableVoices.find(v => 
        v.name.includes('Google') || 
        v.name.includes('Microsoft') ||
        v.name.includes('Alex') || 
        v.localService === true
      ) || availableVoices[0];
    } else {
      voice = availableVoices.find(v => v.name === voiceName);
    }

    if (voice) {
      utterance.voice = voice;
    }

    utterance.rate = 0.85;
    utterance.pitch = 1.05;
    utterance.volume = 0.85;

    speechSynthesis.speak(utterance);
  };

  const renderChatContent = () => {
    return (
      <div className="space-y-6">
        <div className="text-center">
          <MessageSquare className="w-16 h-16 mx-auto text-primary mb-4" />
          <h3 className="text-xl font-semibold">Interactive Learning Chat</h3>
          <p className="text-muted-foreground mb-4">Ask questions about {title}</p>

          {/* Audio controls */}
          <div className="space-y-4 mb-6">
            <div className="flex items-center justify-center gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setAutoPlayEnabled(!autoPlayEnabled)}
                className="flex items-center gap-2"
              >
                {autoPlayEnabled ? <Volume2 className="w-4 h-4" /> : <VolumeX className="w-4 h-4" />}
                {autoPlayEnabled ? 'Auto-play ON' : 'Auto-play OFF'}
              </Button>
              {currentPlayingId && (
                <Button variant="outline" size="sm" onClick={stopSpeech}>
                  <Pause className="w-4 h-4 mr-1" />
                  Stop
                </Button>
              )}
            </div>

            {/* Voice selection */}
            <div className="flex items-center justify-center gap-2">
              <Settings className="w-4 h-4 text-muted-foreground" />
              <span className="text-sm text-muted-foreground">AI Mentor Voice:</span>
              <Select value={selectedVoice} onValueChange={handleVoiceChange}>
                <SelectTrigger className="w-52">
                  <SelectValue placeholder="Select voice" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="auto">🤖 Auto (Best Quality)</SelectItem>
                  {availableVoices.map((voice) => {
                    const genderIcon = voice.name.includes('Samantha') || voice.name.includes('Victoria') ? '👩' : 
                                     voice.name.includes('Alex') || voice.name.includes('Daniel') ? '👨' : '🎙️';
                    const qualityLabel = voice.name.includes('Google') || voice.name.includes('Microsoft') || voice.localService ? ' ⭐' : '';
                    return (
                      <SelectItem key={voice.name} value={voice.name}>
                        {genderIcon} {voice.name.replace(' (Enhanced)', '')}{qualityLabel}
                      </SelectItem>
                    );
                  })}
                </SelectContent>
              </Select>
            </div>
          </div>
        </div>

        <Card className="h-96 flex flex-col">
          <CardContent className="flex-1 p-4 overflow-y-auto">
            <div className="space-y-4">
              {chatMessages.map((message, index) => (
                <div
                  key={index}
                  className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
                >
                  <div className="w-full">
                    <div
                      className={`p-3 rounded-lg w-full ${
                        message.role === 'user'
                          ? 'bg-blue-500 text-white'
                          : 'bg-gray-100 text-gray-900'
                      }`}
                    >
                      <p className="whitespace-pre-wrap">{message.content}</p>
                    </div>
                  </div>
                </div>
              ))}
              {isChatLoading && (
                <div className="flex justify-start">
                  <div className="bg-gray-100 p-3 rounded-lg w-full">
                    <Loader2 className="w-4 h-4 animate-spin" />
                  </div>
                </div>
              )}
            </div>
          </CardContent>
          <div className="p-4 border-t">
            <div className="flex space-x-2">
              <input
                type="text"
                value={chatInputMessage}
                onChange={(e) => setChatInputMessage(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && sendChatMessage()}
                placeholder="Ask a question about Louisiana Plumbing Code administration..."
                className="flex-1 px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                disabled={isChatLoading}
              />
              <Button onClick={sendChatMessage} disabled={isChatLoading || !chatInputMessage.trim()}>
                Send
              </Button>
            </div>
          </div>
        </Card>

        {/* Suggested Questions */}
        <Card>
          <CardContent className="p-4">
            <h4 className="font-semibold mb-3 text-sm text-muted-foreground">Suggested Questions to Explore:</h4>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-2">
              {suggestedQuestions.map((question, index) => (
                <Button
                  key={index}
                  variant="outline"
                  size="sm"
                  onClick={() => handleSuggestedQuestion(question)}
                  disabled={isChatLoading}
                  className="text-left justify-start h-auto p-3 text-xs"
                >
                  {question}
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>

        <Button onClick={handleComplete} className="w-full">
          Complete Session
        </Button>
      </div>
    );
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <div className="mb-6">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h1 className="text-2xl font-bold">{title}</h1>
            <div className="flex items-center gap-2 mt-2">
              <Badge variant="outline">
                {contentType.charAt(0).toUpperCase() + contentType.slice(1)}
              </Badge>
              {studySession.isActive && (
                <Badge variant="secondary" className="flex items-center gap-1">
                  <Timer className="w-3 h-3" />
                  {studySession.formattedTime}
                </Badge>
              )}
            </div>
          </div>

          {hasExtractedContent && (
            <div className="text-right">
              <p className="text-sm text-muted-foreground">
                Extracted {content.content?.extractedAt ? 
                  new Date(content.content.extractedAt).toLocaleDateString() : 
                  'recently'
                }
              </p>
            </div>
          )}
        </div>
      </div>

      <Card>
        <CardContent className="p-6">
          {contentType === 'lesson' && renderLessonContent()}
          {contentType === 'quiz' && renderQuizContent()}
          {contentType === 'podcast' && renderPodcastContent()}
          {contentType === 'flashcards' && renderFlashcardsContent()}
          {contentType === 'study-notes' && renderStudyNotesContent()}
          {contentType === 'study-plan' && renderStudyPlanContent()}
          {contentType === 'chat' && renderChatContent()}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/podcast-player.tsx.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Play, Pause, Square, RotateCcw } from "lucide-react";

interface PodcastPlayerProps {
  content: string;
  autoStart?: boolean;
  initialProgress?: number;
  onProgressUpdate?: (sentenceIndex: number, totalSentences: number) => void;
}

export default function PodcastPlayer({ content, autoStart = false, initialProgress = 0, onProgressUpdate }: PodcastPlayerProps) {
  const [isPlaying, setIsPlaying] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [currentSentence, setCurrentSentence] = useState('');
  const [currentUtterance, setCurrentUtterance] = useState<SpeechSynthesisUtterance | null>(null);
  const [speechSynthesis, setSpeechSynthesis] = useState<SpeechSynthesis | null>(null);
  const [sentences, setSentences] = useState<string[]>([]);
  const [currentIndex, setCurrentIndex] = useState(initialProgress);
  const [hasAutoStarted, setHasAutoStarted] = useState(false);

  useEffect(() => {
    if (typeof window !== 'undefined' && 'speechSynthesis' in window) {
      setSpeechSynthesis(window.speechSynthesis);
    }
  }, []);

  // Auto-start only when autoStart prop is true (when user clicked Review)
  useEffect(() => {
    if (autoStart && content && speechSynthesis && !hasAutoStarted) {
      console.log('Auto-starting podcast because autoStart=true from Review button');
      setHasAutoStarted(true);
      // Give time for component to mount and speech synthesis to initialize
      setTimeout(() => {
        const processedText = cleanText(content);
        const sentenceArray = processedText
          .split(/(?<=[.!?])\s+/)
          .map(s => s.trim())
          .filter(s => s.length > 10);

        setSentences(sentenceArray);
        const startIndex = initialProgress < sentenceArray.length ? initialProgress : 0;
        setCurrentIndex(startIndex);
        setIsPlaying(true);
        setIsPaused(false);
        speakSentence(startIndex);
      }, 1000);
    }
  }, [autoStart, content, speechSynthesis]); // Simplified dependencies

  const cleanText = (text: string) => {
    return text
      .replace(/\*\[This would be.*?\]\*/g, '')
      .replace(/\\n\\n/g, '. ')
      .replace(/\\n/g, '. ')
      .replace(/\*\*(.*?)\*\*/g, '$1')
      .replace(/### (.*$)/gm, '$1. ')
      .replace(/## (.*$)/gm, '$1. ')
      .replace(/# (.*$)/gm, '$1. ')
      .replace(/\(\d{1,2}:\d{2}-\d{1,2}:\d{2}\)/g, '')
      .replace(/###\s*/g, '')
      .replace(/\?\s+/g, '? ')
      .replace(/\.\s*\./g, '.')
      .replace(/\s+/g, ' ')
      .replace(/([a-z])([A-Z])/g, '$1. $2')
      .trim();
  };

  const speakSentence = (index: number) => {
    if (!speechSynthesis || !sentences.length || index >= sentences.length) return;

    const sentence = sentences[index].trim();
    if (!sentence) return;

    setCurrentSentence(sentence);
    setCurrentIndex(index);
    console.log(`Speaking sentence ${index + 1}/${sentences.length}: "${sentence.substring(0, 50)}..."`);

    // Report progress to parent component
    if (onProgressUpdate) {
      onProgressUpdate(index, sentences.length);
    }

    const utterance = new SpeechSynthesisUtterance(sentence);
    utterance.rate = 0.85;
    utterance.pitch = 1.05;
    utterance.volume = 0.85;

    utterance.onend = () => {
      console.log(`Moving to sentence ${index + 2}`);
      if (index + 1 < sentences.length) {
        setTimeout(() => speakSentence(index + 1), 300);
      } else {
        console.log('Reached end of all sentences - podcast complete!');
        setIsPlaying(false);
        setIsPaused(false);
        setCurrentUtterance(null);
        setCurrentSentence('');
      }
    };

    utterance.onerror = () => {
      setIsPlaying(false);
      setCurrentUtterance(null);
    };

    setCurrentUtterance(utterance);
    speechSynthesis.speak(utterance);
  };

  const startPodcast = () => {
    if (!speechSynthesis || !content) return;

    const processedText = cleanText(content);
    // Better sentence splitting that preserves meaningful content
    const sentenceArray = processedText
      .split(/(?<=[.!?])\s+/)
      .map(s => s.trim())
      .filter(s => s.length > 10); // Filter out very short fragments

    setSentences(sentenceArray);
    const startIndex = initialProgress < sentenceArray.length ? initialProgress : 0;
    setCurrentIndex(startIndex);
    console.log('Total sentences found:', sentenceArray.length);
    console.log('Starting from sentence:', startIndex + 1);

    setIsPlaying(true);
    setIsPaused(false);
    speakSentence(startIndex);
  };

  const pausePodcast = () => {
    if (speechSynthesis && isPlaying) {
      speechSynthesis.pause();
      setIsPlaying(false);
      setIsPaused(true);
    }
  };

  const resumePodcast = () => {
    if (speechSynthesis && currentUtterance) {
      speechSynthesis.resume();
      setIsPaused(false);
      setIsPlaying(true);
    }
  };

  const stopPodcast = () => {
    if (speechSynthesis) {
      speechSynthesis.cancel();
      setIsPlaying(false);
      setIsPaused(false);
      setCurrentUtterance(null);
      setCurrentSentence('');
      setCurrentIndex(0);
    }
  };

  const rewindSentence = () => {
    if (!speechSynthesis || !sentences.length) return;

    // Stop current playback
    speechSynthesis.cancel();

    // Go back one sentence (or stay at 0 if already at beginning)
    const newIndex = Math.max(0, currentIndex - 1);

    if (isPlaying) {
      // If currently playing, start from the previous sentence
      speakSentence(newIndex);
    } else {
      // If paused/stopped, just update the current sentence display
      setCurrentIndex(newIndex);
      if (sentences[newIndex]) {
        setCurrentSentence(sentences[newIndex].trim());
      }
    }
  };

  return (
    <div className="space-y-6">
      <div className="text-center">
        <h3 className="text-xl font-semibold mb-4">🎧 Audio Lesson</h3>

        {/* Scrolling sentence display */}
        {currentSentence && (
          <Card className="bg-gradient-to-r from-yellow-50 to-orange-50 border-2 border-yellow-200">
            <CardContent className="p-8">
              <div className="text-center">
                <div className="text-sm text-gray-600 mb-4">📖 Now Reading:</div>
                <div className="text-xl font-medium text-gray-800 leading-relaxed animate-in slide-in-from-bottom-4 duration-500">
                  {currentSentence}
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Progress indicator */}
        {sentences.length > 0 && (
          <div className="text-sm text-gray-600 mb-4">
            Progress: {currentIndex + 1} of {sentences.length} sentences
          </div>
        )}

        {/* Audio controls */}
        <div className="flex justify-center space-x-4 mt-6">
          {!isPlaying && !isPaused && (
            <Button
              onClick={startPodcast}
              size="lg"
              className="flex items-center space-x-2"
              data-testid="button-start-podcast"
            >
              <Play className="w-5 h-5" />
              <span>Start Podcast</span>
            </Button>
          )}

          {(isPlaying || isPaused) && (
            <>
              <Button
                onClick={isPaused ? resumePodcast : pausePodcast}
                size="lg"
                className="flex items-center space-x-2"
                data-testid="button-pause-resume"
              >
                {isPaused ? (
                  <>
                    <Play className="w-5 h-5" />
                    <span>Resume</span>
                  </>
                ) : (
                  <>
                    <Pause className="w-5 h-5" />
                    <span>Pause</span>
                  </>
                )}
              </Button>

              <Button
                onClick={rewindSentence}
                variant="outline"
                size="lg"
                className="flex items-center space-x-2"
                data-testid="button-rewind"
                disabled={!sentences.length}
              >
                <RotateCcw className="w-5 h-5" />
                <span>Rewind</span>
              </Button>

              <Button
                onClick={stopPodcast}
                variant="outline"
                size="lg"
                className="flex items-center space-x-2"
                data-testid="button-stop"
              >
                <Square className="w-5 h-5" />
                <span>Stop</span>
              </Button>
            </>
          )}
        </div>

      </div>
    </div>
  );
}

