client/src/pages/employer-dashboard.tsx.content:
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { 
  Building2, 
  MapPin, 
  Edit, 
  Pause, 
  Play, 
  CheckCircle, 
  XCircle, 
  Clock,
  Users,
  Eye,
  AlertTriangle
} from "lucide-react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

const editJobSchema = z.object({
  title: z.string().min(3, "Job title is required"),
  description: z.string().min(50, "Please provide a detailed job description (min 50 characters)"),
  location: z.string().min(3, "Location is required"),
  type: z.enum(["full_time", "part_time", "contract", "temporary"]),
  salaryMin: z.string().optional(),
  salaryMax: z.string().optional(),
  requirements: z.string().min(10, "Please list job requirements"),
  benefits: z.string().optional(),
});

type EditJobData = z.infer<typeof editJobSchema>;

interface JobListing {
  id: string;
  title: string;
  description: string;
  location: string;
  type: string;
  salaryMin?: string;
  salaryMax?: string;
  requirements: string;
  benefits?: string;
  status: 'active' | 'paused' | 'filled' | 'expired';
  planType: 'basic' | 'premium';
  daysRemaining: number;
  applicationsCount: number;
  createdAt: string;
}

export default function EmployerDashboard() {
  const [selectedJob, setSelectedJob] = useState<JobListing | null>(null);
  const [isEditing, setIsEditing] = useState(false);
  const { toast } = useToast();

  // Fetch employer's job listings
  const { data: jobListings, isLoading } = useQuery({
    queryKey: ['/api/employer/jobs'],
    retry: false,
  });

  const editForm = useForm<EditJobData>({
    resolver: zodResolver(editJobSchema),
  });

  // Edit job mutation
  const editJobMutation = useMutation({
    mutationFn: (data: EditJobData) => 
      apiRequest("PUT", `/api/jobs/${selectedJob?.id}`, data),
    onSuccess: () => {
      setIsEditing(false);
      setSelectedJob(null);
      queryClient.invalidateQueries({ queryKey: ['/api/employer/jobs'] });
      toast({
        title: "Job Updated Successfully",
        description: "Your job listing has been updated.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Update Failed",
        description: error.message || "Failed to update job listing",
        variant: "destructive",
      });
    },
  });

  // Change job status mutation
  const changeStatusMutation = useMutation({
    mutationFn: ({ jobId, status }: { jobId: string; status: string }) => 
      apiRequest("PATCH", `/api/jobs/${jobId}/status`, { status }),
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ['/api/employer/jobs'] });
      const statusMessages = {
        'paused': 'Job listing has been paused',
        'active': 'Job listing has been reactivated',
        'filled': 'Job listing marked as filled',
      };
      toast({
        title: "Status Updated",
        description: statusMessages[variables.status as keyof typeof statusMessages],
      });
    },
    onError: (error: any) => {
      toast({
        title: "Update Failed",
        description: error.message || "Failed to update job status",
        variant: "destructive",
      });
    },
  });

  const onSubmitEdit = (data: EditJobData) => {
    const processedData = {
      ...data,
      requirements: data.requirements.split('\n').filter(req => req.trim()).join('\n'),
      benefits: data.benefits ? data.benefits.split('\n').filter(benefit => benefit.trim()).join('\n') : undefined,
    };
    editJobMutation.mutate(processedData);
  };

  const handleStatusChange = (jobId: string, newStatus: string) => {
    changeStatusMutation.mutate({ jobId, status: newStatus });
  };

  const handleEditJob = (job: JobListing) => {
    setSelectedJob(job);
    setIsEditing(true);
    editForm.reset({
      title: job.title,
      description: job.description,
      location: job.location,
      type: job.type as any,
      salaryMin: job.salaryMin || "",
      salaryMax: job.salaryMax || "",
      requirements: job.requirements,
      benefits: job.benefits || "",
    });
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-700';
      case 'paused': return 'bg-yellow-100 text-yellow-700';
      case 'filled': return 'bg-blue-100 text-blue-700';
      case 'expired': return 'bg-red-100 text-red-700';
      default: return 'bg-gray-100 text-gray-700';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'active': return <CheckCircle className="w-4 h-4" />;
      case 'paused': return <Pause className="w-4 h-4" />;
      case 'filled': return <Users className="w-4 h-4" />;
      case 'expired': return <XCircle className="w-4 h-4" />;
      default: return <Clock className="w-4 h-4" />;
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background">
        <div className="max-w-6xl mx-auto px-4 py-16">
          <div className="animate-pulse space-y-4">
            <div className="h-8 bg-gray-200 rounded w-1/4"></div>
            <div className="h-64 bg-gray-200 rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <section className="border-b bg-white">
        <div className="max-w-6xl mx-auto px-4 py-8">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-3xl font-bold" data-testid="dashboard-title">
                Manage Job Listings
              </h1>
              <p className="text-muted-foreground mt-2">
                Update, pause, or mark your job listings as filled
              </p>
            </div>
            <Button 
              onClick={() => window.location.href = "/employer-portal"}
              data-testid="new-job-button"
            >
              <Building2 className="w-4 h-4 mr-2" />
              Post New Job
            </Button>
          </div>
        </div>
      </section>

      <div className="max-w-6xl mx-auto px-4 py-8">
        {/* Job Listings Grid */}
        {!jobListings || jobListings.length === 0 ? (
          <Card>
            <CardContent className="text-center py-16">
              <Building2 className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-xl font-semibold mb-2">No Job Listings Yet</h3>
              <p className="text-muted-foreground mb-6">
                Create your first job listing to start hiring apprentice plumbers
              </p>
              <Button onClick={() => window.location.href = "/employer-portal"}>
                Post Your First Job
              </Button>
            </CardContent>
          </Card>
        ) : (
          <div className="grid gap-6">
            {jobListings.map((job: JobListing) => (
              <Card key={job.id} className="hover:shadow-md transition-shadow">
                <CardContent className="p-6">
                  <div className="flex items-start justify-between mb-4">
                    <div className="flex-1">
                      <div className="flex items-center space-x-3 mb-2">
                        <h3 className="text-xl font-semibold">{job.title}</h3>
                        <Badge className={getStatusColor(job.status)}>
                          {getStatusIcon(job.status)}
                          <span className="ml-1 capitalize">{job.status}</span>
                        </Badge>
                        <Badge variant="outline">
                          {job.planType.charAt(0).toUpperCase() + job.planType.slice(1)}
                        </Badge>
                      </div>
                      <div className="flex items-center space-x-4 text-sm text-muted-foreground">
                        <span className="flex items-center">
                          <MapPin className="w-4 h-4 mr-1" />
                          {job.location}
                        </span>
                        <span className="flex items-center">
                          <Users className="w-4 h-4 mr-1" />
                          {job.applicationsCount} applications
                        </span>
                        <span className="flex items-center">
                          <Clock className="w-4 h-4 mr-1" />
                          {job.daysRemaining} days left
                        </span>
                      </div>
                    </div>
                  </div>

                  <p className="text-muted-foreground mb-4 line-clamp-2">
                    {job.description}
                  </p>

                  {job.daysRemaining <= 7 && job.status === 'active' && (
                    <Alert className="mb-4">
                      <AlertTriangle className="h-4 w-4" />
                      <AlertDescription>
                        This listing expires in {job.daysRemaining} days. Edit to extend or mark as filled.
                      </AlertDescription>
                    </Alert>
                  )}

                  <div className="flex flex-wrap gap-2">
                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={() => handleEditJob(job)}
                      data-testid={`edit-job-${job.id}`}
                    >
                      <Edit className="w-4 h-4 mr-1" />
                      Edit Details
                    </Button>

                    {job.status === 'active' && (
                      <>
                        <Button 
                          variant="outline" 
                          size="sm"
                          onClick={() => handleStatusChange(job.id, 'paused')}
                        >
                          <Pause className="w-4 h-4 mr-1" />
                          Pause
                        </Button>
                        <Button 
                          variant="outline" 
                          size="sm"
                          onClick={() => handleStatusChange(job.id, 'filled')}
                          className="bg-green-50 hover:bg-green-100"
                        >
                          <CheckCircle className="w-4 h-4 mr-1" />
                          Mark as Filled
                        </Button>
                      </>
                    )}

                    {job.status === 'paused' && (
                      <Button 
                        variant="outline" 
                        size="sm"
                        onClick={() => handleStatusChange(job.id, 'active')}
                        className="bg-blue-50 hover:bg-blue-100"
                      >
                        <Play className="w-4 h-4 mr-1" />
                        Reactivate
                      </Button>
                    )}

                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={() => window.location.href = `/employer/applications/${job.id}`}
                    >
                      <Eye className="w-4 h-4 mr-1" />
                      View Applications
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}

        {/* Edit Job Modal/Form */}
        {isEditing && selectedJob && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
            <Card className="w-full max-w-2xl max-h-[90vh] overflow-y-auto">
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <Edit className="w-5 h-5" />
                  <span>Edit Job Listing</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <Form {...editForm}>
                  <form onSubmit={editForm.handleSubmit(onSubmitEdit)} className="space-y-6">
                    <FormField
                      control={editForm.control}
                      name="title"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Job Title *</FormLabel>
                          <FormControl>
                            <Input {...field} placeholder="e.g., Journeyman Plumber" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <div className="grid md:grid-cols-2 gap-4">
                      <FormField
                        control={editForm.control}
                        name="location"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Location *</FormLabel>
                            <FormControl>
                              <Input {...field} placeholder="City, LA" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={editForm.control}
                        name="type"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Employment Type *</FormLabel>
                            <Select onValueChange={field.onChange} defaultValue={field.value}>
                              <FormControl>
                                <SelectTrigger>
                                  <SelectValue placeholder="Select type" />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <SelectItem value="full_time">Full Time</SelectItem>
                                <SelectItem value="part_time">Part Time</SelectItem>
                                <SelectItem value="contract">Contract</SelectItem>
                                <SelectItem value="temporary">Temporary</SelectItem>
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>

                    <div className="grid md:grid-cols-2 gap-4">
                      <FormField
                        control={editForm.control}
                        name="salaryMin"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Minimum Salary (optional)</FormLabel>
                            <FormControl>
                              <Input {...field} placeholder="$45,000" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={editForm.control}
                        name="salaryMax"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Maximum Salary (optional)</FormLabel>
                            <FormControl>
                              <Input {...field} placeholder="$65,000" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>

                    <FormField
                      control={editForm.control}
                      name="description"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Job Description *</FormLabel>
                          <FormControl>
                            <Textarea 
                              {...field} 
                              rows={6}
                              placeholder="Describe the position, responsibilities, and work environment..."
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={editForm.control}
                      name="requirements"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Requirements *</FormLabel>
                          <FormControl>
                            <Textarea 
                              {...field} 
                              rows={4}
                              placeholder="List requirements (one per line)&#10;Louisiana plumbing license required&#10;2+ years experience&#10;Reliable transportation"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={editForm.control}
                      name="benefits"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Benefits (optional)</FormLabel>
                          <FormControl>
                            <Textarea 
                              {...field} 
                              rows={3}
                              placeholder="List benefits (one per line)&#10;Health insurance&#10;401k matching&#10;Paid time off"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <div className="flex space-x-4">
                      <Button 
                        type="submit" 
                        disabled={editJobMutation.isPending}
                        className="flex-1"
                      >
                        {editJobMutation.isPending ? "Updating..." : "Update Job Listing"}
                      </Button>
                      <Button 
                        type="button" 
                        variant="outline"
                        onClick={() => {
                          setIsEditing(false);
                          setSelectedJob(null);
                        }}
                        className="flex-1"
                      >
                        Cancel
                      </Button>
                    </div>
                  </form>
                </Form>
              </CardContent>
            </Card>
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/admin/beta-feedback-das.content:
import { useState, useEffect } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { 
  MessageSquare, 
  Send, 
  Users, 
  CheckCircle, 
  Clock,
  TrendingUp,
  Mail,
  Calendar
} from "lucide-react";

interface FeedbackStats {
  totalSent: number;
  completed: number;
  completionRate: number;
  totalBetaTesters: number;
}

interface FeedbackCampaign {
  id: string;
  title: string;
  monthYear: string;
  emailSentAt: string | null;
  dueDate: string;
}

interface FeedbackAnalytics {
  campaign: FeedbackCampaign | null;
  stats: FeedbackStats | null;
  responses: any[];
}

export default function BetaFeedbackDashboard() {
  const [analytics, setAnalytics] = useState<FeedbackAnalytics | null>(null);
  const [selectedMonth, setSelectedMonth] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isSending, setIsSending] = useState(false);
  const [isMonitoring, setIsMonitoring] = useState(false);
  const { toast } = useToast();

  useEffect(() => {
    // Default to current month
    const now = new Date();
    const currentMonth = `${now.getFullYear()}-${String(now.getMonth() + 1).padStart(2, '0')}`;
    setSelectedMonth(currentMonth);
    loadAnalytics(currentMonth);
  }, []);

  const loadAnalytics = async (monthYear?: string) => {
    setIsLoading(true);
    try {
      const url = monthYear 
        ? `/api/admin/beta-feedback/analytics?monthYear=${monthYear}`
        : '/api/admin/beta-feedback/analytics';
      const response = await apiRequest("GET", url);
      const data = await response.json();
      setAnalytics(data);
    } catch (error: any) {
      console.error("Error loading analytics:", error);
      toast({
        title: "Error",
        description: "Failed to load feedback analytics",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const sendMonthlyFeedback = async () => {
    setIsSending(true);
    try {
      const response = await apiRequest("POST", "/api/admin/beta-feedback/send-monthly");
      const result = await response.json();

      toast({
        title: "Feedback Emails Sent!",
        description: `Sent to ${result.emailsSent} beta testers`,
      });

      // Reload analytics
      loadAnalytics(selectedMonth);
    } catch (error: any) {
      console.error("Error sending feedback:", error);
      toast({
        title: "Error",
        description: "Failed to send monthly feedback emails",
        variant: "destructive",
      });
    } finally {
      setIsSending(false);
    }
  };

  const runMonitoring = async () => {
    setIsMonitoring(true);
    try {
      const response = await apiRequest("POST", "/api/admin/beta-feedback/run-monitoring");
      const result = await response.json();

      if (result.criticalIssues?.length > 0 || result.alerts?.length > 0) {
        toast({
          title: "Monitoring Complete",
          description: `Found ${result.criticalIssues?.length || 0} critical issues and ${result.alerts?.length || 0} warnings`,
          variant: result.criticalIssues?.length > 0 ? "destructive" : "default",
        });
      } else {
        toast({
          title: "Monitoring Complete",
          description: "No issues detected in recent feedback",
        });
      }
    } catch (error: any) {
      console.error("Error running monitoring:", error);
      toast({
        title: "Error",
        description: "Failed to run feedback monitoring",
        variant: "destructive",
      });
    } finally {
      setIsMonitoring(false);
    }
  };

  const handleMonthChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const monthYear = e.target.value;
    setSelectedMonth(monthYear);
    if (monthYear) {
      loadAnalytics(monthYear);
    }
  };

  if (isLoading && !analytics) {
    return (
      <div className="p-6">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full mx-auto"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-foreground" data-testid="feedback-dashboard-title">
            Beta Feedback Dashboard
          </h1>
          <p className="text-muted-foreground">
            Manage monthly feedback campaigns for beta testers
          </p>
        </div>
        <div className="flex gap-3">
          <Button 
            onClick={runMonitoring}
            disabled={isMonitoring}
            variant="outline"
            data-testid="run-monitoring-button"
          >
            {isMonitoring ? (
              <>
                <div className="animate-spin w-4 h-4 border-2 border-current border-t-transparent rounded-full mr-2"></div>
                Analyzing...
              </>
            ) : (
              <>
                <TrendingUp className="w-4 h-4 mr-2" />
                Run Monitoring
              </>
            )}
          </Button>
          <Button 
            onClick={sendMonthlyFeedback}
            disabled={isSending}
            data-testid="send-feedback-button"
          >
            {isSending ? (
              <>
                <div className="animate-spin w-4 h-4 border-2 border-white border-t-transparent rounded-full mr-2"></div>
                Sending...
              </>
            ) : (
              <>
                <Send className="w-4 h-4 mr-2" />
                Send Monthly Feedback
              </>
            )}
          </Button>
        </div>
      </div>

      {/* Month selector */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Calendar className="w-5 h-5" />
            Select Month
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex items-center gap-4">
            <div className="flex-1">
              <Label htmlFor="month-input">Month & Year</Label>
              <Input
                id="month-input"
                type="month"
                value={selectedMonth}
                onChange={handleMonthChange}
                data-testid="month-selector"
              />
            </div>
            <Button 
              onClick={() => loadAnalytics(selectedMonth)}
              variant="outline"
              data-testid="load-analytics"
            >
              Load Analytics
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Stats Overview */}
      {analytics?.stats && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Total Beta Testers</p>
                  <p className="text-2xl font-bold" data-testid="total-testers">
                    {analytics.stats.totalBetaTesters}
                  </p>
                </div>
                <Users className="h-8 w-8 text-muted-foreground" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Emails Sent</p>
                  <p className="text-2xl font-bold" data-testid="emails-sent">
                    {analytics.stats.totalSent}
                  </p>
                </div>
                <Mail className="h-8 w-8 text-muted-foreground" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Responses</p>
                  <p className="text-2xl font-bold" data-testid="responses-count">
                    {analytics.stats.completed}
                  </p>
                </div>
                <MessageSquare className="h-8 w-8 text-muted-foreground" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Completion Rate</p>
                  <p className="text-2xl font-bold" data-testid="completion-rate">
                    {analytics.stats.completionRate.toFixed(1)}%
                  </p>
                </div>
                <TrendingUp className="h-8 w-8 text-muted-foreground" />
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Campaign Status */}
      {analytics?.campaign && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Calendar className="w-5 h-5" />
              Campaign Status
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="font-semibold" data-testid="campaign-title">
                    {analytics.campaign.title}
                  </h3>
                  <p className="text-sm text-muted-foreground">
                    Due: {new Date(analytics.campaign.dueDate).toLocaleDateString()}
                  </p>
                </div>
                <div className="flex items-center gap-2">
                  {analytics.campaign.emailSentAt ? (
                    <Badge variant="default" className="bg-green-100 text-green-800">
                      <CheckCircle className="w-3 h-3 mr-1" />
                      Sent
                    </Badge>
                  ) : (
                    <Badge variant="secondary">
                      <Clock className="w-3 h-3 mr-1" />
                      Pending
                    </Badge>
                  )}
                </div>
              </div>

              {analytics.campaign.emailSentAt && (
                <p className="text-sm text-muted-foreground">
                  Emails sent on: {new Date(analytics.campaign.emailSentAt).toLocaleString()}
                </p>
              )}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Recent Responses */}
      {analytics?.responses && analytics.responses.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>Recent Responses</CardTitle>
            <CardDescription>
              Latest feedback submissions from beta testers
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {analytics.responses.slice(0, 5).map((response, index) => (
                <div key={index} className="border-b last:border-b-0 pb-4 last:pb-0">
                  <div className="flex items-center justify-between mb-2">
                    <p className="font-medium">
                      {response.userName || 'Anonymous'}
                    </p>
                    <p className="text-sm text-muted-foreground">
                      {new Date(response.completedAt).toLocaleString()}
                    </p>
                  </div>
                  <p className="text-sm text-muted-foreground">
                    {response.userEmail}
                  </p>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* No Data State */}
      {analytics && !analytics.campaign && !analytics.stats && (
        <Card>
          <CardContent className="p-12 text-center">
            <MessageSquare className="w-12 h-12 text-muted-foreground mx-auto mb-4" />
            <h3 className="text-lg font-semibold mb-2">No Campaign Found</h3>
            <p className="text-muted-foreground mb-4">
              No feedback campaign exists for the selected month.
            </p>
            <Button onClick={sendMonthlyFeedback} disabled={isSending}>
              Create & Send Campaign
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/beta-feedback.tsx.content:
import { useEffect, useState } from "react";
import { useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Checkbox } from "@/components/ui/checkbox";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { CheckCircle, Star, MessageSquare, Building2 } from "lucide-react";
import { useForm } from "react-hook-form";

interface FeedbackQuestion {
  id: string;
  questionText: string;
  questionType: string;
  options?: string[];
  isRequired: boolean;
}

interface FeedbackCampaign {
  id: string;
  title: string;
  description: string;
  monthYear: string;
  dueDate: string;
}

export default function BetaFeedback() {
  const [location] = useLocation();
  const [campaign, setCampaign] = useState<FeedbackCampaign | null>(null);
  const [questions, setQuestions] = useState<FeedbackQuestion[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const { toast } = useToast();
  const { register, handleSubmit, formState: { errors }, setValue, watch } = useForm();

  const token = new URLSearchParams(location.split('?')[1] || '').get('token');

  useEffect(() => {
    if (!token) {
      toast({
        title: "Invalid Link",
        description: "This feedback link is invalid or expired",
        variant: "destructive",
      });
      return;
    }
    loadFeedbackForm();
  }, [token]);

  const loadFeedbackForm = async () => {
    try {
      const response = await apiRequest("GET", `/api/beta-feedback/${token}`);
      const data = await response.json();
      setCampaign(data.campaign);
      setQuestions(data.questions);
    } catch (error: any) {
      console.error("Error loading feedback form:", error);
      toast({
        title: "Error Loading Form",
        description: "Unable to load the feedback form. Please try again later.",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const onSubmit = async (data: any) => {
    if (!token || !campaign) return;

    setIsSubmitting(true);
    try {
      const responses: Record<string, any> = {};

      // Process form data into responses object
      Object.keys(data).forEach(key => {
        if (key.startsWith('question_')) {
          const questionId = key.replace('question_', '');
          responses[questionId] = data[key];
        }
      });

      await apiRequest("POST", `/api/beta-feedback/${token}/submit`, {
        responses
      });

      setIsSubmitted(true);
      toast({
        title: "Feedback Submitted!",
        description: "Thank you for helping us improve LA Plumb Prep",
      });
    } catch (error: any) {
      console.error("Error submitting feedback:", error);
      toast({
        title: "Submission Error",
        description: "There was an error submitting your feedback. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const renderQuestion = (question: FeedbackQuestion, index: number) => {
    const fieldName = `question_${question.id}`;

    switch (question.questionType) {
      case 'text':
        return (
          <div key={question.id} className="space-y-2">
            <Label htmlFor={fieldName} className="text-sm font-medium">
              {question.questionText}
              {question.isRequired && <span className="text-red-500 ml-1">*</span>}
            </Label>
            <Input
              id={fieldName}
              {...register(fieldName, { required: question.isRequired })}
              placeholder="Type your answer here..."
              data-testid={`feedback-input-${index}`}
            />
            {errors[fieldName] && (
              <p className="text-sm text-red-500">This field is required</p>
            )}
          </div>
        );

      case 'textarea':
        return (
          <div key={question.id} className="space-y-2">
            <Label htmlFor={fieldName} className="text-sm font-medium">
              {question.questionText}
              {question.isRequired && <span className="text-red-500 ml-1">*</span>}
            </Label>
            <Textarea
              id={fieldName}
              {...register(fieldName, { required: question.isRequired })}
              placeholder="Please share your detailed thoughts..."
              rows={4}
              data-testid={`feedback-textarea-${index}`}
            />
            {errors[fieldName] && (
              <p className="text-sm text-red-500">This field is required</p>
            )}
          </div>
        );

      case 'rating':
        return (
          <div key={question.id} className="space-y-3">
            <Label className="text-sm font-medium">
              {question.questionText}
              {question.isRequired && <span className="text-red-500 ml-1">*</span>}
            </Label>
            <RadioGroup 
              onValueChange={(value) => setValue(fieldName, value)}
              data-testid={`feedback-rating-${index}`}
            >
              {[1, 2, 3, 4, 5].map((rating) => (
                <div key={rating} className="flex items-center space-x-2">
                  <RadioGroupItem value={rating.toString()} id={`${fieldName}_${rating}`} />
                  <Label htmlFor={`${fieldName}_${rating}`} className="flex items-center cursor-pointer">
                    <div className="flex items-center space-x-1">
                      {[1, 2, 3, 4, 5].map((star) => (
                        <Star
                          key={star}
                          className={`w-4 h-4 ${star <= rating ? 'text-yellow-400 fill-current' : 'text-gray-300'}`}
                        />
                      ))}
                    </div>
                    <span className="ml-2 text-sm">
                      {rating === 1 && "Poor"}
                      {rating === 2 && "Fair"}
                      {rating === 3 && "Good"}
                      {rating === 4 && "Very Good"}
                      {rating === 5 && "Excellent"}
                    </span>
                  </Label>
                </div>
              ))}
            </RadioGroup>
            {errors[fieldName] && (
              <p className="text-sm text-red-500">Please select a rating</p>
            )}
          </div>
        );

      case 'multiple_choice':
        return (
          <div key={question.id} className="space-y-3">
            <Label className="text-sm font-medium">
              {question.questionText}
              {question.isRequired && <span className="text-red-500 ml-1">*</span>}
            </Label>
            <RadioGroup 
              onValueChange={(value) => setValue(fieldName, value)}
              data-testid={`feedback-choice-${index}`}
            >
              {question.options?.map((option, optionIndex) => (
                <div key={optionIndex} className="flex items-center space-x-2">
                  <RadioGroupItem value={option} id={`${fieldName}_${optionIndex}`} />
                  <Label htmlFor={`${fieldName}_${optionIndex}`} className="cursor-pointer">
                    {option}
                  </Label>
                </div>
              ))}
            </RadioGroup>
            {errors[fieldName] && (
              <p className="text-sm text-red-500">Please select an option</p>
            )}
          </div>
        );

      case 'boolean':
        return (
          <div key={question.id} className="space-y-3">
            <Label className="text-sm font-medium">
              {question.questionText}
              {question.isRequired && <span className="text-red-500 ml-1">*</span>}
            </Label>
            <RadioGroup 
              onValueChange={(value) => setValue(fieldName, value === 'true')}
              data-testid={`feedback-boolean-${index}`}
            >
              <div className="flex items-center space-x-2">
                <RadioGroupItem value="true" id={`${fieldName}_yes`} />
                <Label htmlFor={`${fieldName}_yes`} className="cursor-pointer">Yes</Label>
              </div>
              <div className="flex items-center space-x-2">
                <RadioGroupItem value="false" id={`${fieldName}_no`} />
                <Label htmlFor={`${fieldName}_no`} className="cursor-pointer">No</Label>
              </div>
            </RadioGroup>
            {errors[fieldName] && (
              <p className="text-sm text-red-500">Please select an option</p>
            )}
          </div>
        );

      default:
        return null;
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-2xl mx-auto">
          <div className="text-center">
            <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full mx-auto mb-4"></div>
            <h2 className="text-2xl font-bold mb-2">Loading Feedback Form...</h2>
          </div>
        </div>
      </div>
    );
  }

  if (isSubmitted) {
    return (
      <div className="min-h-screen bg-background py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-2xl mx-auto text-center">
          <div className="flex items-center justify-center space-x-2 mb-6">
            <div className="w-12 h-12 bg-primary rounded-lg flex items-center justify-center">
              <Building2 className="w-7 h-7 text-primary-foreground" />
            </div>
            <div className="text-left">
              <h1 className="text-xl font-bold text-primary">LA Plumb Prep</h1>
              <p className="text-xs text-muted-foreground">Beta Feedback Program</p>
            </div>
          </div>

          <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-6">
            <CheckCircle className="w-8 h-8 text-green-600" />
          </div>

          <h2 className="text-3xl font-bold text-foreground mb-4">Thank You!</h2>
          <p className="text-muted-foreground mb-8">
            Your feedback has been submitted successfully. Your insights help us improve LA Plumb Prep for all users.
          </p>

          <Card className="bg-blue-50 border-blue-200">
            <CardContent className="p-6">
              <p className="text-blue-800">
                <strong>Remember:</strong> As long as you continue as an active student and complete these monthly feedback surveys, your beta pricing is locked in for all current and future courses.
              </p>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (!campaign) {
    return (
      <div className="min-h-screen bg-background py-12 px-4 sm:px-6 lg:px-8">
        <div className="max-w-2xl mx-auto text-center">
          <h2 className="text-2xl font-bold mb-4">Feedback Form Not Found</h2>
          <p className="text-muted-foreground">This feedback link may be invalid or expired.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-2xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center space-x-2 mb-6">
            <div className="w-12 h-12 bg-primary rounded-lg flex items-center justify-center">
              <Building2 className="w-7 h-7 text-primary-foreground" />
            </div>
            <div className="text-left">
              <h1 className="text-xl font-bold text-primary">LA Plumb Prep</h1>
              <p className="text-xs text-muted-foreground">Beta Feedback Program</p>
            </div>
          </div>

          <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <MessageSquare className="w-6 h-6 text-blue-600" />
          </div>

          <h2 className="text-3xl font-bold text-foreground mb-2" data-testid="feedback-title">
            {campaign.title}
          </h2>
          {campaign.description && (
            <p className="text-muted-foreground mb-4">
              {campaign.description}
            </p>
          )}
          <p className="text-sm text-muted-foreground">
            Due Date: {new Date(campaign.dueDate).toLocaleDateString()}
          </p>
        </div>

        {/* Beta Info Card */}
        <Card className="mb-8 border-orange-200 bg-orange-50">
          <CardHeader>
            <CardTitle className="text-orange-800 text-lg">Beta Tester Benefits</CardTitle>
          </CardHeader>
          <CardContent className="text-orange-700">
            <ul className="space-y-2 text-sm">
              <li className="flex items-start gap-2">
                <CheckCircle className="w-4 h-4 text-orange-600 mt-0.5 flex-shrink-0" />
                <span>Keep your discounted beta pricing forever</span>
              </li>
              <li className="flex items-start gap-2">
                <CheckCircle className="w-4 h-4 text-orange-600 mt-0.5 flex-shrink-0" />
                <span>Help shape the future of plumbing education</span>
              </li>
              <li className="flex items-start gap-2">
                <CheckCircle className="w-4 h-4 text-orange-600 mt-0.5 flex-shrink-0" />
                <span>Access to new features before anyone else</span>
              </li>
            </ul>
            <div className="mt-4 p-3 bg-orange-100 rounded border border-orange-300">
              <p className="text-xs font-medium">
                <strong>Important:</strong> Complete this monthly survey to maintain your beta status and locked pricing.
              </p>
            </div>
          </CardContent>
        </Card>

        {/* Feedback Form */}
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Your Feedback</CardTitle>
              <CardDescription>
                Your responses help us improve the platform for all users.
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              {questions.map((question, index) => renderQuestion(question, index))}
            </CardContent>
          </Card>

          <Button 
            type="submit" 
            className="w-full" 
            size="lg"
            disabled={isSubmitting}
            data-testid="submit-feedback"
          >
            {isSubmitting ? "Submitting..." : "Submit Feedback"}
          </Button>
        </form>
      </div>
    </div>
  );
}

server/betaFeedbackSystem.ts.content:
import { db } from "./db";
import { 
  users, 
  betaFeedbackQuestions, 
  betaFeedbackCampaigns, 
  betaFeedbackResponses,
  emailQueue 
} from "@shared/schema";
import { eq, and, isNull, lt } from "drizzle-orm";
import { randomUUID } from "crypto";
import { emailAutomation } from './email-automation';

export class BetaFeedbackService {
  // Initialize default feedback questions
  static async initializeDefaultQuestions() {
    const existingQuestions = await db.select().from(betaFeedbackQuestions).where(eq(betaFeedbackQuestions.isActive, true));

    if (existingQuestions.length === 0) {
      const defaultQuestions = [
        {
          questionText: "How would you rate your overall experience with LA Plumb Prep this month?",
          questionType: "rating",
          isRequired: true,
          displayOrder: 1,
        },
        {
          questionText: "Which courses or features did you use most frequently?",
          questionType: "multiple_choice",
          options: JSON.stringify([
            "Louisiana Journeyman Prep",
            "Calculator Tools", 
            "AI Mentor",
            "Photo Code Checker",
            "Practice Tests",
            "Job Board"
          ]),
          isRequired: true,
          displayOrder: 2,
        },
        {
          questionText: "What features or improvements would you like to see added?",
          questionType: "textarea",
          isRequired: true,
          displayOrder: 3,
        },
        {
          questionText: "Did you encounter any technical issues or bugs?",
          questionType: "boolean",
          isRequired: true,
          displayOrder: 4,
        },
        {
          questionText: "If yes, please describe the technical issues you experienced:",
          questionType: "textarea",
          isRequired: false,
          displayOrder: 5,
        },
        {
          questionText: "How helpful was our customer support (if you contacted us)?",
          questionType: "multiple_choice",
          options: JSON.stringify([
            "Very Helpful",
            "Helpful", 
            "Somewhat Helpful",
            "Not Helpful",
            "Didn't Contact Support"
          ]),
          isRequired: false,
          displayOrder: 6,
        },
        {
          questionText: "Would you recommend LA Plumb Prep to other plumbing professionals?",
          questionType: "boolean",
          isRequired: true,
          displayOrder: 7,
        },
        {
          questionText: "Any additional comments or suggestions?",
          questionType: "textarea",
          isRequired: false,
          displayOrder: 8,
        }
      ];

      for (const question of defaultQuestions) {
        await db.insert(betaFeedbackQuestions).values(question);
      }

      console.log("Default feedback questions initialized");
    }
  }

  // Create monthly feedback campaign
  static async createMonthlyCampaign(monthYear: string) {
    const existingCampaign = await db.select()
      .from(betaFeedbackCampaigns)
      .where(eq(betaFeedbackCampaigns.monthYear, monthYear))
      .limit(1);

    if (existingCampaign.length > 0) {
      console.log(`Campaign for ${monthYear} already exists`);
      return existingCampaign[0];
    }

    const dueDate = new Date();
    dueDate.setMonth(dueDate.getMonth() + 1);
    dueDate.setDate(0); // Last day of current month

    const campaign = await db.insert(betaFeedbackCampaigns).values({
      title: `Monthly Feedback - ${this.formatMonthYear(monthYear)}`,
      description: "Help us improve LA Plumb Prep with your monthly feedback",
      monthYear,
      dueDate,
    }).returning();

    return campaign[0];
  }

  // Send feedback emails to all beta testers
  static async sendMonthlyFeedbackEmails() {
    const currentMonthYear = this.getCurrentMonthYear();

    // Create or get current month's campaign
    const campaign = await this.createMonthlyCampaign(currentMonthYear);

    // Get all beta testers who haven't responded yet
    const betaTesters = await db.select({
      id: users.id,
      email: users.email,
      firstName: users.firstName,
      lastName: users.lastName
    })
    .from(users)
    .where(
      and(
        eq(users.isBetaTester, true),
        eq(users.isActive, true)
      )
    );

    // Check who hasn't responded yet
    const existingResponses = await db.select({ userId: betaFeedbackResponses.userId })
      .from(betaFeedbackResponses)
      .where(eq(betaFeedbackResponses.campaignId, campaign.id));

    const respondedUserIds = new Set(existingResponses.map(r => r.userId));
    const unrespondedTesters = betaTesters.filter(user => !respondedUserIds.has(user.id));

    console.log(`Sending feedback emails to ${unrespondedTesters.length} beta testers for ${currentMonthYear}`);

    // Send emails to unresponded beta testers
    for (const tester of unrespondedTesters) {
      const submissionToken = randomUUID();

      // Create response record with token
      await db.insert(betaFeedbackResponses).values({
        campaignId: campaign.id,
        userId: tester.id,
        responses: {},
        submissionToken,
        completedAt: null, // Will be set when submitted
      });

      // Queue email
      const feedbackUrl = `${process.env.BASE_URL || 'http://localhost:5000'}/beta-feedback?token=${submissionToken}`;

      await db.insert(emailQueue).values({
        recipientEmail: tester.email,
        subject: `🔧 Monthly Beta Feedback Required - Keep Your Discount!`,
        content: this.generateFeedbackEmail(tester.firstName || 'Beta Tester', feedbackUrl, campaign.dueDate.toLocaleDateString()),
        scheduledFor: new Date(),
      });
    }

    // Mark campaign as email sent
    await db.update(betaFeedbackCampaigns)
      .set({ emailSentAt: new Date() })
      .where(eq(betaFeedbackCampaigns.id, campaign.id));

    return {
      campaignId: campaign.id,
      emailsSent: unrespondedTesters.length,
      totalBetaTesters: betaTesters.length
    };
  }

  // Get feedback campaign and questions by token
  static async getFeedbackByToken(token: string) {
    const response = await db.select({
      campaignId: betaFeedbackResponses.campaignId,
      userId: betaFeedbackResponses.userId,
      completed: betaFeedbackResponses.completedAt,
    })
    .from(betaFeedbackResponses)
    .where(eq(betaFeedbackResponses.submissionToken, token))
    .limit(1);

    if (response.length === 0) {
      throw new Error("Invalid feedback token");
    }

    const responseRecord = response[0];

    if (responseRecord.completed) {
      throw new Error("Feedback already submitted");
    }

    // Get campaign details
    const campaign = await db.select()
      .from(betaFeedbackCampaigns)
      .where(eq(betaFeedbackCampaigns.id, responseRecord.campaignId))
      .limit(1);

    if (campaign.length === 0) {
      throw new Error("Campaign not found");
    }

    // Get questions
    const questions = await db.select()
      .from(betaFeedbackQuestions)
      .where(eq(betaFeedbackQuestions.isActive, true))
      .orderBy(betaFeedbackQuestions.displayOrder);

    return {
      campaign: campaign[0],
      questions: questions.map(q => ({
        ...q,
        options: q.options ? JSON.parse(q.options as string) : undefined
      }))
    };
  }

  // Submit feedback response
  static async submitFeedback(token: string, responses: Record<string, any>) {
    const responseRecord = await db.select()
      .from(betaFeedbackResponses)
      .where(eq(betaFeedbackResponses.submissionToken, token))
      .limit(1);

    if (responseRecord.length === 0) {
      throw new Error("Invalid feedback token");
    }

    if (responseRecord[0].completedAt) {
      throw new Error("Feedback already submitted");
    }

    // Update response with answers and completion time
    await db.update(betaFeedbackResponses)
      .set({ 
        responses: responses,
        completedAt: new Date()
      })
      .where(eq(betaFeedbackResponses.submissionToken, token));

    console.log(`Feedback submitted for token: ${token}`);
    return true;
  }

  // Get feedback analytics for admin
  static async getFeedbackAnalytics(monthYear?: string) {
    const targetMonth = monthYear || this.getCurrentMonthYear();

    const campaign = await db.select()
      .from(betaFeedbackCampaigns)
      .where(eq(betaFeedbackCampaigns.monthYear, targetMonth))
      .limit(1);

    if (campaign.length === 0) {
      return { campaign: null, stats: null, responses: [] };
    }

    // Get response stats
    const allResponses = await db.select({
      userId: betaFeedbackResponses.userId,
      responses: betaFeedbackResponses.responses,
      completedAt: betaFeedbackResponses.completedAt,
      userEmail: users.email,
      userName: users.firstName
    })
    .from(betaFeedbackResponses)
    .leftJoin(users, eq(betaFeedbackResponses.userId, users.id))
    .where(eq(betaFeedbackResponses.campaignId, campaign[0].id));

    const completedResponses = allResponses.filter(r => r.completedAt);
    const totalBetaTesters = await db.select({ count: users.id })
      .from(users)
      .where(and(eq(users.isBetaTester, true), eq(users.isActive, true)));

    return {
      campaign: campaign[0],
      stats: {
        totalSent: allResponses.length,
        completed: completedResponses.length,
        completionRate: allResponses.length > 0 ? (completedResponses.length / allResponses.length) * 100 : 0,
        totalBetaTesters: totalBetaTesters.length
      },
      responses: completedResponses
    };
  }

  // Helper methods
  private static getCurrentMonthYear(): string {
    const now = new Date();
    return `${now.getFullYear()}-${String(now.getMonth() + 1).padStart(2, '0')}`;
  }

  private static formatMonthYear(monthYear: string): string {
    const [year, month] = monthYear.split('-');
    const date = new Date(parseInt(year), parseInt(month) - 1);
    return date.toLocaleDateString('en-US', { month: 'long', year: 'numeric' });
  }

  private static generateFeedbackEmail(firstName: string, feedbackUrl: string, dueDate: string): string {
    return `
      <!DOCTYPE html>
      <html>
      <head>
        <meta charset="utf-8">
        <meta name="viewport" content="width=device-width, initial-scale=1">
        <title>Monthly Beta Feedback - LA Plumb Prep</title>
        <style>
          body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
          .container { max-width: 600px; margin: 0 auto; padding: 20px; }
          .header { background: #1a365d; color: white; padding: 20px; text-align: center; }
          .content { padding: 30px 20px; }
          .warning-box { background: #fff3cd; border: 1px solid #ffeaa7; padding: 15px; margin: 20px 0; border-radius: 5px; }
          .cta-button { display: inline-block; background: #3182ce; color: white; text-decoration: none; padding: 15px 30px; border-radius: 5px; font-weight: bold; margin: 20px 0; }
          .footer { background: #f8f9fa; padding: 15px; text-align: center; font-size: 12px; color: #666; }
        </style>
      </head>
      <body>
        <div class="container">
          <div class="header">
            <h1>🔧 LA Plumb Prep</h1>
            <p>Beta Feedback Program</p>
          </div>

          <div class="content">
            <h2>Hi ${firstName}!</h2>

            <p>It's that time of the month again! As one of our valued beta testers, we need your feedback to continue improving LA Plumb Prep.</p>

            <div class="warning-box">
              <strong>⚠️ Action Required:</strong> Complete this feedback survey by <strong>${dueDate}</strong> to maintain your beta status and locked-in pricing for all courses.
            </div>

            <h3>Why Your Feedback Matters:</h3>
            <ul>
              <li>Help shape new features and improvements</li>
              <li>Report bugs and technical issues</li>
              <li>Guide our development roadmap</li>
              <li>Improve the experience for all users</li>
            </ul>

            <h3>Beta Tester Benefits (Reminder):</h3>
            <ul>
              <li>✅ Keep your discounted pricing forever</li>
              <li>✅ Early access to new features</li>
              <li>✅ Direct influence on product development</li>
              <li>✅ Priority customer support</li>
            </ul>

            <div style="text-align: center; margin: 30px 0;">
              <a href="${feedbackUrl}" class="cta-button">Complete Monthly Feedback</a>
            </div>

            <p><strong>Important:</strong> This survey should only take 3-5 minutes to complete. Your continued participation as a beta tester ensures you maintain your special pricing on all current and future courses.</p>

            <p>Thank you for being part of our beta program and helping us build the best plumbing education platform!</p>

            <p>Best regards,<br>The LA Plumb Prep Team</p>
          </div>

          <div class="footer">
            <p>LA Plumb Prep - Official Louisiana Plumbing Certification Preparation</p>
            <p>This email was sent to beta program participants only.</p>
          </div>
        </div>
      </body>
      </html>
    `;
  }

  // Monitor and analyze feedback for concerning patterns
  static async analyzeFeedbackPatterns() {
    try {
      // Get recent feedback responses
      const recentResponses = await db.select({
        id: betaFeedbackResponses.id,
        userId: betaFeedbackResponses.userId,
        campaignId: betaFeedbackResponses.campaignId,
        responses: betaFeedbackResponses.responses,
        completedAt: betaFeedbackResponses.completedAt,
        user: {
          firstName: users.firstName,
          lastName: users.lastName,
          email: users.email
        }
      })
      .from(betaFeedbackResponses)
      .leftJoin(users, eq(betaFeedbackResponses.userId, users.id))
      .where(isNull(betaFeedbackResponses.deletedAt));

      const alerts: string[] = [];
      const criticalIssues: string[] = [];

      // Analyze satisfaction scores (Question 1 - Overall experience rating)
      const satisfactionScores = recentResponses
        .map(r => {
          if (typeof r.responses === 'object' && r.responses) {
            const responses = r.responses as any;
            return responses['1'] ? parseInt(responses['1']) : null;
          }
          return null;
        })
        .filter(score => score !== null);

      if (satisfactionScores.length > 0) {
        const avgSatisfaction = satisfactionScores.reduce((a, b) => a + b, 0) / satisfactionScores.length;
        const lowScores = satisfactionScores.filter(score => score <= 2).length;
        const lowScorePercentage = (lowScores / satisfactionScores.length) * 100;

        // Alert thresholds
        if (avgSatisfaction < 3.0) {
          criticalIssues.push(`🚨 CRITICAL: Average satisfaction score is ${avgSatisfaction.toFixed(1)}/5 - Immediate attention required`);
        } else if (avgSatisfaction < 3.5) {
          alerts.push(`⚠️ Warning: Average satisfaction score declining to ${avgSatisfaction.toFixed(1)}/5`);
        }

        if (lowScorePercentage > 25) {
          criticalIssues.push(`🚨 CRITICAL: ${lowScorePercentage.toFixed(1)}% of users rated experience 2/5 or lower`);
        } else if (lowScorePercentage > 15) {
          alerts.push(`⚠️ Warning: ${lowScorePercentage.toFixed(1)}% of users giving low satisfaction scores`);
        }
      }

      // Check completion rates
      const campaigns = await db.select()
        .from(betaFeedbackCampaigns)
        .where(isNull(betaFeedbackCampaigns.deletedAt));

      const latestCampaign = campaigns[campaigns.length - 1];
      if (latestCampaign) {
        const campaignResponses = recentResponses.filter(r => r.campaignId === latestCampaign.id);
        const totalBetaTesters = await db.select().from(users).where(eq(users.isBetaTester, true));
        const completionRate = (campaignResponses.length / totalBetaTesters.length) * 100;

        if (completionRate < 30) {
          criticalIssues.push(`🚨 CRITICAL: Only ${completionRate.toFixed(1)}% feedback completion rate - System may have issues`);
        } else if (completionRate < 50) {
          alerts.push(`⚠️ Warning: Low feedback completion rate at ${completionRate.toFixed(1)}%`);
        }
      }

      // Analyze common improvement requests for bug reports
      const improvementRequests = recentResponses
        .map(r => {
          if (typeof r.responses === 'object' && r.responses) {
            const responses = r.responses as any;
            return responses['3'] || ''; // Question 3 - improvement requests
          }
          return '';
        })
        .filter(text => text.length > 10);

      const problemKeywords = ['bug', 'error', 'broken', 'not working', 'slow', 'crash', 'issue', 'problem'];
      const bugReports = improvementRequests.filter(text => 
        problemKeywords.some(keyword => text.toLowerCase().includes(keyword))
      );

      if (bugReports.length > improvementRequests.length * 0.3) {
        criticalIssues.push(`🚨 CRITICAL: ${bugReports.length} users reporting bugs or technical issues`);
      } else if (bugReports.length > 2) {
        alerts.push(`⚠️ Warning: ${bugReports.length} users mentioning technical problems`);
      }

      return {
        alerts,
        criticalIssues,
        totalResponses: recentResponses.length,
        avgSatisfaction: satisfactionScores.length > 0 ? satisfactionScores.reduce((a, b) => a + b, 0) / satisfactionScores.length : 0,
        bugReports: bugReports.length,
        satisfactionScores
      };
    } catch (error) {
      console.error('Error analyzing feedback patterns:', error);
      return { alerts: [], criticalIssues: ['🚨 Error analyzing feedback patterns'], totalResponses: 0, avgSatisfaction: 0, bugReports: 0, satisfactionScores: [] };
    }
  }

  // Send alert emails to admin
  static async sendAdminAlerts(alerts: string[], criticalIssues: string[]) {
    if (alerts.length === 0 && criticalIssues.length === 0) return;

    const adminEmail = process.env.SUPPORT_EMAIL || 'admin@laplumbprep.com';
    const subject = criticalIssues.length > 0 
      ? '🚨 CRITICAL: Beta Feedback Alerts Requiring Immediate Attention'
      : '⚠️ Beta Feedback Monitoring Alerts';

    const content = `
      <div style="font-family: Arial, sans-serif; max-width: 600px; margin: 0 auto; padding: 20px;">
        <h2 style="color: #1e40af; margin-bottom: 30px;">Beta Feedback System Alert</h2>

        ${criticalIssues.length > 0 ? `
          <div style="background: #fee2e2; border: 2px solid #dc2626; border-radius: 8px; padding: 20px; margin-bottom: 20px;">
            <h3 style="color: #dc2626; margin-top: 0;">🚨 Critical Issues Requiring Immediate Action</h3>
            <ul style="margin: 0;">
              ${criticalIssues.map(issue => `<li style="color: #7f1d1d; margin-bottom: 8px;">${issue}</li>`).join('')}
            </ul>
          </div>
        ` : ''}

        ${alerts.length > 0 ? `
          <div style="background: #fef3c7; border: 2px solid #d97706; border-radius: 8px; padding: 20px; margin-bottom: 20px;">
            <h3 style="color: #d97706; margin-top: 0;">⚠️ Warning Alerts</h3>
            <ul style="margin: 0;">
              ${alerts.map(alert => `<li style="color: #92400e; margin-bottom: 8px;">${alert}</li>`).join('')}
            </ul>
          </div>
        ` : ''}

        <div style="background: #f3f4f6; border-radius: 8px; padding: 20px; margin-top: 20px;">
          <h4 style="margin-top: 0; color: #374151;">Recommended Actions:</h4>
          <ul style="color: #6b7280;">
            <li>Review detailed feedback responses in the admin dashboard</li>
            <li>Prioritize fixes for critical issues affecting user satisfaction</li>
            <li>Consider reaching out directly to users with low satisfaction scores</li>
            <li>Monitor completion rates for technical issues</li>
          </ul>
        </div>
      </div>
    `;

    // Queue admin alert email - temporarily disabled to prevent errors
    console.log("Beta feedback admin alert would be sent:", subject);
    // TODO: Fix email queue schema issues before re-enabling
    // await db.insert(emailQueue).values({
    //   recipientEmail: adminEmail,
    //   subject,
    //   content,
    //   scheduledFor: new Date(),
    // });
  }

  // Run automated monitoring and send alerts if needed
  static async runAutomatedMonitoring() {
    try {
      const analysis = await this.analyzeFeedbackPatterns();

      if (analysis.alerts.length > 0 || analysis.criticalIssues.length > 0) {
        await this.sendAdminAlerts(analysis.alerts, analysis.criticalIssues);
        console.log(`Beta feedback monitoring: ${analysis.criticalIssues.length} critical issues, ${analysis.alerts.length} warnings`);
      }

      return analysis;
    } catch (error) {
      console.error('Error running feedback monitoring:', error);
      return null;
    }
  }
}

// Initialize the feedback system
export const initializeBetaFeedbackSystem = async () => {
  try {
    await BetaFeedbackService.initializeDefaultQuestions();
    console.log("Beta feedback system initialized");
  } catch (error) {
    console.error("Error initializing beta feedback system:", error);
  }
};

