              </ul>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/admin/content-import.ts.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Upload, CheckCircle, AlertCircle, FileText } from "lucide-react";

interface ParsedContent {
  question: string;
  answer: string;
  keywords: string[];
  contentId?: string;
}

export default function ContentImport() {
  const [rawContent, setRawContent] = useState("");
  const [contentId, setContentId] = useState("");
  const [parsedContent, setParsedContent] = useState<ParsedContent[]>([]);
  const [isProcessing, setIsProcessing] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const importMutation = useMutation({
    mutationFn: (content: ParsedContent[]) => 
      apiRequest("POST", "/api/admin/import-chat-content", { content }),
    onSuccess: () => {
      toast({
        title: "Content Imported Successfully",
        description: `${parsedContent.length} chat answers have been added to the system.`,
      });
      setRawContent("");
      setParsedContent([]);
      setContentId("");
    },
    onError: (error: any) => {
      toast({
        title: "Import Failed",
        description: error.message || "Failed to import content",
        variant: "destructive",
      });
    },
  });

  const parseContent = () => {
    setIsProcessing(true);

    try {
      const lines = rawContent.split('\n').filter(line => line.trim());
      const parsed: ParsedContent[] = [];
      let currentQuestion = "";
      let currentAnswer = "";
      let isAnswerMode = false;

      for (let i = 0; i < lines.length; i++) {
        const line = lines[i].trim();

        // Skip empty lines and emojis/icons
        if (!line || line.match(/^[🧠💡📚]+/) || line.startsWith('🧠 Key Concept:') || line.startsWith('💡 Key Information:')) {
          continue;
        }

        // Detect questions (lines ending with ?)
        if (line.endsWith('?')) {
          // Save previous question/answer pair if exists
          if (currentQuestion && currentAnswer) {
            const keywords = extractKeywords(currentQuestion, currentAnswer);
            parsed.push({
              question: currentQuestion,
              answer: currentAnswer.trim(),
              keywords,
              contentId: contentId || undefined
            });
          }

          currentQuestion = line;
          currentAnswer = "";
          isAnswerMode = true;
        } 
        // This is part of an answer
        else if (isAnswerMode && currentQuestion) {
          currentAnswer += line + "\n\n";
        }
        // Handle standalone informational content as Q&A
        else if (!currentQuestion && line.length > 20) {
          // Create a question from the content
          const generatedQuestion = generateQuestionFromContent(line);
          if (generatedQuestion) {
            const keywords = extractKeywords(generatedQuestion, line);
            parsed.push({
              question: generatedQuestion,
              answer: line,
              keywords,
              contentId: contentId || undefined
            });
          }
        }
      }

      // Handle the last question/answer pair
      if (currentQuestion && currentAnswer) {
        const keywords = extractKeywords(currentQuestion, currentAnswer);
        parsed.push({
          question: currentQuestion,
          answer: currentAnswer.trim(),
          keywords,
          contentId: contentId || undefined
        });
      }

      setParsedContent(parsed);
      toast({
        title: "Content Parsed Successfully",
        description: `Found ${parsed.length} question/answer pairs`,
      });
    } catch (error) {
      toast({
        title: "Parsing Failed",
        description: "Please check your content format and try again",
        variant: "destructive",
      });
    }

    setIsProcessing(false);
  };

  const extractKeywords = (question: string, answer: string): string[] => {
    const text = `${question} ${answer}`.toLowerCase();
    const keywords = new Set<string>();

    // Extract important terms
    const patterns = [
      /louisiana state plumbing code|lspc/g,
      /department of health and hospitals/g,
      /enforcement|authority|delegat/g,
      /legal basis|statutory/g,
      /r\.s\. \d+:\d+/g,
      /historical|promulgat|amend/g,
      /section \d+|§\d+/g,
      /state health officer/g,
      /violation|code/g
    ];

    patterns.forEach(pattern => {
      const matches = text.match(pattern);
      if (matches) {
        matches.forEach(match => keywords.add(match.trim()));
      }
    });

    // Add key individual words
    const importantWords = text.match(/\b(?:enforcement|authority|legal|basis|delegation|violation|code|plumbing|louisiana|health|officer|section|historical|promulgated|amended)\b/g);
    if (importantWords) {
      importantWords.forEach(word => keywords.add(word));
    }

    return Array.from(keywords);
  };

  const generateQuestionFromContent = (content: string): string | null => {
    if (content.includes("Louisiana State Plumbing Code")) {
      return "What is the Louisiana State Plumbing Code (LSPC)?";
    }
    if (content.includes("Department of Health and Hospitals")) {
      return "Who enforces the LSPC?";
    }
    if (content.includes("legal foundation") || content.includes("R.S.")) {
      return "What is the legal basis for the LSPC?";
    }
    if (content.includes("historical") || content.includes("promulgated")) {
      return "Tell me about the historical notes.";
    }
    if (content.includes("enforcement") || content.includes("authority")) {
      return "How is the LSPC enforced?";
    }
    return null;
  };

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center space-x-2">
        <FileText className="w-6 h-6" />
        <h1 className="text-2xl font-bold">Import Chat Content</h1>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Import Form */}
        <Card data-testid="content-import-form">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Upload className="w-5 h-5" />
              <span>Import Educational Content</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="content-id">Content ID (Optional)</Label>
              <Input
                id="content-id"
                placeholder="e.g., section-101, lesson-1"
                value={contentId}
                onChange={(e) => setContentId(e.target.value)}
                data-testid="input-content-id"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="raw-content">Paste Your Educational Content</Label>
              <Textarea
                id="raw-content"
                placeholder="Paste your formatted content here..."
                className="h-64"
                value={rawContent}
                onChange={(e) => setRawContent(e.target.value)}
                data-testid="textarea-raw-content"
              />
            </div>

            <div className="flex space-x-2">
              <Button
                onClick={parseContent}
                disabled={!rawContent.trim() || isProcessing}
                data-testid="button-parse-content"
              >
                <FileText className="w-4 h-4 mr-2" />
                {isProcessing ? "Parsing..." : "Parse Content"}
              </Button>

              {parsedContent.length > 0 && (
                <Button
                  onClick={() => importMutation.mutate(parsedContent)}
                  disabled={importMutation.isPending}
                  data-testid="button-import-content"
                >
                  <Upload className="w-4 h-4 mr-2" />
                  {importMutation.isPending ? "Importing..." : `Import ${parsedContent.length} Items`}
                </Button>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Preview */}
        <Card data-testid="content-preview">
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <CheckCircle className="w-5 h-5" />
              <span>Parsed Content Preview</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            {parsedContent.length === 0 ? (
              <p className="text-muted-foreground text-center py-8">
                No content parsed yet. Paste your content and click "Parse Content" to preview.
              </p>
            ) : (
              <div className="space-y-4 max-h-96 overflow-y-auto">
                {parsedContent.map((item, index) => (
                  <div key={index} className="border rounded-lg p-4 space-y-2" data-testid={`parsed-item-${index}`}>
                    <div className="font-medium text-sm text-primary">
                      Q: {item.question}
                    </div>
                    <div className="text-sm text-muted-foreground line-clamp-3">
                      {item.answer.substring(0, 150)}...
                    </div>
                    <div className="flex flex-wrap gap-1">
                      {item.keywords.slice(0, 5).map((keyword, i) => (
                        <Badge key={i} variant="outline" className="text-xs">
                          {keyword}
                        </Badge>
                      ))}
                      {item.keywords.length > 5 && (
                        <Badge variant="outline" className="text-xs">
                          +{item.keywords.length - 5} more
                        </Badge>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      {/* Instructions */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <AlertCircle className="w-5 h-5" />
            <span>How to Use</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-2 text-sm text-muted-foreground">
            <p>1. <strong>Paste your educational content</strong> in the text area above</p>
            <p>2. <strong>Add a Content ID</strong> (optional) to associate with specific lessons</p>
            <p>3. <strong>Click "Parse Content"</strong> to automatically extract questions and answers</p>
            <p>4. <strong>Review the preview</strong> to ensure content was parsed correctly</p>
            <p>5. <strong>Click "Import"</strong> to add the content to the AI mentor system</p>
            <p className="mt-3 p-3 bg-muted rounded-lg">
              <strong>Supported Format:</strong> The parser looks for questions ending with "?" and treats the following text as answers. It also automatically generates questions from informational content about the Louisiana Plumbing Code.
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/admin/podcast-import.ts.content:
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Link } from "wouter";
import { ArrowLeft, Mic, Upload, Play, User, UserCheck, Save } from "lucide-react";
import { useState } from "react";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

export default function PodcastImport() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [podcastData, setPodcastData] = useState({
    title: "",
    description: "",
    hostName: "Host",
    guestName: "Guest",
    content: ""
  });

  const [parsedSegments, setParsedSegments] = useState<Array<{
    speaker: "host" | "guest";
    text: string;
  }>>([]);

  const createPodcastMutation = useMutation({
    mutationFn: async (data: any) => {
      return apiRequest("POST", "/api/admin/podcasts", data);
    },
    onSuccess: () => {
      toast({
        title: "Podcast Created",
        description: "Your podcast has been successfully imported and is ready to publish.",
      });
      setPodcastData({
        title: "",
        description: "",
        hostName: "Host",
        guestName: "Guest",
        content: ""
      });
      setParsedSegments([]);
      queryClient.invalidateQueries({ queryKey: ["/api/podcasts"] });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create podcast. Please try again.",
        variant: "destructive",
      });
    },
  });

  const parseContent = () => {
    if (!podcastData.content.trim()) {
      toast({
        title: "No Content",
        description: "Please paste your podcast content first.",
        variant: "destructive",
      });
      return;
    }

    const lines = podcastData.content.split('\n').filter(line => line.trim());
    const segments: Array<{ speaker: "host" | "guest"; text: string }> = [];

    // Parse alternating host/guest format
    lines.forEach((line, index) => {
      const trimmedLine = line.trim();
      if (trimmedLine) {
        // Alternate between host (even indices) and guest (odd indices)
        const speaker = index % 2 === 0 ? "host" : "guest";
        segments.push({
          speaker,
          text: trimmedLine
        });
      }
    });

    setParsedSegments(segments);

    toast({
      title: "Content Parsed",
      description: `Found ${segments.length} segments (${segments.filter(s => s.speaker === 'host').length} host, ${segments.filter(s => s.speaker === 'guest').length} guest)`,
    });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!podcastData.title.trim()) {
      toast({
        title: "Missing Title",
        description: "Please enter a podcast title.",
        variant: "destructive",
      });
      return;
    }

    if (parsedSegments.length === 0) {
      toast({
        title: "No Segments",
        description: "Please parse your content first to create segments.",
        variant: "destructive",
      });
      return;
    }

    createPodcastMutation.mutate({
      title: podcastData.title,
      description: podcastData.description,
      hostName: podcastData.hostName,
      guestName: podcastData.guestName,
      segments: parsedSegments,
      totalSegments: parsedSegments.length,
      duration: Math.round(parsedSegments.length * 15), // Estimate 15 seconds per segment
      status: "draft"
    });
  };

  const updateField = (field: string, value: string) => {
    setPodcastData(prev => ({ ...prev, [field]: value }));
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="mb-8 flex items-center gap-4">
          <Link href="/admin">
            <Button variant="outline" size="sm">
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back to Admin
            </Button>
          </Link>
          <div>
            <h1 className="text-3xl font-bold mb-2" data-testid="podcast-import-title">
              Podcast Import
            </h1>
            <p className="text-muted-foreground">
              Import conversation content to create podcast episodes with host and guest segments
            </p>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Podcast Details */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Mic className="h-5 w-5" />
                Podcast Details
              </CardTitle>
              <CardDescription>
                Enter the basic information for your podcast episode
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="title">Episode Title *</Label>
                <Input
                  id="title"
                  value={podcastData.title}
                  onChange={(e) => updateField("title", e.target.value)}
                  placeholder="e.g., Louisiana State Plumbing Code §101 Administration"
                  required
                />
              </div>

              <div>
                <Label htmlFor="description">Episode Description</Label>
                <Textarea
                  id="description"
                  value={podcastData.description}
                  onChange={(e) => updateField("description", e.target.value)}
                  placeholder="Brief description of what this episode covers..."
                  rows={3}
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="hostName">Host Name</Label>
                  <Input
                    id="hostName"
                    value={podcastData.hostName}
                    onChange={(e) => updateField("hostName", e.target.value)}
                    placeholder="Host"
                  />
                </div>
                <div>
                  <Label htmlFor="guestName">Guest Name</Label>
                  <Input
                    id="guestName"
                    value={podcastData.guestName}
                    onChange={(e) => updateField("guestName", e.target.value)}
                    placeholder="Guest"
                  />
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Content Import */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Upload className="h-5 w-5" />
                Content Import
              </CardTitle>
              <CardDescription>
                Paste your alternating host/guest conversation content. Each line will alternate between host and guest automatically.
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="content">Conversation Content</Label>
                <Textarea
                  id="content"
                  value={podcastData.content}
                  onChange={(e) => updateField("content", e.target.value)}
                  placeholder="Paste your conversation content here... First line = Host, Second line = Guest, Third line = Host, etc."
                  rows={12}
                  className="font-mono text-sm"
                />
                <p className="text-sm text-muted-foreground mt-2">
                  Format: Line 1 (Host) → Line 2 (Guest) → Line 3 (Host) → Line 4 (Guest)...
                </p>
              </div>

              <Button 
                type="button" 
                onClick={parseContent}
                variant="outline"
                disabled={!podcastData.content.trim()}
              >
                <Play className="h-4 w-4 mr-2" />
                Parse Content
              </Button>
            </CardContent>
          </Card>

          {/* Preview Segments */}
          {parsedSegments.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle>Content Preview</CardTitle>
                <CardDescription>
                  Review the parsed segments before creating the podcast
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-3 max-h-96 overflow-y-auto">
                  {parsedSegments.map((segment, index) => (
                    <div 
                      key={index}
                      className={`p-3 rounded-lg border ${
                        segment.speaker === 'host' 
                          ? 'bg-blue-50 border-blue-200' 
                          : 'bg-green-50 border-green-200'
                      }`}
                    >
                      <div className="flex items-center gap-2 mb-2">
                        {segment.speaker === 'host' ? (
                          <User className="h-4 w-4 text-blue-600" />
                        ) : (
                          <UserCheck className="h-4 w-4 text-green-600" />
                        )}
                        <Badge variant={segment.speaker === 'host' ? 'default' : 'secondary'}>
                          {segment.speaker === 'host' ? podcastData.hostName : podcastData.guestName}
                        </Badge>
                        <span className="text-sm text-muted-foreground">#{index + 1}</span>
                      </div>
                      <p className="text-sm">{segment.text}</p>
                    </div>
                  ))}
                </div>

                <div className="mt-4 p-3 bg-muted rounded-lg">
                  <div className="grid grid-cols-3 gap-4 text-sm">
                    <div>
                      <span className="font-medium">Total Segments:</span>
                      <p className="text-lg">{parsedSegments.length}</p>
                    </div>
                    <div>
                      <span className="font-medium">Host Lines:</span>
                      <p className="text-lg text-blue-600">
                        {parsedSegments.filter(s => s.speaker === 'host').length}
                      </p>
                    </div>
                    <div>
                      <span className="font-medium">Guest Lines:</span>
                      <p className="text-lg text-green-600">
                        {parsedSegments.filter(s => s.speaker === 'guest').length}
                      </p>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Submit */}
          <div className="flex justify-end">
            <Button 
              type="submit" 
              disabled={createPodcastMutation.isPending || parsedSegments.length === 0}
              className="w-full sm:w-auto"
            >
              <Save className="h-4 w-4 mr-2" />
              {createPodcastMutation.isPending ? "Creating Podcast..." : "Create Podcast"}
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
}

scripts/import-data.js.content:
import { drizzle } from 'drizzle-orm/neon-serverless';
import { Pool } from '@neondatabase/serverless';
import * as schema from '../shared/schema.js';
import fs from 'fs';
import path from 'path';

if (!process.env.DATABASE_URL) {
  throw new Error('DATABASE_URL environment variable is required');
}

const pool = new Pool({ connectionString: process.env.DATABASE_URL });
const db = drizzle(pool, { schema });

async function importData() {
  try {
    console.log('Starting data import...');

    // Import courses
    if (fs.existsSync('./data/courses.json')) {
      const coursesData = JSON.parse(fs.readFileSync('./data/courses.json', 'utf8'));
      if (coursesData.length > 0) {
        await db.insert(schema.courses).values(coursesData).onConflictDoNothing();
        console.log(`Imported ${coursesData.length} courses`);
      }
    }

    // Import course content
    if (fs.existsSync('./data/course_content.json')) {
      const contentData = JSON.parse(fs.readFileSync('./data/course_content.json', 'utf8'));
      if (contentData.length > 0) {
        await db.insert(schema.courseContent).values(contentData).onConflictDoNothing();
        console.log(`Imported ${contentData.length} course content items`);
      }
    }

    // Import course enrollments
    if (fs.existsSync('./data/course_enrollments.json')) {
      const enrollmentData = JSON.parse(fs.readFileSync('./data/course_enrollments.json', 'utf8'));
      if (enrollmentData.length > 0) {
        await db.insert(schema.courseEnrollments).values(enrollmentData).onConflictDoNothing();
        console.log(`Imported ${enrollmentData.length} course enrollments`);
      }
    }

    console.log('Data import completed successfully!');
  } catch (error) {
    console.error('Error importing data:', error);
    process.exit(1);
  } finally {
    await pool.end();
  }
}

importData();

server/competitionNotifications.ts.content:
import { db } from "./db";
import { users, monthlyCompetitions, competitionNotifications, emailQueue } from "@shared/schema";
import { eq, and, sql } from "drizzle-orm";
import { format, subDays, addHours } from "date-fns";

export class CompetitionNotificationService {

  // Schedule all notifications for a competition
  async scheduleCompetitionNotifications(competitionId: string) {
    const competition = await this.getCompetition(competitionId);
    if (!competition) return;

    const activeUsers = await this.getActiveUsers();

    for (const user of activeUsers) {
      // Schedule advance notice (3 days before)
      await this.scheduleNotification(
        competitionId,
        user.id,
        "advance_notice",
        "Monthly Competition Reminder",
        `Get ready! The ${competition.title} starts ${format(new Date(competition.startDate), "MMMM d")}. Test your Louisiana plumbing code knowledge and compete for subscription rewards!`,
        subDays(new Date(competition.startDate), 3)
      );

      // Schedule day-of notification (competition start time)
      await this.scheduleNotification(
        competitionId,
        user.id,
        "day_of",
        "🏆 Competition is LIVE NOW!",
        `The ${competition.title} is now active! You have ${competition.timeLimit} minutes to complete ${competition.questionCount} questions. Winners get free subscription time!`,
        new Date(competition.startDate)
      );
    }
  }

  // Schedule a single notification
  private async scheduleNotification(
    competitionId: string,
    userId: string,
    type: string,
    title: string,
    message: string,
    scheduledTime: Date
  ) {
    // Create dashboard/in-app notification
    await db.insert(competitionNotifications).values({
      competitionId,
      userId,
      notificationType: type,
      channel: "dashboard",
      title,
      message,
    });

    // Schedule email notification
    const user = await db.select().from(users).where(eq(users.id, userId)).limit(1);
    if (user[0]?.email) {
      await this.scheduleEmail(
        user[0].email,
        user[0].firstName || "Student",
        title,
        message,
        competitionId,
        scheduledTime
      );
    }
  }

  // Schedule email notification
  private async scheduleEmail(
    email: string,
    firstName: string,
    subject: string,
    message: string,
    competitionId: string,
    scheduledTime: Date
  ) {
    const emailTemplate = this.generateEmailTemplate(firstName, subject, message, competitionId);

    await db.insert(emailQueue).values({
      recipientEmail: email,
      senderEmail: process.env.NOREPLY_USER || "noreply@latrainer.com",
      subject,
      htmlContent: emailTemplate,
      textContent: message,
      scheduledFor: scheduledTime,
      status: "pending",
      retryCount: 0,
    });
  }

  // Generate HTML email template
  private generateEmailTemplate(firstName: string, subject: string, message: string, competitionId: string): string {
    return `
    <!DOCTYPE html>
    <html>
    <head>
      <meta charset="utf-8">
      <title>${subject}</title>
      <style>
        body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
        .container { max-width: 600px; margin: 0 auto; padding: 20px; }
        .header { background: linear-gradient(135deg, #3B82F6, #1D4ED8); color: white; padding: 30px; text-align: center; border-radius: 8px 8px 0 0; }
        .content { background: #fff; padding: 30px; border: 1px solid #e5e7eb; }
        .footer { background: #f9fafb; padding: 20px; text-align: center; border-radius: 0 0 8px 8px; }
        .btn { display: inline-block; background: #10B981; color: white; padding: 12px 24px; text-decoration: none; border-radius: 6px; font-weight: bold; margin: 20px 0; }
        .btn:hover { background: #059669; }
        .highlight { background: #FEF3C7; padding: 15px; border-radius: 6px; margin: 20px 0; border-left: 4px solid #F59E0B; }
      </style>
    </head>
    <body>
      <div class="container">
        <div class="header">
          <h1>🏆 ${subject}</h1>
          <p>LA Plumb Prep - Official State Prep Course</p>
        </div>

        <div class="content">
          <h2>Hello ${firstName}!</h2>
          <p>${message}</p>

          <div class="highlight">
            <strong>Competition Details:</strong><br>
            📅 When: Last day of the month<br>
            ⏱️ Duration: 120 minutes<br>
            📝 Questions: 100 questions<br>
            🏅 Rewards: 1st place = Free month, 2nd place = Half month free
          </div>

          <a href="${process.env.VITE_APP_URL || 'https://latrainer.com'}/competitions" class="btn">
            View Competition Details
          </a>

          <p>Don't miss out on this opportunity to test your Louisiana plumbing code knowledge and compete for valuable prizes!</p>

          <p>Good luck!<br>
          The LA Plumb Prep Team</p>
        </div>

        <div class="footer">
          <p><small>
            You received this email because you're enrolled in LA Plumb Prep courses.<br>
            <a href="${process.env.VITE_APP_URL || 'https://latrainer.com'}/unsubscribe">Unsubscribe from competition notifications</a>
          </small></p>
        </div>
      </div>
    </body>
    </html>
    `;
  }

  // Send results notifications after competition ends
  async sendResultsNotifications(competitionId: string) {
    const competition = await this.getCompetition(competitionId);
    if (!competition) return;

    // Get all participants and their results
    const participants = await db.execute(sql`
      SELECT 
        ca.userId,
        ca.score,
        ca.rank,
        ca.pointsEarned,
        u.email,
        u.firstName,
        u.lastName
      FROM competition_attempts ca
      JOIN users u ON ca.userId = u.id
      WHERE ca.competitionId = ${competitionId}
      ORDER BY ca.rank ASC
    `);

    for (const participant of participants.rows) {
      const isWinner = participant.rank <= 2;
      const title = isWinner 
        ? `🎉 Congratulations! You placed #${participant.rank} in ${competition.title}!`
        : `📊 Your ${competition.title} Results`;

      const message = isWinner
        ? `Amazing work! You ranked #${participant.rank} and earned ${participant.pointsEarned} points. Your subscription reward will be applied automatically.`
        : `You completed the competition with a score of ${participant.score}% and earned ${participant.pointsEarned} points. Keep practicing for next month's competition!`;

      // Send in-app notification
      await db.insert(competitionNotifications).values({
        competitionId,
        userId: participant.userId as string,
        notificationType: "results",
        channel: "in_app",
        title,
        message,
        sentAt: new Date(),
      });

      // Send email notification
      if (participant.email) {
        const emailTemplate = this.generateResultsEmail(
          participant.firstName as string || "Student",
          title,
          message,
          participant.score as number,
          participant.rank as number,
          participant.pointsEarned as number
        );

        await db.insert(emailQueue).values({
          recipientEmail: participant.email as string,
          senderEmail: process.env.NOREPLY_USER || "noreply@latrainer.com",
          subject: title,
          htmlContent: emailTemplate,
          textContent: message,
          scheduledFor: new Date(),
          status: "pending",
          retryCount: 0,
        });
      }
    }
  }

  // Generate results email template
  private generateResultsEmail(
    firstName: string,
    subject: string,
    message: string,
    score: number,
    rank: number,
    points: number
  ): string {
    const isWinner = rank <= 2;
    const rankColor = rank === 1 ? "#FFD700" : rank === 2 ? "#C0C0C0" : "#CD7F32";

    return `
    <!DOCTYPE html>
    <html>
    <head>
      <meta charset="utf-8">
      <title>${subject}</title>
      <style>
        body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
        .container { max-width: 600px; margin: 0 auto; padding: 20px; }
        .header { background: linear-gradient(135deg, #10B981, #059669); color: white; padding: 30px; text-align: center; border-radius: 8px 8px 0 0; }
        .content { background: #fff; padding: 30px; border: 1px solid #e5e7eb; }
        .footer { background: #f9fafb; padding: 20px; text-align: center; border-radius: 0 0 8px 8px; }
        .results-card { background: #f8fafc; border: 2px solid ${rankColor}; border-radius: 8px; padding: 20px; margin: 20px 0; text-align: center; }
        .score { font-size: 48px; font-weight: bold; color: ${rankColor}; }
        .rank-badge { background: ${rankColor}; color: white; padding: 8px 16px; border-radius: 20px; font-weight: bold; }
        .btn { display: inline-block; background: #3B82F6; color: white; padding: 12px 24px; text-decoration: none; border-radius: 6px; font-weight: bold; margin: 20px 0; }
      </style>
    </head>
    <body>
      <div class="container">
        <div class="header">
          <h1>${isWinner ? "🏆" : "📊"} ${subject}</h1>
          <p>LA Plumb Prep Competition Results</p>
        </div>

        <div class="content">
          <h2>Hello ${firstName}!</h2>
          <p>${message}</p>

          <div class="results-card">
            <div class="score">${score.toFixed(1)}%</div>
            <div style="margin: 10px 0;">
              <span class="rank-badge">Rank #${rank}</span>
            </div>
            <p><strong>${points} Points Earned</strong></p>
          </div>

          ${isWinner ? `
          <div style="background: #FEF3C7; padding: 20px; border-radius: 8px; border-left: 4px solid #F59E0B; margin: 20px 0;">
            <h3>🎉 Congratulations on your ${rank === 1 ? "1st place" : "2nd place"} finish!</h3>
            <p>Your subscription reward will be automatically applied to your account.</p>
          </div>
          ` : `
          <div style="background: #DBEAFE; padding: 20px; border-radius: 8px; border-left: 4px solid #3B82F6; margin: 20px 0;">
            <h3>Keep practicing for next month's competition!</h3>
            <p>Every month is a new opportunity to compete and win valuable prizes.</p>
          </div>
          `}

          <a href="${process.env.VITE_APP_URL || 'https://latrainer.com'}/leaderboard" class="btn">
            View Full Leaderboard
          </a>

          <p>Thank you for participating in our monthly competition!</p>

          <p>Best regards,<br>
          The LA Plumb Prep Team</p>
        </div>

        <div class="footer">
          <p><small>
            <a href="${process.env.VITE_APP_URL || 'https://latrainer.com'}/competitions">View Next Competition</a> |
            <a href="${process.env.VITE_APP_URL || 'https://latrainer.com'}/unsubscribe">Unsubscribe</a>
          </small></p>
        </div>
      </div>
    </body>
    </html>
    `;
  }

  // Get unread notifications for a user
  async getUserNotifications(userId: string) {
    return await db.select()
      .from(competitionNotifications)
      .where(and(
        eq(competitionNotifications.userId, userId),
        eq(competitionNotifications.isRead, false)
      ))
      .orderBy(competitionNotifications.createdAt);
  }

  // Mark notification as read
  async markNotificationRead(notificationId: string) {
    await db.update(competitionNotifications)
      .set({ 
        isRead: true, 
        readAt: new Date() 
      })
      .where(eq(competitionNotifications.id, notificationId));
  }

  // Helper methods
  private async getCompetition(competitionId: string) {
    const [competition] = await db.select()
      .from(monthlyCompetitions)
      .where(eq(monthlyCompetitions.id, competitionId))
      .limit(1);
    return competition;
  }

  private async getActiveUsers() {
    return await db.select({
      id: users.id,
      email: users.email,
      firstName: users.firstName,
      lastName: users.lastName,
    })
    .from(users)
    .where(eq(users.isActive, true));
  }
}

export const competitionNotificationService = new CompetitionNotificationService();

