import { useState, useEffect } from "react";
import { useParams, useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { Clock, AlertCircle, CheckCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";

export default function CompetitionTest() {
  const { competitionId } = useParams<{ competitionId: string }>();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState<{ [key: number]: number }>({});
  const [timeRemaining, setTimeRemaining] = useState<number>(0);
  const [startTime] = useState(Date.now());

  // Start competition attempt
  const { data: attempt, isLoading } = useQuery({
    queryKey: [`/api/competitions/${competitionId}/start`],
    queryFn: async () => {
      const response = await fetch(`/api/competitions/${competitionId}/start`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
      });
      if (!response.ok) throw new Error("Failed to start competition");
      return response.json();
    },
    enabled: !!competitionId,
  });

  // Submit competition attempt
  const submitMutation = useMutation({
    mutationFn: async (submissionData: any) => {
      const response = await fetch(`/api/competitions/${competitionId}/submit`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(submissionData),
      });
      if (!response.ok) throw new Error("Failed to submit competition");
      return response.json();
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ["/api/competitions/history"] });
      queryClient.invalidateQueries({ queryKey: ["/api/leaderboard"] });
      queryClient.invalidateQueries({ queryKey: ["/api/points/summary"] });

      toast({
        title: "Competition Submitted!",
        description: `You scored ${data.score}% and earned ${data.pointsEarned} points!`,
      });

      setLocation("/competitions");
    },
    onError: (error: any) => {
      toast({
        title: "Submission Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Timer effect
  useEffect(() => {
    if (!attempt) return;

    const competition = attempt.competition;
    const timeLimitMs = competition.timeLimit * 60 * 1000;
    const endTime = startTime + timeLimitMs;

    const timer = setInterval(() => {
      const now = Date.now();
      const remaining = Math.max(0, endTime - now);
      setTimeRemaining(remaining);

      if (remaining === 0) {
        handleSubmit();
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [attempt, startTime]);

  const handleAnswerChange = (questionIndex: number, answerIndex: number) => {
    setAnswers(prev => ({
      ...prev,
      [questionIndex]: answerIndex
    }));
  };

  const handleSubmit = () => {
    if (!attempt) return;

    const timeSpent = Math.floor((Date.now() - startTime) / 1000);
    const questions = attempt.questions.map((q: any, index: number) => ({
      ...q,
      userAnswer: answers[index] ?? null
    }));

    submitMutation.mutate({
      questions,
      timeSpent
    });
  };

  const formatTime = (ms: number) => {
    const minutes = Math.floor(ms / 60000);
    const seconds = Math.floor((ms % 60000) / 1000);
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-4xl mx-auto px-4">
          <div className="animate-pulse space-y-6">
            <div className="h-8 bg-gray-200 rounded w-1/3"></div>
            <div className="h-64 bg-gray-200 rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  if (!attempt) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-4xl mx-auto px-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2 text-red-600">
                <AlertCircle className="w-6 h-6" />
                Competition Not Available
              </CardTitle>
            </CardHeader>
            <CardContent>
              <p>This competition is not currently available or you may have already completed it.</p>
              <Button onClick={() => setLocation("/competitions")} className="mt-4">
                Return to Competitions
              </Button>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  const questions = attempt.questions;
  const currentQ = questions[currentQuestion];
  const progress = ((currentQuestion + 1) / questions.length) * 100;
  const answeredCount = Object.keys(answers).length;

  return (
    <div className="min-h-screen bg-gray-50 py-8" data-testid="competition-test">
      <div className="max-w-4xl mx-auto px-4">
        {/* Header */}
        <div className="mb-6">
          <div className="flex items-center justify-between mb-4">
            <h1 className="text-2xl font-bold" data-testid="competition-title">
              {attempt.competition.title}
            </h1>
            <div className="flex items-center gap-2 text-lg font-semibold">
              <Clock className="w-5 h-5" />
              <span data-testid="time-remaining" className={timeRemaining < 300000 ? "text-red-600" : ""}>
                {formatTime(timeRemaining)}
              </span>
            </div>
          </div>

          {/* Progress */}
          <div className="space-y-2">
            <div className="flex justify-between text-sm text-gray-600">
              <span>Question {currentQuestion + 1} of {questions.length}</span>
              <span data-testid="answered-count">{answeredCount} answered</span>
            </div>
            <Progress value={progress} className="h-2" />
          </div>
        </div>

        {/* Question Card */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle className="text-lg" data-testid="question-text">
              {currentQ.question}
            </CardTitle>
            {currentQ.category && (
              <p className="text-sm text-gray-600" data-testid="question-category">
                Category: {currentQ.category}
              </p>
            )}
          </CardHeader>
          <CardContent>
            <RadioGroup 
              value={answers[currentQuestion]?.toString()} 
              onValueChange={(value) => handleAnswerChange(currentQuestion, parseInt(value))}
            >
              {currentQ.options.map((option: string, index: number) => (
                <div key={index} className="flex items-center space-x-2">
                  <RadioGroupItem 
                    value={index.toString()} 
                    id={`option-${index}`}
                    data-testid={`option-${index}`}
                  />
                  <Label htmlFor={`option-${index}`} className="flex-1 cursor-pointer">
                    {option}
                  </Label>
                </div>
              ))}
            </RadioGroup>
          </CardContent>
        </Card>

        {/* Navigation */}
        <div className="flex items-center justify-between mb-6">
          <Button
            variant="outline"
            onClick={() => setCurrentQuestion(Math.max(0, currentQuestion - 1))}
            disabled={currentQuestion === 0}
            data-testid="prev-question"
          >
            Previous
          </Button>

          <div className="text-sm text-gray-600">
            {answers[currentQuestion] !== undefined && (
              <span className="flex items-center gap-1 text-green-600">
                <CheckCircle className="w-4 h-4" />
                Answered
              </span>
            )}
          </div>

          <Button
            onClick={() => setCurrentQuestion(Math.min(questions.length - 1, currentQuestion + 1))}
            disabled={currentQuestion === questions.length - 1}
            data-testid="next-question"
          >
            Next
          </Button>
        </div>

        {/* Question Navigation */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle className="text-lg">Question Navigation</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-10 gap-2">
              {questions.map((_: any, index: number) => (
                <Button
                  key={index}
                  variant={currentQuestion === index ? "default" : "outline"}
                  size="sm"
                  onClick={() => setCurrentQuestion(index)}
                  className={`w-10 h-10 ${
                    answers[index] !== undefined 
                      ? "bg-green-100 border-green-300 text-green-700" 
                      : ""
                  }`}
                  data-testid={`nav-question-${index}`}
                >
                  {index + 1}
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Submit Section */}
        <Card>
          <CardHeader>
            <CardTitle>Submit Competition</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-center">
                <div>
                  <div className="text-2xl font-bold text-blue-600" data-testid="total-questions">
                    {questions.length}
                  </div>
                  <div className="text-sm text-gray-600">Total Questions</div>
                </div>
                <div>
                  <div className="text-2xl font-bold text-green-600" data-testid="answered-questions">
                    {answeredCount}
                  </div>
                  <div className="text-sm text-gray-600">Answered</div>
                </div>
                <div>
                  <div className="text-2xl font-bold text-orange-600" data-testid="unanswered-questions">
                    {questions.length - answeredCount}
                  </div>
                  <div className="text-sm text-gray-600">Remaining</div>
                </div>
              </div>

              {questions.length - answeredCount > 0 && (
                <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
                  <p className="text-yellow-800 text-sm">
                    You have {questions.length - answeredCount} unanswered questions. 
                    You can submit now or continue answering.
                  </p>
                </div>
              )}

              <Button 
                onClick={handleSubmit}
                disabled={submitMutation.isPending}
                className="w-full"
                data-testid="submit-competition"
              >
                {submitMutation.isPending ? "Submitting..." : "Submit Competition"}
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/competitions.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Link } from "wouter";
import { Clock, Trophy, Users, Medal, Target, Calendar, Award } from "lucide-react";
import { formatDistanceToNow, format } from "date-fns";

export default function Competitions() {
  const { data: currentCompetition, isLoading: competitionLoading } = useQuery({
    queryKey: ["/api/competitions/current"],
  });

  const { data: userHistory, isLoading: historyLoading } = useQuery({
    queryKey: ["/api/competitions/history"],
  });

  const { data: leaderboard, isLoading: leaderboardLoading } = useQuery({
    queryKey: ["/api/leaderboard"],
    queryFn: () => fetch("/api/leaderboard").then(res => res.json()),
  });

  const { data: pointsSummary } = useQuery({
    queryKey: ["/api/points/summary"],
  });

  if (competitionLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="animate-pulse space-y-6">
            <div className="h-8 bg-gray-200 rounded w-1/3"></div>
            <div className="h-64 bg-gray-200 rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case "active": return "bg-green-500";
      case "upcoming": return "bg-blue-500";
      case "completed": return "bg-gray-500";
      default: return "bg-gray-400";
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case "active": return "Live Now";
      case "upcoming": return "Coming Soon";
      case "completed": return "Completed";
      default: return status;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8" data-testid="competitions-page">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-4" data-testid="page-title">
            Monthly Competitions
          </h1>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto" data-testid="page-description">
            Compete with fellow plumbers in monthly challenges. Win free subscription time and showcase your expertise!
          </p>
        </div>

        {/* Points Summary */}
        {pointsSummary && (
          <Card className="mb-8 bg-gradient-to-r from-purple-500 to-pink-500 text-white">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Award className="w-6 h-6" />
                Your Performance
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="text-center">
                  <div className="text-3xl font-bold" data-testid="total-points">
                    {pointsSummary.totalPoints}
                  </div>
                  <div className="text-sm opacity-90">Total Points</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold" data-testid="competitions-entered">
                    {pointsSummary.competitionsEntered || 0}
                  </div>
                  <div className="text-sm opacity-90">Competitions Entered</div>
                </div>
                <div className="text-center">
                  <div className="text-3xl font-bold" data-testid="best-rank">
                    {pointsSummary.bestRank ? `#${pointsSummary.bestRank}` : "N/A"}
                  </div>
                  <div className="text-sm opacity-90">Best Rank</div>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Current Competition */}
        {currentCompetition && (
          <Card className="mb-8 border-2 border-primary shadow-lg">
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle className="flex items-center gap-2 text-2xl" data-testid="current-competition-title">
                    <Trophy className="w-6 h-6 text-yellow-500" />
                    {currentCompetition.title}
                  </CardTitle>
                  <CardDescription className="text-lg mt-2" data-testid="current-competition-description">
                    {currentCompetition.description}
                  </CardDescription>
                </div>
                <Badge 
                  className={`${getStatusColor(currentCompetition.status)} text-white px-4 py-2 text-sm`}
                  data-testid={`status-badge-${currentCompetition.status}`}
                >
                  {getStatusText(currentCompetition.status)}
                </Badge>
              </div>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-6">
                <div className="flex items-center gap-3">
                  <Calendar className="w-5 h-5 text-blue-500" />
                  <div>
                    <div className="font-semibold">Test Date</div>
                    <div className="text-sm text-gray-600" data-testid="competition-date">
                      {format(new Date(currentCompetition.startDate), "MMMM d, yyyy")}
                    </div>
                  </div>
                </div>
                <div className="flex items-center gap-3">
                  <Clock className="w-5 h-5 text-green-500" />
                  <div>
                    <div className="font-semibold">Time Limit</div>
                    <div className="text-sm text-gray-600" data-testid="time-limit">
                      {currentCompetition.timeLimit} minutes
                    </div>
                  </div>
                </div>
                <div className="flex items-center gap-3">
                  <Target className="w-5 h-5 text-orange-500" />
                  <div>
                    <div className="font-semibold">Questions</div>
                    <div className="text-sm text-gray-600" data-testid="question-count">
                      {currentCompetition.questionCount} questions
                    </div>
                  </div>
                </div>
                <div className="flex items-center gap-3">
                  <Medal className="w-5 h-5 text-purple-500" />
                  <div>
                    <div className="font-semibold">Rewards</div>
                    <div className="text-sm text-gray-600" data-testid="rewards">
                      1st: Free Month, 2nd: Half Month
                    </div>
                  </div>
                </div>
              </div>

              {currentCompetition.status === "active" && (
                <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-4">
                  <h4 className="font-semibold text-green-800 mb-2">Competition is Live!</h4>
                  <p className="text-green-700 text-sm mb-3">
                    You have until {format(new Date(currentCompetition.endDate), "h:mm a")} to complete your test.
                  </p>
                  <Link href={`/competition/${currentCompetition.id}/test`}>
                    <Button className="bg-green-600 hover:bg-green-700" data-testid="start-competition-btn">
                      Start Competition Test
                    </Button>
                  </Link>
                </div>
              )}

              {currentCompetition.status === "upcoming" && (
                <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                  <h4 className="font-semibold text-blue-800 mb-2">Competition Starting Soon</h4>
                  <p className="text-blue-700 text-sm">
                    Competition begins {formatDistanceToNow(new Date(currentCompetition.startDate), { addSuffix: true })}
                  </p>
                </div>
              )}
            </CardContent>
          </Card>
        )}

        {/* Quick Actions */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <Link href="/leaderboard">
            <Card className="cursor-pointer hover:shadow-lg transition-shadow h-full">
              <CardHeader className="text-center">
                <Trophy className="w-12 h-12 text-yellow-500 mx-auto mb-2" />
                <CardTitle data-testid="leaderboard-link">View Leaderboard</CardTitle>
                <CardDescription>See top performers</CardDescription>
              </CardHeader>
            </Card>
          </Link>

          <Link href="/achievements">
            <Card className="cursor-pointer hover:shadow-lg transition-shadow h-full">
              <CardHeader className="text-center">
                <Award className="w-12 h-12 text-purple-500 mx-auto mb-2" />
                <CardTitle data-testid="achievements-link">Your Achievements</CardTitle>
                <CardDescription>Track your progress</CardDescription>
              </CardHeader>
            </Card>
          </Link>

          <Card className="h-full">
            <CardHeader className="text-center">
              <Users className="w-12 h-12 text-blue-500 mx-auto mb-2" />
              <CardTitle>Community</CardTitle>
              <CardDescription data-testid="total-participants">
                {leaderboard?.length || 0} active competitors
              </CardDescription>
            </CardHeader>
          </Card>
        </div>

        {/* Competition History */}
        {userHistory && userHistory.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>Your Competition History</CardTitle>
              <CardDescription>Track your previous performance</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {userHistory.map((attempt: any) => (
                  <div key={attempt.id} className="flex items-center justify-between p-4 border rounded-lg" data-testid={`history-item-${attempt.id}`}>
                    <div>
                      <h4 className="font-semibold">{attempt.competition?.title}</h4>
                      <p className="text-sm text-gray-600">
                        {format(new Date(attempt.completedAt), "MMMM d, yyyy")}
                      </p>
                    </div>
                    <div className="text-right">
                      <div className="font-bold text-lg" data-testid={`score-${attempt.id}`}>
                        {parseFloat(attempt.score).toFixed(1)}%
                      </div>
                      {attempt.rank && (
                        <div className="text-sm text-gray-600" data-testid={`rank-${attempt.id}`}>
                          Rank #{attempt.rank}
                        </div>
                      )}
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/pages/leaderboard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Trophy, Medal, Award, Crown, Target, TrendingUp } from "lucide-react";

export default function Leaderboard() {
  const { data: leaderboard, isLoading } = useQuery({
    queryKey: ["/api/leaderboard"],
    queryFn: () => fetch("/api/leaderboard").then(res => res.json()),
  });

  const { data: currentUser } = useQuery({
    queryKey: ["/api/auth/me"],
    retry: false,
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="animate-pulse space-y-6">
            <div className="h-8 bg-gray-200 rounded w-1/3"></div>
            <div className="h-96 bg-gray-200 rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  const getRankIcon = (rank: number) => {
    switch (rank) {
      case 1: return <Crown className="w-6 h-6 text-yellow-500" />;
      case 2: return <Trophy className="w-6 h-6 text-gray-400" />;
      case 3: return <Medal className="w-6 h-6 text-orange-500" />;
      default: return <Award className="w-6 h-6 text-blue-500" />;
    }
  };

  const getRankColor = (rank: number) => {
    switch (rank) {
      case 1: return "bg-gradient-to-r from-yellow-400 to-yellow-600 text-white";
      case 2: return "bg-gradient-to-r from-gray-300 to-gray-500 text-white";
      case 3: return "bg-gradient-to-r from-orange-400 to-orange-600 text-white";
      default: return "bg-white border";
    }
  };

  const getInitials = (firstName?: string, lastName?: string, username?: string) => {
    if (firstName && lastName) {
      return `${firstName[0]}${lastName[0]}`.toUpperCase();
    }
    if (username) {
      return username.slice(0, 2).toUpperCase();
    }
    return "??";
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-8" data-testid="leaderboard-page">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 mb-4" data-testid="page-title">
            Competition Leaderboard
          </h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto" data-testid="page-description">
            See how you rank against other Louisiana plumbing professionals
          </p>
        </div>

        {/* Top 3 Podium */}
        {leaderboard && leaderboard.length > 0 && (
          <div className="mb-8">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
              {/* 2nd Place */}
              {leaderboard[1] && (
                <Card className={`${getRankColor(2)} transform md:-translate-y-4`}>
                  <CardHeader className="text-center pb-2">
                    <div className="flex justify-center mb-2">
                      {getRankIcon(2)}
                    </div>
                    <div className="text-lg font-bold" data-testid="rank-2-name">
                      {leaderboard[1].firstName} {leaderboard[1].lastName}
                    </div>
                    <div className="text-sm opacity-90">2nd Place</div>
                  </CardHeader>
                  <CardContent className="text-center pt-0">
                    <div className="text-2xl font-bold" data-testid="rank-2-points">
                      {leaderboard[1].totalPoints}
                    </div>
                    <div className="text-sm opacity-90">Total Points</div>
                  </CardContent>
                </Card>
              )}

              {/* 1st Place */}
              {leaderboard[0] && (
                <Card className={`${getRankColor(1)} transform md:-translate-y-8`}>
                  <CardHeader className="text-center pb-2">
                    <div className="flex justify-center mb-2">
                      {getRankIcon(1)}
                    </div>
                    <div className="text-xl font-bold" data-testid="rank-1-name">
                      {leaderboard[0].firstName} {leaderboard[0].lastName}
                    </div>
                    <div className="text-sm opacity-90">Champion</div>
                  </CardHeader>
                  <CardContent className="text-center pt-0">
                    <div className="text-3xl font-bold" data-testid="rank-1-points">
                      {leaderboard[0].totalPoints}
                    </div>
                    <div className="text-sm opacity-90">Total Points</div>
                  </CardContent>
                </Card>
              )}

              {/* 3rd Place */}
              {leaderboard[2] && (
                <Card className={`${getRankColor(3)} transform md:-translate-y-4`}>
                  <CardHeader className="text-center pb-2">
                    <div className="flex justify-center mb-2">
                      {getRankIcon(3)}
                    </div>
                    <div className="text-lg font-bold" data-testid="rank-3-name">
                      {leaderboard[2].firstName} {leaderboard[2].lastName}
                    </div>
                    <div className="text-sm opacity-90">3rd Place</div>
                  </CardHeader>
                  <CardContent className="text-center pt-0">
                    <div className="text-2xl font-bold" data-testid="rank-3-points">
                      {leaderboard[2].totalPoints}
                    </div>
                    <div className="text-sm opacity-90">Total Points</div>
                  </CardContent>
                </Card>
              )}
            </div>
          </div>
        )}

        {/* Full Leaderboard */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <TrendingUp className="w-6 h-6" />
              Full Rankings
            </CardTitle>
          </CardHeader>
          <CardContent>
            {!leaderboard || leaderboard.length === 0 ? (
              <div className="text-center py-8">
                <Target className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-600">No competition data yet. Be the first to compete!</p>
              </div>
            ) : (
              <div className="space-y-3">
                {leaderboard.map((user: any, index: number) => {
                  const rank = index + 1;
                  const isCurrentUser = currentUser && user.id === currentUser.id;

                  return (
                    <div
                      key={user.id}
                      className={`flex items-center justify-between p-4 rounded-lg transition-colors ${
                        isCurrentUser 
                          ? "bg-blue-50 border-2 border-blue-200" 
                          : "bg-gray-50 hover:bg-gray-100"
                      }`}
                      data-testid={`leaderboard-row-${rank}`}
                    >
                      <div className="flex items-center gap-4">
                        <div className="flex items-center justify-center w-10 h-10">
                          {rank <= 3 ? (
                            getRankIcon(rank)
                          ) : (
                            <div className="text-lg font-bold text-gray-600" data-testid={`rank-${rank}`}>
                              #{rank}
                            </div>
                          )}
                        </div>

                        <Avatar className="w-10 h-10">
                          <AvatarFallback className="bg-primary text-primary-foreground">
                            {getInitials(user.firstName, user.lastName, user.username)}
                          </AvatarFallback>
                        </Avatar>

                        <div>
                          <div className="font-semibold flex items-center gap-2" data-testid={`user-name-${rank}`}>
                            {user.firstName} {user.lastName}
                            {isCurrentUser && (
                              <Badge variant="secondary" className="text-xs">You</Badge>
                            )}
                          </div>
                          <div className="text-sm text-gray-600" data-testid={`user-subscription-${rank}`}>
                            {user.subscriptionTier} member
                          </div>
                        </div>
                      </div>

                      <div className="text-right">
                        <div className="text-xl font-bold text-primary" data-testid={`user-points-${rank}`}>
                          {user.totalPoints}
                        </div>
                        <div className="text-sm text-gray-600">points</div>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Current User Position (if not in top visible) */}
        {currentUser && leaderboard && leaderboard.length > 10 && (
          <Card className="mt-6 border-2 border-blue-200">
            <CardHeader>
              <CardTitle className="text-lg">Your Position</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-center">
                <div className="text-3xl font-bold text-blue-600 mb-2">
                  Your position will be calculated after competitions
                </div>
                <p className="text-gray-600">Participate in monthly competitions to see your ranking</p>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/pages/pay-per-use-checkout.ts.content:
import { useState, useEffect } from 'react';
import { useStripe, useElements, PaymentElement, Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import { useLocation, Link } from 'wouter';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { ArrowLeft, CheckCircle, XCircle } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

// Make sure to call `loadStripe` outside of a component's render to avoid
// recreating the `Stripe` object on every render.
if (!import.meta.env.VITE_STRIPE_PUBLIC_KEY) {
  throw new Error('Missing required Stripe key: VITE_STRIPE_PUBLIC_KEY');
}
const stripePromise = loadStripe(import.meta.env.VITE_STRIPE_PUBLIC_KEY);

const CheckoutForm = ({ service, amount, onSuccess, onError }: {
  service: string;
  amount: number;
  onSuccess: (paymentIntentId: string) => void;
  onError: (error: string) => void;
}) => {
  const stripe = useStripe();
  const elements = useElements();
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    setIsLoading(true);

    try {
      const { error, paymentIntent } = await stripe.confirmPayment({
        elements,
        redirect: 'if_required',
      });

      if (error) {
        onError(error.message || 'Payment failed');
        toast({
          title: "Payment Failed",
          description: error.message,
          variant: "destructive",
        });
      } else if (paymentIntent && paymentIntent.status === 'succeeded') {
        onSuccess(paymentIntent.id);
        toast({
          title: "Payment Successful",
          description: `Your ${service} payment has been processed!`,
        });
      }
    } catch (err: any) {
      onError(err.message || 'Payment failed');
      toast({
        title: "Payment Error",
        description: err.message || 'An unexpected error occurred',
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <PaymentElement />
      <Button
        type="submit"
        className="w-full"
        disabled={!stripe || isLoading}
        data-testid="confirm-payment-button"
      >
        {isLoading ? 'Processing...' : `Pay $${amount.toFixed(2)}`}
      </Button>
    </form>
  );
};

export default function PayPerUseCheckout() {
  const [, setLocation] = useLocation();
  const [clientSecret, setClientSecret] = useState("");
  const [service, setService] = useState("");
  const [amount, setAmount] = useState(0);
  const [paymentStatus, setPaymentStatus] = useState<'pending' | 'processing' | 'succeeded' | 'failed'>('pending');
  const [result, setResult] = useState<any>(null);

  useEffect(() => {
    // Get service type from URL params
    const urlParams = new URLSearchParams(window.location.search);
    const serviceType = urlParams.get('service');
    const data = urlParams.get('data');

    if (!serviceType) {
      setLocation('/tools');
      return;
    }

    const initializePayment = async () => {
      try {
        let endpoint = '';
        let serviceDisplayName = '';

        switch (serviceType) {
          case 'photo-analysis':
            endpoint = '/api/pay-per-use/photo-analysis';
            serviceDisplayName = 'Photo Analysis';
            break;
          case 'plan-analysis':
            endpoint = '/api/pay-per-use/plan-analysis';
            serviceDisplayName = 'Plan Analysis';
            break;
          case 'mentor-question':
            endpoint = '/api/pay-per-use/mentor-question';
            serviceDisplayName = 'AI Mentor Question';
            break;
          default:
            setLocation('/tools');
            return;
        }

        const response = await apiRequest("POST", endpoint, {
          [serviceType === 'photo-analysis' ? 'imageData' : 
           serviceType === 'plan-analysis' ? 'planData' : 'question']: data
        });

        const result = await response.json();
        setClientSecret(result.clientSecret);
        setAmount(result.amount);
        setService(serviceDisplayName);
      } catch (error: any) {
        console.error('Failed to initialize payment:', error);
        setLocation('/tools');
      }
    };

    initializePayment();
  }, [setLocation]);

  const handlePaymentSuccess = async (paymentIntentId: string) => {
    setPaymentStatus('processing');

    try {
      const urlParams = new URLSearchParams(window.location.search);
      const serviceType = urlParams.get('service');
      const data = urlParams.get('data');

      const response = await apiRequest("POST", "/api/pay-per-use/process", {
        paymentIntentId,
        service: serviceType,
        data: {
          [serviceType === 'photo-analysis' ? 'imageData' : 
           serviceType === 'plan-analysis' ? 'planData' : 'question']: data
        }
      });

      const result = await response.json();
      setResult(result.result);
      setPaymentStatus('succeeded');
    } catch (error: any) {
      console.error('Failed to process service:', error);
      setPaymentStatus('failed');
    }
  };

  const handlePaymentError = (error: string) => {
    console.error('Payment error:', error);
    setPaymentStatus('failed');
  };

  if (!clientSecret) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  if (paymentStatus === 'processing') {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <Card className="w-full max-w-md mx-4">
          <CardContent className="p-8 text-center">
            <div className="animate-spin w-12 h-12 border-4 border-primary border-t-transparent rounded-full mx-auto mb-4" />
            <h2 className="text-xl font-semibold mb-2">Processing Your Request</h2>
            <p className="text-muted-foreground">
              Your payment was successful! We're now analyzing your data...
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (paymentStatus === 'succeeded') {
    return (
      <div className="min-h-screen bg-background p-4">
        <div className="max-w-4xl mx-auto">
          <Card>
            <CardHeader>
              <div className="flex items-center space-x-2 text-green-600">
                <CheckCircle className="w-6 h-6" />
                <CardTitle>Analysis Complete!</CardTitle>
              </div>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="bg-green-50 dark:bg-green-900/20 p-4 rounded-lg">
                <p className="text-green-800 dark:text-green-200">
                  Your {service} has been completed successfully.
                </p>
              </div>

              {result && (
                <div className="space-y-4">
                  <h3 className="text-lg font-semibold">Results:</h3>
                  <div className="bg-muted p-4 rounded-lg">
                    <pre className="whitespace-pre-wrap text-sm">{JSON.stringify(result, null, 2)}</pre>
                  </div>
                </div>
              )}

              <div className="flex space-x-4">
                <Link href="/tools" className="flex-1">
                  <Button variant="outline" className="w-full">
                    <ArrowLeft className="w-4 h-4 mr-2" />
                    Back to Tools
                  </Button>
                </Link>
                <Link href="/tools/ai-pricing" className="flex-1">
                  <Button className="w-full">
                    Upgrade to Unlimited
                  </Button>
                </Link>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  if (paymentStatus === 'failed') {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <Card className="w-full max-w-md mx-4">
          <CardHeader>
            <div className="flex items-center space-x-2 text-red-600">
              <XCircle className="w-6 h-6" />
              <CardTitle>Payment Failed</CardTitle>
            </div>
          </CardHeader>
          <CardContent className="space-y-4">
            <p className="text-muted-foreground">
              There was an issue processing your payment. Please try again.
            </p>
            <Link href="/tools" className="block">
              <Button variant="outline" className="w-full">
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Tools
              </Button>
            </Link>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background p-4">
      <div className="max-w-lg mx-auto">
        <Card>
          <CardHeader>
            <CardTitle className="text-center">Pay Per Use - {service}</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="mb-6 p-4 bg-muted rounded-lg">
              <div className="flex justify-between items-center">
                <span className="font-medium">{service}</span>
                <span className="text-2xl font-bold text-primary">${amount.toFixed(2)}</span>
              </div>
            </div>

            <Elements stripe={stripePromise} options={{ clientSecret }}>
              <CheckoutForm
                service={service}
                amount={amount}
                onSuccess={handlePaymentSuccess}
                onError={handlePaymentError}
              />
            </Elements>

            <div className="mt-6 text-center">
              <Link href="/tools">
                <Button variant="ghost" size="sm">
                  <ArrowLeft className="w-4 h-4 mr-2" />
                  Cancel and go back
                </Button>
              </Link>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

